INSERT INTO `articles` (`id`, `title`, `slug`, `description`, `body`, `showonmenu`, `lang`, `images`, `tags`, `type`, `viewCount`, `commentCount`, `created_at`, `updated_at`) VALUES
(1, 'فن بیان و گویندگی تخصصی رادیو', '', '', '<h4 class=\"\" style=\"font-family: &quot;Open Sans&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;Noto Sans&quot;, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;, &quot;Segoe UI Symbol&quot;, &quot;Noto Color Emoji&quot;; color: rgb(33, 37, 41);\">فن بیان و گویندگی تخصصی رادیو</h4><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\"><br></p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">همراه با اجرای برنامه های رادیویی</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">مدرسین دوره</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">سجاد نادر ( تهیه کننده و برنامه ساز رادیو)</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">میترا خواجه ئیان ( استاد دانشگاه و کارشناس رادیو)</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">مینا امامی ( کارشناس ارشد نمایش و مدرس نمایش رادیویی)</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\"><br></p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">سرفصل های دوره</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️فن بیان و سخنوری&nbsp;</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️انواع حس ، لحن ، بیان و تنفس</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️تیزرهای تبلیغاتی و دکلمه خوانی</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️پادکست ، کتاب صوتی و نریشن</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️آشنایی با انواع گویندگی&nbsp;</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️ اجرای پشت میکروفن</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️انواع نمایشنامه و درک موقعیت</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️ریتم ، تمپو ، موتیف</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️خلق کاراکتر و تخیل فعال</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️نقش بدن در تولید صدا</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">❗️بداهه گویی ، تیپ سازی و صداسازی</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">۱۵ جلسه ۳ ساعته</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">روز های&nbsp; پنجشنبه ساعت 15 الی 18</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\"><span style=\"background-color: rgb(181, 165, 214);\">هزینه دوره : 13/000/000 میلیون تومان</span></p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">‼️جهت کسب اطلاعات بیشتر با کارشناسان ما تماس بگیرید یا در بسترهای مجازی جهت مشاوره بیشتر در ارتباط باشید.‼️</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">☎️ 02188341652</p><p style=\"color: rgb(33, 37, 41); font-family: &quot;B Titr&quot;;\">09354637055', NULL, NULL, 'images/speaking1.jpg', '', '', 0, 0, '2024-08-24 06:39:08', NULL),
(3, 'نخستین عکاس ایران کی بود؟', 'نخستین عکاس ایران کی بود؟', 'پیدایش عکس و دوربین عکاسی برای اولین بار در اواخر دوره حکومت محمد شاه قاجار توسط نظامیان و مستشاران اروپایی که به تهران رفت و آمد داشتند، گزارش شده است.', '<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">پیدایش عکس و دوربین عکاسی برای اولین بار در اواخر دوره حکومت محمد شاه قاجار توسط نظامیان و مستشاران اروپایی که به تهران رفت و آمد داشتند، گزارش شده است</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.<br />\r\n&nbsp;</span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">عکس هایی که در ابتدا از تهران گرفته شده شامل عکس هایی از زندگی مردم و درباریان قاجار بود که متاسفانه نخستین عکس های برگرفته شده از ایران در موزه ها و مراکز فرهنگی خارج از کشور نگهداری می شود. با روی کار آمدن ناصرالدین شاه و علاقه وی به دوربین عکاسی، عکاسی از انحصار فرنگیان خارج شد</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">وی عکاسی را از شخصی فرانسوی به نام &laquo;مسیو کارلیان&raquo; آموخت. خودش به کار عکاسی، ظهور فیلم و چاپ عکس پرداخت. از جمله عکس های معروفی که ناصرالدین شاه از خود به جا گذاشته است، عکسی از مهدعلیا (مادرشاه) و زنان حرمسرایش است. وی به غیر از عکاسی از زنان و سوگلی هایش، از بناها، مناظر، شکارگاه ها، ییلاقات و زندانیان سیاسی عکس می انداخت</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">بسیاری از عکس هایی که ناصرالدین شاه از زندانیان سیاسی گرفته در آلبوم خانه کاخ </span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ndash;</span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> موزه گلستان نگهداری می شود و جمله &laquo;خودمان انداختیم&raquo; دیده می شود</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">پس از ناصرالدین شاه، سایر عکس های برجای مانده از دوران قاجار، عکس هایی از مظفرالدین شاه، محمدعلی شاه، احمد شاه است که در حالت های مختلف ایستاده، نشسته، سوار بر کالسکه اسب دیده می شود و کمتر عکسی از مناظر طبیعی در آن روزگار به ثبت رسیده است</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">با روی کار آمدن پهلوی اول، عکس های بجای مانده در آن دوران بیشتر از احداثاث، ابنیه عمارات، پل ها، ساختمان، دانشگاه، خیابان کشی، شهرسازی، پارک، دانشکده، مدرسه و قشون است</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">ناگفته نماند که تا مدت ها ی مدیدی عکاسی در انحصار درباریان بود و آنها از عکس به عنوان وسیله ای جهت پیشبرد پاره ای از اهداف و مقاصد خاص خود استفاده می کردندو اگر در اوایل دوربین عکاسی فقط به منظور گرفتن عکس های یادگاری و نمایش مورد استفاده قرار می گرفت، به تدریج با استفاده از تکنیک های جدید عکاسی، عکس در زمینه های دیگر به کار گرفته شد. تا جایی که گروهی از دربایان برای تخریب چهره برخی از رجال سیاسی به دفعات عکس افراد مهم و سرشناس و یا دشمنان خود را با بدن حیواناتی نظیر سگ، گربه، الاغ و... مونتاژ و درمیان مردم توزیع می کردند</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">عکاسی که در ابتدا برای تفنن شاه و درباریان به تهران راه یافت، بعدها وسیله ای شد تا عده ای از شاگردان ممتاز و با استعداد دارالفنون برای آموختن فن عکاسی به اروپا اعزام شوند و چند نفر از آنها بعد از مراجعت مورد توجه ناصرالدین شاه قرار گرفتند و به عنوان عکاسباشی در دارالخلافه مبارکه ناصری مشغول به کار شدند و حتی برخی از آنها در مسافرت های داخلی و خارجی شاه را همراهی می کردند</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">محمدرضا بیگی، نگارنده کتاب تهران قدیم، نام عده ای از عکاسباشیان پایتخت را به ترتیب قدمت و مسوولیت برشمرده است</span></span></strong><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">:</span></span></strong><br />\r\n<span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">&laquo;</span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">حاج میرزاعلی عکاسباشی </span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&ndash;</span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> آقا رضای پیشخدمت (اقبال السلطنه)، عزیز خواجه (متصدی تاریکخانه و ظهور عکس)، عباسعلی بیگ (ناظم عکاسخانه)، معتمدالسلطان (رییس عکاسخانه)، حسنعلی (عکاسباشی)، عبدالله قاجار (عکاسباشی مدرسه دارالفنون)، محمدحسین قاجار و میرزا جهانگیرخان. در این میان حسنعلی عکاسباشی و عبدالله قاجار از کسانی بودند که عکاسی را در اروپا آموخته بودند و به سایران آموزش می دادند</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">&raquo;.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">اما تاریخ، عبدالله قاجار را پدر عکاسی ایران می شناسد و عکس های متعلق به دوران قاجار که توسط وی تهیه شده است باعث ترقی و پیشرفت وی در این رشته بیش از سایر رشته ها شد</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">مردم تهران نسبت به این ابزار فرنگی و ماحصل آن آشنایی چندانی نداشتند. مشاهده تصویری از یک نفر که دقیقا با واقعیت مطابقت داشته باشد امری بسیار حیرت انگیز و تعجب آور برای عامه مردم بود.متداول شدن عکس در میان مردم را باید از زمان اجباری شدن ثبت احوال در تهران و سایر شهرستان ها دانست</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">نخستین سجل احوال که در زمان آخرین شاه قاجار به مردم تکلیف شده بود ورقه ای بدون عکس بود. اما با روی کار آمدن پهلوی اول، دستور عکس دار شدن سجل داده شد و مردم اجبارا با عکس و عکاسی سر و کار پیدا کردند</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">مناظر مضحک عکس گرفتن ها برای ثبت احوال، ماجراهای خنده آوری را در تهران آن روزگار ایجاد کرده از آن جمله که برخی از مردم از دوربین عکاسی وحشت داشتند و حاضر به نشستن در جلوی آن نبودند یا اینکه خودشان را مچاله می کردند و یا با دست های شان صورت شان را می پوشانیدند در نتیجه عکاسان وقت زیاد را صرف ادا و اطوار های عامه مردم می کردند و با سخنان شان به آنها قوت قلب می دادند تا ترس شان از بین برود</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">به عنوان مثال اگر پیرمردی برای عکاسی می آمد و ترس داشت، عکاس به وی می گفت اگر دوست داری مسجد آبادی یا محله تان را ببینی، تکان نخور و داخل سوراخ دوربین را نگاه کن که الان آن منظره جلویت نمایان می شود و یا اگر جوان بود، عکاس از وی می پرسید که کدام یک از رفقایت را دوست داری و وقتی جواب می داد، فلانی به او می گفت، جُم نخور، اکنون دوستت را داخل دوربین می بینی</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">در اوان رواج صنعت عکاسی در تهران، تنها عکاسخانه تهران &laquo;عکاسخانه مبارکه دولتی&raquo; بود که همگان به آنجا راه نداشتند و بیشتر اشراف و اعیان به این عکاسخانه می رفتند. عکاسخانه مذکور در خیابان جبارخانه به مدیریت عباسعلی بیگ بود و قیمت عکس در این عکاسخانه بستگی به کوچکی و بزرگی عکس داشت</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">اما نخستین عکاسخانه عمومی تهران در خیابان علا ءالدوله (فردوسی) گشایش یافت که متعلق به شخصی به نام &laquo;روسی خان&raquo; بود. بعداز وی نیز فردی به نام ماشاءالله اقدام به پرپایی عکاسخانه ای در بازار بین الحرمین کرد که به عکاسخانه بین الحرمین ماشاءالله شهرت داشت. این دو عکاسخانه مختص مردم عادی بود. زنان تهرانی به هیچ عنوان راضی نمی شدند که داخل عکاسخانه های عمومی بشوند و نامحرم از آنان عکس بیندازد</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">به دلیل عمومیت یافتن عکاسی در خانواده های اعیان و اشراف، چند نفر از زنان و دختران نیز به تجربه کردن این صنعت نوظهور پرداختند و معروف ترین آنها که نامش به عنوان اولین عکاس زن ایران به ثبت رسیده، اشرف السلطنه همسر محمدحسن خان اعتمادالسلطنه بود که عکاسی را از شاهزاده محمدمیرزا آموخته و راسا به فعالیت می پرداخت. اما نخستین زنی که به عکاسی عمومی پرداخت، خانم حداد بود که زنان برای گرفتن عکس به عکاسخانه وی در میدان شاهپور می رفتند</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">در تهران قدیم، عکس ها همگی در روز و توسط نور آفتاب برداشته می شد اما نخستین عکاسی که در شب عکس گرفت، جعفر خادم بود</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.&nbsp;</span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">ابتکار جالب وی به این صورت بود که پودر منیزیوم را در ظرف درازی ریخته و دو طرف پودر را به سر سیم لخت برق وصل کرد و با آماده شدن عکس، کلید برق را زده منیزیوم مشتعل و تولید نور درخشان می کرد در این هنگام فلاش به کار می افتاد، خادم نخستین عکس در هنگام شب را از چهارراه حسن آباد گرفت و به دلیل نبود رقیب، عکس شب بسیار گران و تقریبا سه برابر قیمت عکس های روز بود</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">این عکاسخانه به نام خادم با آویختن تابلویی به نام &laquo;عکاس شب و روز&raquo; باعث حیرت و اعجاز مردم شد. جعفر خادم که از دوستان رضاخان بود بعد از به سلطنت رسیدن وی به خدمت دربار او درآمد و عکاس مخصوص دربار شد و پسرش به نام علی خادم نیز بعدها جانشین پدرش شد</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">قیمت عکس و شرایط پرداخت در آن دوران به این گونه بود که عکاس در ابتدا پیش پرداختی دریافت نمی کرد. بعداز تحویل عکس، پول را نقدا می گرفت. شش عکس شش در چهار فوری از یک قران تا دو قران و غیرفوری سه تا چهار قران و اگر همراه با کارت پستال بود برای مشتری پنج قران تمام می شد</span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>', '0', '', '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/13.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_13.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_13.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_13.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_13.jpg\"}', 'عکاسی , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر', 'post', 955, 0, '2018-02-11 17:07:24', '2024-07-16 00:09:57');
INSERT INTO `articles` (`id`, `title`, `slug`, `description`, `body`, `showonmenu`, `lang`, `images`, `tags`, `type`, `viewCount`, `commentCount`, `created_at`, `updated_at`) VALUES
(4, 'تاریخچه گویندگی و رادیو', 'تاریخچه گویندگی و رادیو', 'پس‌ از انقلاب‌ اسلامی‌، نام‌ سازمان‌ رادیو تلویزیون‌ ملی‌ ایران‌ تبدیل‌ به‌ صدا و سیمای ‌جمهوری‌ اسلامی‌ ایران‌ شد. ریاست‌ این‌ سازمان‌ هر پنج‌ سال‌ یک‌ بار توسط ‌ رهبر‌ انتخاب‌ می‌شود، بالاترین‌ مقام ‌رادیو، معاون‌ رییس‌ سازمان‌ در حوزه‌ صدا می‌باشد.', '<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">هفت دهه قبل رادیو در ایران راه&zwnj;اندازی شد</span></span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اولین&zwnj; افراد از میان&zwnj; فرهنگیان&zwnj; و هنرمندان&zwnj; شایسته&zwnj; و با ذوق&zwnj; انتخاب&zwnj; و از سایر وزارتخانه&zwnj;ها به&zwnj; اداره&zwnj; کل انتشارات&zwnj; و تبلیغات&zwnj; منتقل&zwnj; شدند. استاد محمد حجازی&zwnj; ملقب&zwnj; به&zwnj; مطیع&zwnj;الدوله&zwnj; (نویسنده&zwnj; و داستان&zwnj;نویس&zwnj;)، عبدالرحمن&zwnj; فرامرزی&zwnj; (نویسنده&zwnj; و مدیر روزنامه&zwnj; کیهان&zwnj;)، حسینقلی&zwnj; مستعان&zwnj; (داستان&zwnj;نویس&zwnj;)، ابوالقاسم&zwnj; پاینده&zwnj; (نویسنده&zwnj;)، ابوالقاسم&zwnj; اعتصام&zwnj;زاده&zwnj; و مشفق&zwnj; همدانی&zwnj; (مترجم&zwnj;).</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">همچنین برای&zwnj; تعیین&zwnj; خط مشی&zwnj; و سیاست&zwnj; رادیو، شورای&zwnj; عالی&zwnj; انتشارات&zwnj; برپا شد که&zwnj; اعضای&zwnj; آن عبارت&zwnj; بود از: علامه&zwnj; محمد قزوینی&zwnj;، محمدعلی فروغی&zwnj; (ذکاءالملک)، دکتر قائم&zwnj; غنی&zwnj;، دکتر علی&zwnj;اکبر سیاسی&zwnj; (رییس&zwnj; دانشگاه&zwnj; تهران&zwnj;)، دکتر رضا زاده&zwnj;شفق&zwnj;، دکتر محمود افشار و استاد علینقی&zwnj; وزیری&zwnj; تشکیل شد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اولین مدیرکل انتشارات و تبلیغات (اولین مدیر رادیو)</span></span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">عیسی صدیق اعلم در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۳۷</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در اصفهان متولد شد. در مدرسه دارالفنون به تحصیل پرداخت. وی را پایه&zwnj;&zwnj;گذار مجله اصول تعلیمات می&zwnj;دانند. او از جانب احمدشاه لقب صدیق اعلم را دریافت کرد و در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> به طراحی، تکمیل و اجرای مقدمات تأسیس دانشگاه تهران پرداخت. پس از آنکه در بهار </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> فردی به نام مقدم نتوانست به خوبی اداره کل انتشارات و تبلیغات را تشکیل دهد، صدیق اعلم عهده&zwnj;دار تشکیل این اداره کل شد و امور مختلف رادیو را که از بدو افتتاح رادیو در وزارتخانه و نهادهای مختلف پراکنده بود در اداره کل انتشارات و تبلیغات متمرکز کرد و برای این اداره چنان وجاهتی قائل شد که حکم مدیرکل آن تا سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۴۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> توسط نخست&zwnj;وزیر ابلاغ </span></span></span>&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">می&zwnj;گردید. وی تا شهریور </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۲۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در مقام مدیرکل تبلیغات و انتشارات یا به عبارتی مدیر رادیو بود و پس از آن به تناوب چندین بار وزیر فرهنگ شد. در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۵۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> از خدمات دولتی بازنشسته شد اما کرسی تدریس تاریخ فرهنگ اروپا را در دانشگاه تهران در اختیار داشت و استاد ممتاز این دانشگاه بود.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; </span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">یکی از قدیمی&zwnj;ترین نویسنده&zwnj;ها</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">حسینقلی مستعان پس از اتمام دوره دبیرستان در دارالفنون مدتی در مدرسه علوم سیاسی به تحصیل پرداخت. وی کار روزنامه&zwnj;نویسی را از سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۹۸</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> آغاز کرد و از </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۷</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> سالگی در روزنامه نیمه رسمی ایران فعالیت داشت و نخستین شعر&zwnj;ها و داستان&zwnj;های کوتاه خود را در روزنامه&zwnj;های نسیم شمال، اتحاد و روزنامه ایران منتشر کرد. قصه&zwnj;نویسی را نیز از سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۴</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در مجله &laquo;مهرگان&raquo; با نام مستعار &laquo;ح. م. حمید&raquo; آغاز نمود.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">او در کار ترجمه هم دست داشت، ترجمه &laquo;بینوایان&raquo; اثر ویکتور هوگو که از جمله ترجمه&zwnj;های اوست یکی از بهترین ترجمه&zwnj;های بینوایان به شمار می&zwnj;رود. او در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> مجله &laquo;راهنمای زندگی&raquo; را منتشر کرد و بدون وقفه از سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> تا </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۵۷</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> ـ یعنی </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۵۷</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> سال ـ قلم زد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">مستعان پس از واقعه شهریور </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۲۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> مدتی به سیاست روی آورد و سردبیر و مدیر برخی از روزنامه&zwnj;های سیاسی آن زمان مانند روزنامه&zwnj;های اخبار روز، اخبار و دستور شده و از دهه </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۲۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> برای بعضی از برنامه&zwnj;های رادیو می&zwnj;نوشت. او در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۲۸</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> رییس شورای نویسندگان رادیو شد. مستعان بسیار پرکار بود و گاهی در هفته، هفت تا هشت پاورقی و داستان کوتاه می&zwnj;نوشت و همه را مرتب و بدون تأخیر می&zwnj;رساند. او از سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۳۵</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> تا سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۴۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در &laquo;مجله رادیو&raquo; داستان کوتاه می&zwnj;نوشت. اغلب نوشته&zwnj;های او در &laquo;مجله رادیو&raquo; داستان&zwnj;های پندآموز اجتماعی بود که با یک نتیجه&zwnj;گیری اخلاقی پایان می&zwnj;یافت. بسیاری از داستان&zwnj;های او در برنامه&zwnj;های رادیو استفاده می&zwnj;شد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">او نثری روان و ساده داشت و دارای تخیل قوی بود و در شخصیت&zwnj;پردازی مهارت زیادی داشت. نوشته&zwnj;هایش پیش&zwnj;نویس و پاکنویس نداشت و از&zwnj;&zwnj; همان آغاز کار، شروع به نوشتن می&zwnj;کرد. وی </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۰۰۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> داستان کوتاه و </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۲۰۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> کتاب نوشته است و به مرد هزار داستان شهرت دارد. او مدتی هم به کار نمایشنامه&zwnj;نویسی مشغول شد و طی سال&zwnj;های دهه </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۳۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> و </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۴۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> نمایشنامه&zwnj;های زیادی برای رادیو نوشت. مستعان در اواخر دهه بیست مدتی به ریاست رادیو منصوب شد.</span></span></span></span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; <strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اولین ناظر متون رادیویی</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">علی دشتی در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۷۲</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> شمسی در کربلا به دنیا آمد. او هنگام افتتاح رادیو در ایران عضو اداره شهربانی بود و ریاست دایره راهنمای نامه&zwnj;نگاری را در اداره سیاسی شهربانی به عهده داشت. او به همراه عبدالرحمن فرامرزی (روزنامه نگار و نویسنده) و چند نفر دیگر جزو اولین کسانی بودند که نظارت بر متون گفتار رادیویی و رسانه&zwnj;های گروهی آن زمان را در حد امکان انجام می&zwnj;داد. هیچ روزنامه&zwnj;ای بدون اجازه این قسمت حتی اجازه چاپ یک آگهی را نیز نداشت. همچنین هیچ جمله&zwnj;ای در رادیو خوانده نمی&zwnj;شد مگر اینکه قبلا از نظر علی دشتی گذشته باشد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">دشتی روزنامه&zwnj;نگاری منتقد و تندرو بود. روزنامه شفق سرخ در نخستین سال&zwnj;های تأسیس، این موضوع را اثبات کرد ولی بعد&zwnj;ها او به صورت یک روزنامه&zwnj;نگار خبری طرفدار دولت درآمد. او به زبان و ادبیات فارسی، عربی و فرانسه تسلط کامل داشت و با زبان&zwnj;های آلمانی و انگلیسی نیز آشنا بود. او سخنوری صحنه&zwnj;ساز بود. شهرت او بیشتر به خاطر نویسندگی است و آثار مکتوبی نیز دارد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; </span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اولین ویراستار متون رادیویی</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">سعید نفیسی، مورخ، ادیب، منتقد، نویسنده، نمایشنامه&zwnj;نویس، شاعر، لغت&zwnj;نویس، زبان&zwnj;شناس و مترجم، فرزند دکتر علی&zwnj;اکبر نفیسی ناظم&zwnj;الاطبا بود. تحصیلات دوران ابتدایی را در مدرسه شرف و دوران متوسطه را در مدرسه علمیه گذراند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۹۷</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> تهیه مقالات و تصحیح مجله &laquo;انجمن ادبی دانشکده&raquo; به مدیریت ملک&zwnj;الشعرای بهار به او سپرده شد و در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۹۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> به عضویت انجمن ادبی ایران درآمد. در حدود </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> به تدریس در دانشکده ادبیات پرداخت و در </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۴</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> عضو پیوسته فرهنگستان ایران شد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">او پس از کسب درجه دکتری کرسی تاریخ ادبیات ایران را در دانشگاه تهران به دست آورد. مدتی به عنوان استاد افتخاری در دانشگاه&zwnj;های کابل در افغانستان، قاهره در مصر، پن ژوزف در لبنان، بروکسل در بلژیک، هاروارد در آمریکا، کلمبیا در نیویورک و دانشگاه پرینستن در نیوجرسی به تدریس پرداخت.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">وی ریاست کمیسیون رادیو را قبل از افتتاح رادیو به عهده داشت و مسوولیت تهیه و ویرایش گفتارهای رادیو با او بود، زیرا توانایی کم&zwnj;نظیری در نویسندگی داشت و پرکار&zwnj;ترین و معروف&zwnj;ترین نویسنده زمان خود بود. نثر او ساده و روان بود و بسیار سریع می&zwnj;نوشت.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">او مردی ساده و قانع بود. محمدعلی فروغی در کابینه خود وی را برای تصدی پست وزارت فرهنگ در نظر گرفت ولی او کرسی استادی را بر مقام وزارت ترجیح داد. ایرج افشار او را یکی از پیشروان پژوهش&zwnj;های ایران&zwnj;شناسی می&zwnj;داند. او سال&zwnj;ها در بسیاری از برنامه&zwnj;های رادیو با موضوع ادبیات و ایران&zwnj;شناسی به عنوان کار&zwnj;شناس مجری در برنامه&zwnj;ها حضور داشت. سعید نفیسی کتابخانه عظیمی داشت که قبل از مرگ آن را در چهار بخش به نهادهای مختلف فروخت و یا به آن&zwnj;ها اهدا کرد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; </span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong>&nbsp;</strong><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">قدیمی&zwnj;ترین خواننده مرد</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اسماعیل خوانساری مشهور به ادیب خوانساری در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۸۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> متولد شد. کار خود را از مؤذنی مسجد خوانسار آغاز کرد و پس از مدتی نزد اساتید گوناگون گوشه&zwnj;های مختلف ساز و آواز ایرانی را فرا گرفت. او برای هنرش قدر و منزلت زیادی قائل بود، خودش در این باره می&zwnj;گوید: &laquo;هیچ گاه از کسی در ازای خواندن نوا پول نگرفته&zwnj;ام، زیرا این کار را دون شخصیت یک هنرمند می&zwnj;دانم.&raquo;</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">ادیب خوانساری در پاسداری از هنر و شخصیت خود سخت کوشا بود. وی در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> یعنی در زمان افتتاح رادیو همکاری خود را با این رسانه آغاز کرد و پس از آن نیز با آن همکاری داشت و با شروع برنامه &laquo;گلها&raquo; از خوانندگان پرکار در این برنامه به شمار می&zwnj;آمد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اولین گوینده خانم</span></span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">قدسی رهبری اولین گوینده خانم، در روز افتتاح رادیو، در تاریخ چهارم اردیبهشت سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> بود. قبل از افتتاح رادیو دو نفر از وزرا، عده&zwnj;ای از اساتید دانشگاه تهران و چند نفر از کارمندان عالی&zwnj;رتبه و مهندسان وزارت پست و تلگراف در یکی از سالن&zwnj;های باشگاه افسران از وی تست گویندگی گرفتند و او نفر اول شد. بد نیست بدانید ملک&zwnj;الشعرای بهار یکی از اساتیدی بود که از داوطلبان گویندگی، تست معلومات عمومی، فن بیان و شعرخوانی می&zwnj;گرفت.</span></span></span></span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;&nbsp; <strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">قدیمی&zwnj;ترین گوینده</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">رضا سجادی متولد </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۹۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> فرزند حجت&zwnj;الاسلام حاج سیدمصطفی سرابی واعظ و ناطق زبردست بود که در شهر مشهد مقدس به دنیا آمد. وی تحصیلات ابتدایی و متوسط را در موطن خود به پایان رساند و وارد دانشکده ادبیات دانشگاه تهران شد. او در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> به عنوان گوینده در رادیو استخدام شد و قسمتی از برنامه&zwnj;های رادیو توسط او اجرا می&zwnj;شد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">رضا سجادی مدتی به ریاست اداره رادیو منصوب گردید و مدتی هم ریاست اداره مطبوعات وزارت پیشه و هنر را عهده&zwnj;دار بود. رضا سجادی ناطقی زبردست، ادیبی پرمایه و شعر&zwnj;شناس بود او اشعار زیادی را در حافظه داشت و هنگام محاوره از اشعار حافظ، شاهد و مثال می&zwnj;آورد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong>&nbsp;</strong><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">قدیمی&zwnj;ترین گوینده/ هنرپیشه/ کارگردان</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">نصرت&zwnj;الله محتشم </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۲۸</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> اسفند </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۹۴</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در تهران به دنیا آمد. </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> ساله بود که برای اولین بار روی صحنه تئا&zwnj;تر ایفای نقش کرد. وی دانش&zwnj;آموخته دوره ادبی در دبیرستان دارالفنون و حقوق و علوم سیاسی از دانشگاه تهران بود و از سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> هنگام تأسیس رادیو، همکاری خود را با رادیو آغاز کرد. او ابتدا سمت گوینده داشت و بعد&zwnj;ها مشاور سرپرست نمایش&zwnj;های رادیو شد. او اولین تنظیم کننده داستان&zwnj;های شب رادیو بود و مدت&zwnj;های مدیدی نیز کارگردانی نمایش&zwnj;های داستان&zwnj;های شب را به عهده داشت. محتشم در عرصه سینما و دوبله نیز فعالیت می&zwnj;کرد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; </span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اولین مسوول تمرین ارکستر رادیو</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">ابراهیم آژنگ که آغاز حیات وی به دوره قاجار بر می&zwnj;گردد موسیقی را در شعبه موزیک مدرسه دارالفنون نزد استادان فرانسوی آموخت و ردیف ایرانی را نزد میرزاحسینقلی یاد گرفت. در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> سمت معاونت اداره موسیقی کشور را به عهده داشت و تمرینات گروه موسیقی رادیو به عهده او بود. نوازندگان و خوانندگان رادیو چند ساعت قبل از اجرای زنده برنامه در اداره موسیقی کشور حاضر می&zwnj;شدند و زیر نظر ابراهیم آژنگ به تمرین موسیقی می&zwnj;پرداختند. او در این تمرینات علاوه بر رهبری ارکستر، تنظیم قطعات موسیقی ایرانی برای ارکستر و خوانندگان را نیز به عهده داشت.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">یکی از قدیمی&zwnj;ترین ترانه&zwnj;سرا&zwnj;ها</span></span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">کریم فکور متولد </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۴</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در مشهد بود. تحصیلات متوسطه را در زادگاهش به پایان برد و در رشته حقوق از دانشگاه تهران فارغ&zwnj;التحصیل شد. وی فعالیت&zwnj;های هنری را با سرودن شعر و ترانه و همکاری با مطبوعات آغاز کرد. از حدود سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۲۴</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در اوان جوانی همکاری خود را با رادیو آغاز نمود. او مبتکر ساخت ترانه&zwnj;های دو لهجه&zwnj;ای یا ترانه&zwnj;های دو یا سه نفری بود که از رادیو پخش می&zwnj;شد. سبک ترانه&zwnj;سرایی او روان بود و از به&zwnj;کارگیری کلمات ثقیل پرهیز می&zwnj;کرد. او از ترانه&zwnj;&zwnj;سرایان فعال رادیو بود و هر ماه پنج تا شش ترانه برای خوانندگان رادیو می&zwnj;سرود و در دهه سی و چهل شهرت زیادی پیدا کرد. او با نوشتن فیلمنامه &laquo;شاهین ترس&raquo; در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۳۳</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> به جمع دست&zwnj;اندرکاران سینما پیوست اما همکاری خود را با رادیو همچنان ادامه داد.</span></span></span></span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; <strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اولین سخنران مذهبی</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">حسینعلی راشد </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۷۶</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در تربت حیدریه متولد شد. پدرش روحانی بود. راشد تا </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۶</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> سالگی در موطن خود مقدمات زبان و ادب فارسی و عربی، صرف و نحو، منطق و معانی را فرا گرفت و سپس وارد حوزه علمیه مشهد شد. راشد در </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۲۸</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> سالگی عازم نجف شد اما بعد از مدت کوتاهی به ایران بازگشت و در شیراز و اصفهان به کار وعظ و خطابه مشغول شد. کتاب &laquo;دو فیلسوف شرق و غرب&raquo; از آثار تألیفی استاد راشد است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۲۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> به دعوت وزیر فرهنگ وقت ـ محمد تدین ـ برای ایراد یک سلسله سخنرانی مذهبی به &laquo;رادیو تهران&raquo; دعوت شد و حدود سی سال هر هفته یک سخنرانی او در رادیو پخش می&zwnj;شد. استاد راشد علاوه بر رادیو در مدرسه عالی سپهسالار و سپس در دانشکده معقول و منقول در دوره لیسانس و دکترا تدریس می&zwnj;کرد. سخنرانی&zwnj;های استاد راشد در کشورهای همسایه ایران نیز که امواج رادیو ایران را دریافت می&zwnj;کردند، طرفداران زیادی داشت.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">اما رادیو در ایران چگونه راه&zwnj;اندازی شد؟</span></span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۳ (۱۹۲۴&zwnj;)</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">، وزارت جنگ مقدمات استفاده از بی&zwnj;سیم را فراهم کرد. در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۵</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> بی&zwnj;سیم به ایران وارد شد. از سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۱</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> موسسات بی&zwnj;سیم توسعه پیدا کردند، که نهایتا به ایجاد رادیو منتهی شد. تاسیس رادیو در ایران با تلگراف بی&zwnj;سیم ارتباط دارد بنابراین برای درک بهتر از چگونگی تاسیس رادیو نظری می&zwnj;اندازیم به بنیانگذاری تلگراف بی&zwnj;سیم در ایران در اواخر سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۳</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> در دویست هزار متر مربع زمین&zwnj;های (قصر قاجار) مقدمات راه&zwnj;اندازی تلگراف بی&zwnj;سیم توسط وزارت جنگ فراهم شد و نخستین دکل موج بلند به ارتفاع </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۲۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> متر که هنوز پایه ان موجود است نصب گردید و در بیست و چهارم اردیبهشت </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۴</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> دستگاه </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۲۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> کیلوواتی موج بلند نصب شد و در ششم اردیبهشت </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۵</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> کار بی&zwnj;سیم با پخش تلگرافی برای دعوت عمومی برای همکاری به کشورهای مختلف جهان افتتاح گردید. در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۷</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> نصب دو دستگاه موج کوتاه آغاز و در سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> از آن&zwnj;ها بهره&zwnj;برداری و مرکز گیرنده به نجف&zwnj;آباد تهران و مرکز مخابرات به میدان توپخانه منتقل شد.</span></span></span></span></span></span></p>\r\n\r\n<h3 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp; <strong><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">و اما مقدمات تاسیس رادیو</span></span></span></strong></span></span></span></h3>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">طبق مقررات ایران ورود دستگاه&zwnj;های مخابراتی و استفاده از آن&zwnj;ها تنها در حیطه مسوولیت وزارت پست و تلگراف و تلفن بود. این مساله را می&zwnj;توان از مکاتبه شخصی به نام داوود موشه لازار و دایره تجارت داخلی وزارت فلاحت و تجارت و فواید عامه دریافت کرد. آقای داوود موشه لازار، در جواب مراسله شما مورخه </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۴</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> ژوئن </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۹۲۹ (</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">خرداد </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۰۸) </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">راجع به تاسیس دستگاه رادیو در ایران، اشعار می&zwnj;دارد: &laquo;ورود دستگاه تلگراف و تلفن بی&zwnj;سیم گیرنده و یا مخابره کننده مطالب و آلات و اسباب آن جز برای اداره تلگراف بی&zwnj;سیم دولت وقت به طور کلی ممنوع است، لکن وارد کردن و استفاده از دستگاه&zwnj;های (رادیو کنیر) که فقط برای شنیدن نغمات به کار می&zwnj;رود و همچنین تاسیس سینمای ناطق در ایران با رعایت مقررات مربوط مانعی ندارد.&raquo;</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">مقدمات تأسیس رادیو تهران فراهم شد. بعد از صدور فرمان تأسیس رادیو، قرار شد از دو فرستنده موج کوتاه </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۲۰</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> کیلوواتی و متوسط </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۲</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> کیلوواتی وزارت پست و تلگراف و تلفن، که برای ارسال تلگراف بی&zwnj;سیم توسط کارخانه آلمانی تلفونکن</span></span></span>&nbsp;<span dir=\"LTR\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:#333333\">telefunken</span></span></span>&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">در بی&zwnj;سیم قصر، نصب شده بود استفاده شود.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">برای آمادگی پخش برنامه پس از افتتاح رادیو جمعی از استادان و نویسندگان عالیقدر ازجمله استاد سعید نفیسی و دکتر ذبیح&zwnj;الله صفا و عده&zwnj;ای از بانوان روشنفکر یک سلسله گفتارهای ادبی، تاریخی، جغرافیایی، اجتماعی و خانه&zwnj;داری را قبلاً تهیه کرده بودند به طوری که احتیاجات رادیو را برای مدت سه ماه تأمین می&zwnj;کرد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">عده&zwnj;ای مترجم و ماشین&zwnj;نویس هم استخدام شدند و برای انتخاب گوینده در باشگاه افسران و با حضور عده&zwnj;ای از دانشمندان و ادبا و متخصصان صوت (ازجمله عده&zwnj;ای آلمانی) جلساتی تشکیل شد و امتحانات دقیقی به عمل آمد. کارهای فنی رادیو با وزارت پست و تلگراف و تلفن (آقای ابراهیم علم، شوکت&zwnj;الملک وزیر پست و تلگراف و تلفن بودند) بود و امور مربوط به دستگاه&zwnj;های فرستنده و استودیو زیر نظر آقای دکتر داریوش، رییس کل بی&zwnj;سیم اداره می&zwnj;شد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">موسیقی زیر نظر سرگرد غلامحسین مین&zwnj;&zwnj;باشیان، رییس مدرسه عالی موسیقی بود و بودجه رادیو که فقط هشتاد هزار تومان بود توسط سازمان پرورش افکار تأمین می&zwnj;شد. بعد از فراهم شدن مقدمات، بالاخره رادیو در ساعت ده بامداد روز </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۴</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> اردیبهشت </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۱۹</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> افتتاح شد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">در شروع کار ساختمان رادیو از دو طبقه در بی&zwnj;سیم قصر تشکیل می&zwnj;شد، که طبقه اول شامل یک اتاق انتظار و دستگاه&zwnj;های فرستنده و تقویت&zwnj;کننده بود و طبقه دوم از یک اتاق که با نصب پرده در سقف و دوبل کردن در ورودی و خروجی و سیمان کاری دیوارهای داخلی به صورت استودیو درآمده بود.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;</span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">تا سال </span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۴۲</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\"> که کل تشکیلات رادیو به وزارت اطلاعات منتقل شد و این وضعیت تا ایجاد &laquo;سازمان رادیو تلویزیون ملی ایران&raquo; در سال</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#333333\">۱۳۵۰</span></span></span><span style=\"font-size:', 'off', 'fa', '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/12.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_12.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_12.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_12.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_12.jpg\"}', 'دوبله , تلویزیون , صداوسیما , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , فن بیان , سخنوری', 'post', 1416, 0, '2020-01-29 07:42:32', '2024-07-15 16:02:11');
INSERT INTO `articles` (`id`, `title`, `slug`, `description`, `body`, `showonmenu`, `lang`, `images`, `tags`, `type`, `viewCount`, `commentCount`, `created_at`, `updated_at`) VALUES
(5, 'درباره اولین نقاش ایران', 'درباره اولین نقاش ایران', 'تاریخ هنر نقاشی در ایران به زمان غارنشینی برمیگردد. در غارهای استان لرستان تصاویر نقاشی شده از حیوانات و تصاویر کشف شده است...', '<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">تاریخ هنر نقاشی در ایران به زمان غارنشینی برمیگردد. در غارهای استان لرستان تصاویر نقاشی شده از حیوانات و تصاویر کشف شده است...</span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">نقاشی های کشف شده در مناطق تپه سیالک و لرستان بر روی ظروف سفالی، ثابت می کند که هنرمندان این مناطق با هنر نقاشی آشنایی داشته اند.همچنین نقاشیهایی از دوران اشکانیان، نقاشی های معدودی بر روی دیوار، که بیشتر آنها از قسمتهای شمالی رودخانه فرات بدست آمده، کشف شده است. یکی از این نقاشیها منظره یک شکار را نشان میدهد. وضعیت سوارها و حیوانات و سبک بکار رفته در این نقاشی ما را بیاد منیاتورهای ایرانی می اندازد.در نقاشیهای دوران هخامنشی، نقاشی از روی چهره بر سایر نقاشی های دیگر تقدم داشت. تناسب و زیبایی رنگها از این دوران، بسیار جالب توجه است. نقاشی ها بدون سایه و با همدیگر هماهنگی دارند. در بعضی از موارد، سطوح سیاه پر رنگ را محدود کرده اند.مانی، (پیامبر و نقاش ایرانی)، که در قرن سوم زندگی میکرد، یک نقاش با تجربه و ماهر بود. نقاشی های او یکی از معجزاتش بود.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">نقاشی (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Torfan</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) که در صحرای گال استان ترکستان در چین بدست آمد، مربوط است بسال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۸۴۰</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> تا </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۸۶۰</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> بعد از میلاد.این نقاشی های دیوارنما مناظر و تصاویر ایرانی را نمایش میدهند. همچنین تصاویر شاخه های درختی در این نقاشی ها وجود دارند. باستانی ترین نقاشی های دوران اسلامی، که بسیار کمیاب است، در نیمه نخست قرن سیزدهم به وجود آمده بودند.مینیاتورهای ایرانی (طرح های خوب و کوچک) بعد از سقوط بغداد در سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۲۸۵</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی بوجود آمدند. از آغاز قرن چهاردهم کتابهای خطی بوسیله نقاشی از صحنه های جنگ و شکار تزئین شدند.چین از قرن هفتم به بعد به عنوان یک مرکز هنری، مهمترین انگیزه برای هنر نقاشی در ایران بود. از آن به بعد یک رابطه بین نقاشان بودائی چینی و نقاشان ایرانی بوجود آمده است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">از لحاظ تاریخی، مهمترین تکامل در هنر ایرانی، تقبل طرحهای چینی بوده است که با رنگ آمیزی که ادراک ویژه هنرمندان ایرانی است مخلوط شده بود. زیبایی و مهارت خارق العاده نقاشان ایرانی واقعاً خارج از توصیف است. در قرن اول اسلامی، هنرمندان ایرانی زینت دادن به کتابها را شروع کردند.کتابها با سرآغاز و حاشیه های زیبا رنگ آمیزی و تزئین شده بود. این طرحها و روشها از یک نسل به نسل دیگر با همان روش و اصلوب منتقل می شد، که معروف است به &quot;هنر روشن سازی&quot;. هنر روشن سازی و زیبا کردن کتابها در زمان سلجوقیان و مغول و تیموریان پیشرفت زیادی کرد. شهرت نقاشی های دوران اسلامی شهرت خود را از مدرسه بغداد داشت.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">مینیاتورهای مدرسه بغداد، کلاً سبک و روشهای نقاشی های معمولی پیش از دوره اسلامی را گم کرده است. این نقاشی های اولیه و بدعت کارانه فشار هنری لازم را ندارند.مینیاتورهای مدرسه بغداد اصلاً متناسب نیستند. تصاویر نژاد سامی را نشان میدهد؛ و رنگ روشن استفاده شده در آن نقاشی را. هنرمندان مدرسه بغداد پس از سالها رکود مشتاق بودند که آیین تازه ای را بوجود آورده و ابتکار کنند. نگاه های مخصوص این مدرسه، در طرح حیوانات و با تصاویر شرح دادن داستانها است.اگر چه مدرسه بغداد، هنر پیش از دوره اسلامی را تا حدی در نظر گرفته بود، که بسیار سطحی و بدوی بود. در همان دوره هنر مینیاتور ایرانی در تمام قلمرو اسلامی از آسیای دور تا آفریقا و اروپا پخش شده بود.از میان کتابهای مصور به سبک بغداد میتوان به کتاب &quot;کلیله و دمنه&quot; اشاره کرد. تصاویر نامتناسب و بزرگتر از حد معمول رنگ شده است؛ و فقط رنگهای معدودی در این نقاشیها بکار رفته است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">بیشتر کتابهای خطی قرن سیزدهم، افسانه ها و داستانها، با تصاویری از حیوانات و سبزیجات تزئین شده است. یکی از قدیمی ترین کتابهای کوچک طراحی شده ایرانی بنام &quot;مناف الحیوان&quot; در سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۲۹۹</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی بوجود آمده است. این کتاب مشخصات حیوانات را شرح میدهد. در این کتاب تاریخ طبیعی با افسانه در هم آمیخته شده است.موضوعات این کتاب که تصاویر بسیاری دارد، برای آشنایی با هنر نقاشی ایرانی بسیار مهم است. رنگها روشن تر و از روش مدرسه بغداد که روشی قدیمی بود جلوتر است.پس از هجوم مغولان، یک مدرسه جدید در ایران به نظر میرسید. این مدرسه تحت تأثیر از هنر چینی و سبک مغول بود. این نقاشی ها همه خشک و بی حرکت و خالص و یکسان، مانند سبک چینی است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">بعد از حمله مغول به ایران، آنها تحت تأثیر از هنر ایرانی، نقاشان و هنرمندان را تشویق کردند. در میان نقاشی های هنرمندان ایرانی میتوان سبک مغول هم مشاهده کرد، لطافت ها، ترکیبات آرایشی، و خطوط کوتاه خوب که میتوان آنها را بشمار آورد. نقاشی های ایرانی بصورت خطی و نه ابعادی می باشد. هنرمندان در این زمینه از خود یک خلاقیت و اصالت نشان داده اند.هنرمندان مغول دادگاه سلطنتی نه فقط به تکنیک بلکه به موضوعات ایرانی هم احترام گذاشتند. یک بخش از کارشان شرح دادن به آثار ادبی ایران مانند شاهنامه فردوسی بود. میان موضوعات مختلف بیشترین علاقه آنها به تصویریسم (کتابهای با تصاویر زیاد) بود.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">بر خلاف مدارس مغول و بغداد بیشترین کارها از مدرسه هرات بجا مانده است. مؤسس این سبک نقاشی مدرسه هرات بود؛ که از نیاکان تیموریان بودند و این مدرسه را بخاطر محل تأسیس آن مدرسه هراتی نامیدند.متخصاص هنر نقاشی بر این باورند، که نقاشی در ایران در دوران تیموری به اوج خود رسیده بود. در طول این دوره استادان برجسته ای، همچون کمال الدین بهزاد، یک متد جدید را به نقاشی ایران عرضه کرد. در این دوره (تیموریان) که از سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۳۷۰</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> تا </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۴۰۵</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی به طول انجامید؛ هنر نقاشی و کوچک سازی به بالاترین درجه کمال رسیده و بسیاری از نقاشان مشهور عمرشان را بر سر اینکار گذاشته اند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">دو کتاب با ارزش از زمان بایسغر باقی مانده است؛ یکی کتاب &quot;کلیله و دمنه&quot; و دیگری کتاب &quot;شاهنامه&quot;. هنر کوچک ایرانی در کتاب شاهنامه که در سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۴۴۴</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی در شیراز رنگ آمیزی شده است، بخوبی مشخص است.یکی از این طرح ها نشان دادن یک منظره زیبا از یک دادگاه ایرانی است که به سبک چینی رنگ آمیزی شده است. کاشی های سفید و آبی ایرانی همراه با فرشهای زیبای ایرانی بصورت هندسی نقش شده است. در یکی از کتابهای دستنویس &quot;خمسه نظامی گنجوی&quot;، سیزده مینیاتور زیبا بوسیله &quot;میرک&quot; کشیده شده است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ساختگی بودن، حساسیت، و هنر نقاشی های بغداد از طرحهای کشیده شده در کتاب &quot;خمسه نظامی&quot; کاملا مشهود است. این قطعه با ارزش و فوق العاده هم اکنون در موزه بریتانیا قرار دارد. در این قسمت از معماران و مهندسان مشغول ساختمان سازی هستند. این نقاشی در سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۴۹۴</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی در هرات نقاشی شده است.بهزاد، بهترین استاد سبک مدرسه هراتی، این هنر را بسیار بسط داد. او سبک را اختراع کرد که قبل از او هرگز استفاده نشده بود.یکی از برجسته ترین کارها کتاب شاهنامه فردوسی است که در کتابخانه گلستان ایران قرار دارد. این شاهنامه در زمان سلطان بایسغر که یکی از شاهزادگان تیموری بود؛ به سبک مدرسه هراتی نقاشی شده است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">نقاشی های این کتاب از نگاه رنگ آمیزی و تناسب ترکیب دهنده تصاویر، در بالاترین حد زیبایی و استحکام قرار دارد.در دوران صفویه مرکزیت هنر به شهر تبریز آمد. و برخی از هنرمندان هم در شهر قزوین ماندگار شدند. اما اصل سبک نقاشی صفویه در شهر اصفهان بوجود آمد.مینیاتورهای ایرانی، در دوره صفویه در شهر اصفهان، از سبک چینی جدا شده و قدم در راهی جدید نهاد. این نقاشان بعدها به سبک طبیعی متمایل شدند. رضا عباسی مؤسس سبک &quot;مدرسه نقاشی صفوی&quot; بود. در نقاشی های دوران صفوی دگرگونی بسیار عالی در نقاشی ایرانی رخ داد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">طرحهای این دوره یکی از بهترین و زیبا ترین و با سلیقه ترین طرحهای نقاشان ایرانی است که نشان دهنده ذوق نقاشان ایرانی است.مینیاتورهای خلق شده در این دوره(مدرسه صفوی)، هیچکدام منحصراً با هدف مزین کردن کتابها کشیده نشده اند. سبک صفوی بسیار بهتر و ملایم تر از سبک مدرسه تیموریان (هراتی) و مخصوصاً سبک مغولی است.در نقاشی نقاشان دوران صفوی آشکار شدن تخصص آنها در این رشته بوضوح مشخص است. بهترین معرف از این دوره نقاشی های هستند که در عمارت چهل ستون و عالی قاپو کشیده شده است.در نقاشی های صفوی، موضوع اصلی شکوه و زیبایی این دوره است. موضوعات نقاشی ها بیشتر حول محور بارگاه سلاطین، اشراف زادگان، کاخ های زیبا، مناظر زیبا و صحنه هایی از جنگ ها است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در این نقاشی ها انسانها با لباسهای پرخرج نخ کشی، صورتهایی زیبا و مجسمه های ظریف رنگین به طور پر هیجان روشن به تصویر کشیده شده است. هنر نقاشی در طول دوران صفوی هم زیاد و هم دارای کیفیت بهتر شد. در این نقاشی ها آزادی بیشتر و مهارت و دقت بیشتر مشهود است.هنرمندان بیشتر به اصول کلی پرداخته و از جزئیات غیر ضروری اجتناب، که در شیوه هراتی و تبریزی بکار رفته بود، خودداری کردند. صافی خطوط، بیان زود احساسات و متراکم شدن موضوعات از مشخصات سبک نقاشی صفوی است. از آنجاییکه در اواخر دوران صفوی، از لحاظ جنبه فکری اختلافات جزئی در رنگ آمیزی بوجود آورد، میتوان به ظهور سبک اروپایی در این نقاشی ها پی برد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">نقاشی های دوران قاجاریه (قرن شانزدهم میلادی) یک ترکیبی از سبک هنرهای اروپایی کلاسیک و سبک و تکنیک مینیاتورهای دوران صوفی است. در این دوران &quot;محمد غفاری (کمال الملک)&quot; سبک کلاسیک اروپایی را در ایران رواج داد. در این دوران سبکی از نقاشی بوجود آمد که بنام &quot;قهوه خانه&quot; شناخته شد. این نوع نقاشی یک پدیده جدید در تاریخ هنر ایرانی است. سبک &quot;قهوه خانه&quot; عامه پسند و مذهبی است. موضوعات این سبک بیشتر تصاویر پیامبران و امامان، شعائر مذهبی، جنگها و نام آوران ملی بودند. &quot;قهوه خانه ها&quot; رفته رفته جای خود را در بین مردم معمولی باز نمودند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در این مکان داستان سرایان و نقالان داستانهای حماسی و مذهبی را برای مردم بازگو میکردند. هنرمندان همان داستانها را بر روی دیوارهای این &quot;قهوه خانه ها&quot; نقاشی کرده بودند. قبلا در گذشته که پادشاهان و اشراف زادگان نقاشان را پشتیبانی میکردند؛ اما اینبار هنرمندان به درخواست مردم عادی آن مناظر را بر روی دیوارها میکشیدند و به این کار علاقمند بودند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در بیشتر این قهوه خانه ها این مناظر که بیشتر به درخواست عمومی بود رنگ آمیزی شده بود. زیباترین مثالها در موزه اصلی تهران و همینطور در بعضی از موزه های خصوصی داخل و خارج از کشور نگهداری میشود. نقاشی های ایرانی، یک نوع ملاحت را که بی شباهت به هر چیز دیگر است به تماشاگر عرضه میکند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">آنها یک ارتباط بیکران با داستانهای حماسی نگه داشته اند. نقاشی ایرانی، بعنوان یکی از بزرگترین سبکهای نقاشی در آسیا مطرح است. آسمان های روشن (آبی)، زیبایی شگفت انگیز شکوفه ها و در میان آنها انسانهایی که دوست میدارند و انسانهایی که تنفر دارند، خوشگذران و افسرده، به موضوعات مختلف نقاشان ایرانی شکل میدهند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">نقاشی ایرانی تا مدتها محدود به مصور ساختن کتابهای طبی و گیاهان دارویی بود. در این جا اگر بدن مریض نمایش داده می شود، بدون رعایت آناتومی و پرسپکتیو است و مسأله نمایش موضوع است حال به هر صورتی که باشد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در زمان ایلخانیان ارتباط با کشور چین تأثیرات عمیقی را سبب می شود که هنر چین بر هنر ایران نهاد، نقاشی چینی به علت سکون و بی تحرکی و بدون حالت چهره ها و عدم استفاده از خطوط در چهره برای بیان حالت زمینه مناسبی برای نقاشی ایران در آن روزگار شد و مایه نقاشی ایران گشت. از لحاظ تاریخی مهمترین جریان تکامل نقاشی ایران اقتباس طراحی و رنگ آمیزی و مجلس سازی چینی و تطبیق آن با ادراک خاص هنرمندان ایرانی از فضا و اشکال طبیعی بود. این اقتباس در مدت دو قرن بدست ده ، دوازده نقاش زبر دست تکمیل شد. قدیمی ترین این نقاشان احمد موسی بود که افسانه های بید پای مضبوط در مرقع اسلامبول را مصور کرده است. این هنرمند گلها و درختها و چمن و پرندگان و جانواران کار استادان چینی را در باغی ایرانی نقش کرده است. پس از او شاگردش &laquo; شمس الدین &raquo; است. دو استاد بزرگ دیگر &laquo; میر احمد باغ شمالی&raquo; ( خصوصیات هنر ایرانی را جلوه گر ساخت) و &laquo;بهزاد&raquo; که توانست تعادل زنده ی مجلس را به شبیه سازی و تنوع ترکیب کند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">قاسم علی &laquo; از معاصران بهزاد&raquo; است و میر مصور که مجلس &laquo; وزیر و جغد&raquo; او در خمسه ی نظامی شاه طهماسب برجسته ترین نمونه ی تناسب رنگ در نقاشی ایرانی است و سلطان محمد که طراح بسیار چیره دستی است و &laquo; شیخ محمد&raquo; و &laquo; آقا رضا که در آثارشان برجستگی و ابعاد سه گانه نخستین بار بوسیله ی خطوط ساده جلوه داده می شود و صادقی که جنبشهای طراحی او به خطاطی قدرت کامل داد و &laquo; رضا عباسی &raquo; که در اشکال و صورتهای او گویی همان صور خاص دوره ی ساسانی تجدید شده است از هنرمندان برجسته به شمار می روند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">عامل مهم در شکوفایی نقاشی، پادشاهان تیموری و صفویه هستند که مراکز مهم هنری در زمان خود تأسیس کرده و هنرمندان را گرد هم آوردند. دوستدار هنر و خود نیز هنرمند بودند. در دوره ی صفویه بر اثر مراوده ی شاه عباس با اروپاییان نفوذ هنر اروپا در ایران شیوع می یابد و در این دوره هنرمندان بیش از پیش به تصویر تک صورت پرداختند و این صورتها در مجموعه های نقاشی و خطاطی یا مرقعات جمع و ضبط می شد. علاقه به تک چهره سازی روی متن صفحه روزافزون بود این دوره بر خلاف عهد تیموریان که کمال فنی کتاب مطرح بود، علاقه به هنر برای هنر را رواج داد. در این دوران مصور سازی کتاب و رابطه ی نقاشی با ادبیات از بین می رود و هر چه جلوتر می آییم تأثیرات هنر اروپا را واضح تر مشاهده می کنیم. در این دوران رفت و آمد هنرمندان خارجی و ایرانی و خرید تابلوهای اروپایی از طرف پادشاهان صفویه یا هدایای سفرای خارجی برای شاهان صفویه ، گرایشات جدیدی را در هنر نقاشی ایران به وجود آورد. کم کم توسط نقاشی های اروپایی و سفر نقاشان ایرانی به خارج، رعایت آناتومی و پرسپکتیو در نقاشی ایرانی باب می شود اگر چه موضوع و سوژه کار هنوز ایرانیست، گرایش به سوی نقاشی اروپایی به اوج خود رسیده است، اما پایه و اساس آکادمیک ندارد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">کمال الملک نقاشی است که نقاشی کلاسیک اروپا را عیناً به ایران وارد کرد و مکتب نقاشی اروپایی را به طور آکادمیک و اصولی در ایران پیاده می کند، ولی روحیه مینیاتورریستی خود را حفظ کرده است. پس از آن گرایشهای متفاوتی در نقاشی ایران پدیدار می شود و تحت تأثیر تحولات هنری اروپا قرار می گیرد و این دوران بی هویتی هنر نقاشی و بی تکلیفی نقاشان ایرانی است. در مقابل هجوم و سلطه ی هنر غربی بغضی از هنرمندان به سنتهای گذشته رو می آورند، که غالب آنها در اصفهان بوده اند. ( خانواده ی امامی- حاجی اسلامیان ) که ایشان می کوشند تکنیک قدما را با تأثیرات هنر غربی و دست آوردهای آن تلفیق نمایند و به حل مسائل پرسپکتیو و آناتومی که در نقاشی ایران دید خاص خود را دارد بپردازند . دیدی که بر اساس جهان بینی و معانی سمبولیک ایرانیان پی ریزی شده و به هیچ وجه نشانه ضعف یا عدم آگاهی ایشان از این مسائل نیست، بلکه &laquo; خو است &raquo; است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">غیر از تلاشی که هنرمندان مسلمان برای تزئین کتب دینی به کار می بردند، کتابهای علمی بسیاری در قرون اولیه هجری از یونانی و لاتین به عربی ترجمه شده و از جمله این کتب، منابع علمی پزشکی و دارویی بود که نسخه های اصلی آنها دارای نقاشی بود و ترسیم مجدد آنها اجتناب ناپذیر بود. معرفی گیاهان دارویی . ترسیم شکل آنها را به همراه داشت و لذا نقاشی بوم جدیدی یافت که تا قرن ها دوام آورد و عرصه شکل گیری یکی از ارزنده ترین هنرهای ایرانی گردید.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">نقاشیهایی از دوران اشکانیان، نقاشی های معدودی بر روی دیوار، که بیشتر آنها از قسمتهای شمالی رودخانه فرات بدست آمده، کشف شده است. یکی از این نقاشیها منظره یک شکار را نشان میدهد. وضعیت سوارها و حیوانات و سبک بکار رفته در این نقاشی ما را بیاد منیاتورهای ایرانی می اندازد.نقاشیهایی از دوران اشکانیان، نقاشی های معدودی بر روی دیوار، که بیشتر آنها از قسمتهای شمالی رودخانه فرات بدست آمده، کشف شده است. یکی از این نقاشیها منظره یک شکار را نشان میدهد. وضعیت سوارها و حیوانات و سبک بکار رفته در این نقاشی ما را بیاد منیاتورهای ایرانی می اندازد. در نقاشیهای دوران هخامنشی، نقاشی از روی چهره بر سایر نقاشی های دیگر تقدم داشت. تناسب و زیبایی رنگها از این دوران، بسیار جالب توجه است. نقاشی ها بدون سایه و با همدیگر هماهنگی دارند. در بعضی از موارد، سطوح سیاه پر رنگ را محدود کرده اند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">مانی، (پیامبر و نقاش ایرانی)، که در قرن سوم زندگی میکرد، یک نقاش با تجربه و ماهر بود. نقاشی های او یکی از معجزاتش بود. نقاشی (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Torfan</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) که در صحرای گال استان ترکستان در چین بدست آمد، مربوط است بسال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۸۴۰</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> تا </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۸۶۰</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> بعد از میلاد. این نقاشی های دیوارنما مناظر و تصاویر ایرانی را نمایش میدهند. همچنین تصاویر شاخه های درختی در این نقاشی ها وجود دارند. باستانی ترین نقاشی های دوران اسلامی، که بسیار کمیاب است، در نیمه نخست قرن سیزدهم به وجود آمده بودند. مینیاتورهای ایرانی (طرح های خوب و کوچک) بعد از سقوط بغداد در سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۲۸۵</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی بوجود آمدند. از آغاز قرن چهاردهم کتابهای خطی بوسیله نقاشی از صحنه های جنگ و شکار تزئین شدند. چین از قرن هفتم به بعد به عنوان یک مرکز هنری، مهمترین انگیزه برای هنر نقاشی در ایران بود. از آن به بعد یک رابطه بین نقاشان بودائی چینی و نقاشان ایرانی بوجود آمده است. از لحاظ تاریخی، مهمترین تکامل در هنر ایرانی، تقبل طرحهای چینی بوده است که با رنگ آمیزی که ادراک ویژه هنرمندان ایرانی است مخلوط شده بود. زیبایی و مهارت خارق العاده نقاشان ایرانی واقعاً خارج از توصیف است.</span></span></span>&nbsp;<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در قرن اول اسلامی، هنرمندان ایرانی زینت دادن به کتابها را شروع کردند. کتابها با سرآغاز و حاشیه های زیبا رنگ آمیزی و تزئین شده بود. این طرحها و روشها از یک نسل به نسل دیگر با همان روش و اصلوب منتقل می شد، که معروف است به &quot;هنر روشن سازی&quot;. هنر روشن سازی و زیبا کردن کتابها در زمان سلجوقیان و مغول و تیموریان پیشرفت زیادی کرد. شهرت نقاشی های دوران اسلامی شهرت خود را از مدرسه بغداد داشت. مینیاتورهای مدرسه بغداد، کلاً سبک و روشهای نقاشی های معمولی پیش از دوره اسلامی را گم کرده است. این نقاشی های اولیه و بدعت کارانه فشار هنری لازم را ندارند. مینیاتورهای مدرسه بغداد اصلاً متناسب نیستند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">تصاویر نژاد سامی را نشان میدهد؛ و رنگ روشن استفاده شده در آن نقاشی را. هنرمندان مدرسه بغداد پس از سالها رکود مشتاق بودند که آیین تازه ای را بوجود آورده و ابتکار کنند. نگاه های مخصوص این مدرسه، در طرح حیوانات و با تصاویر شرح دادن داستانها است. اگر چه مدرسه بغداد، هنر پیش از دوره اسلامی را تا حدی در نظر گرفته بود، که بسیار سطحی و بدوی بود، در همان دوره هنر مینیاتور ایرانی در تمام قلمرو اسلامی از آسیای دور تا آفریقا و اروپا پخش شده بود.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">از میان کتابهای مصور به سبک بغداد میتوان به کتاب &quot;کلیله و دمنه&quot; اشاره کرد. تصاویر نامتناسب و بزرگتر از حد معمول رنگ شده است؛ و فقط رنگهای معدودی در این نقاشیها بکار رفته است. بیشتر کتابهای خطی قرن سیزدهم، افسانه ها و داستانها، با تصاویری از حیوانات و سبزیجات تزئین شده است. یکی از قدیمی ترین کتابهای کوچک طراحی شده ایرانی بنام &quot;مناف الحیوان&quot; در سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۲۹۹</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی بوجود آمده است. این کتاب مشخصات حیوانات را شرح میدهد. در این کتاب تاریخ طبیعی با افسانه در هم آمیخته شده است. موضوعات این کتاب که تصاویر بسیاری دارد، برای آشنایی با هنر نقاشی ایرانی بسیار مهم است. رنگها روشن تر و از روش مدرسه بغداد که روشی قدیمی بود جلوتر است. پس از هجوم مغولان، یک مدرسه جدید در ایران به نظر میرسید. این مدرسه تحت تأثیر از هنر چینی و سبک مغول بود. این نقاشی ها همه خشک و بی حرکت و خالص و یکسان، مانند سبک چینی است. بعد از حمله مغول به ایران، آنها تحت تأثیر از هنر ایرانی، نقاشان و هنرمندان را تشویق کردند. در میان نقاشی های هنرمندان ایرانی میتوان سبک مغول هم مشاهده کرد، لطافت ها، ترکیبات آرایشی، و خطوط کوتاه خوب که میتوان آنها را بشمار آورد. نقاشی های ایرانی بصورت خطی و نه ابعادی می باشد. هنرمندان در این زمینه از خود یک خلاقیت و اصالت نشان داده اند. هنرمندان مغول دادگاه سلطنتی نه فقط به تکنیک بلکه به موضوعات ایرانی هم احترام گذاشتند. یک بخش از کارشان شرح دادن به آثار ادبی ایران مانند شاهنامه فردوسی بود. میان موضوعات مختلف بیشترین علاقه آنها به تصویریسم (کتابهای با تصاویر زیاد) بود. بر خلاف مدارس مغول و بغداد بیشترین کارها از مدرسه هرات بجا مانده است. مؤسس این سبک نقاشی مدرسه هرات بود؛ که از نیاکان تیموریان بودند و این مدرسه را بخاطر محل تأسیس آن مدرسه هراتی نامیدند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">متخصاص هنر نقاشی بر این باورند، که نقاشی در ایران در دوران تیموری به اوج خود رسیده بود. در طول این دوره استادان برجسته ای، همچون کمال الدین بهزاد، یک متد جدید را به نقاشی ایران عرضه کرد. در این دوره (تیموریان) که از سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۳۷۰</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> تا </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۴۰۵</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی به طول انجامید؛ هنر نقاشی و کوچک سازی به بالاترین درجه کمال رسیده و بسیاری از نقاشان مشهور عمرشان را بر سر اینکار گذاشته اند. دو کتاب با ارزش از زمان بایسغر باقی مانده است؛ یکی کتاب &quot;کلیله و دمنه&quot; و دیگری کتاب &quot;شاهنامه&quot;. هنر کوچک ایرانی در کتاب شاهنامه که در سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۴۴۴</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی در شیراز رنگ آمیزی شده است، بخوبی مشخص است. یکی از این طرح ها نشان دادن یک منظره زیبا از یک دادگاه ایرانی است که به سبک چینی رنگ آمیزی شده است. کاشی های سفید و آبی ایرانی همراه با فرشهای زیبای ایرانی بصورت هندسی نقش شده است. در یکی از کتابهای دستنویس &quot;خمسه نظامی گنجوی&quot;، سیزده مینیاتور زیبا بوسیله &quot;میرک&quot; کشیده شده است. ساختگی بودن، حساسیت، و هنر نقاشی های بغداد از طرحهای کشیده شده در کتاب &quot;خمسه نظامی&quot; کاملا مشهود است. این قطعه با ارزش و فوق العاده هم اکنون در موزه بریتانیا قرار دارد. در این قسمت از معماران و مهندسان مشغول ساختمان سازی هستند. این نقاشی در سال </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۴۹۴</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> میلادی در هرات نقاشی شده است. بهزاد، بهترین استاد سبک مدرسه هراتی، این هنر را بسیار بسط داد. او سبک را اختراع کرد که قبل از او هرگز استفاده نشده بود. یکی از برجسته ترین کارها کتاب شاهنامه فردوسی است که در کتابخانه گلستان ایران قرار دارد. این شاهنامه در زمان سلطان بایسغر که یکی از شاهزادگان تیموری بود؛ به سبک مدرسه هراتی نقاشی شده است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">نقاشی های این کتاب از نگاه رنگ آمیزی و تناسب ترکیب دهنده تصاویر، در بالاترین حد زیبایی و استحکام قرار دارد. در دوران صفویه مرکزیت هنر به شهر تبریز آمد. و برخی از هنرمندان هم در شهر قزوین ماندگار شدند. اما اصل سبک نقاشی صفویه در شهر اصفهان بوجود آمد. مینیاتورهای ایرانی، در دوره صفویه در شهر اصفهان، از سبک چینی جدا شده و قدم در راهی جدید نهاد. این نقاشان بعدها به سبک طبیعی متمایل شدند. رضا عباسی مؤسس سبک &quot;مدرسه نقاشی صفوی&quot; بود. در نقاشی های دوران صفوی دگرگونی بسیار عالی در نقاشی ایرانی رخ داد. طرحهای این دوره یکی از بهترین و زیبا ترین و با سلیقه ترین طرحهای نقاشان ایرانی است که نشان دهنده ذوق نقاشان ایرانی است. مینیاتورهای خلق شده در این دوره(مدرسه صفوی)، هیچکدام منحصراً با هدف مزین کردن کتابها کشیده نشده اند. سبک صفوی بسیار بهتر و ملایم تر از سبک مدرسه تیموریان (هراتی) و مخصوصاً سبک مغولی است. در نقاشی نقاشان دوران صفوی آشکار شدن تخصص آنها در این رشته بوضوح مشخص است. بهترین معرف از این دوره نقاشی های هستند که در عمارت چهل ستون و عالی قاپو کشیده شده است. در نقاشی های صفوی، موضوع اصلی شکوه و زیبایی این دوره است. موضوعات نقاشی ها بیشتر حول محور بارگاه سلاطین، اشراف زادگان، کاخ های زیبا، مناظر زیبا و صحنه هایی از جنگ ها است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در این نقاشی ها انسانها با لباسهای پرخرج نخ کشی، صورتهایی زیبا و مجسمه های ظریف رنگین به طور پر هیجان روشن به تصویر کشیده شده است. هنر نقاشی در طول دوران صفوی هم زیاد و هم دارای کیفیت بهتر شد. در این نقاشی ها آزادی بیشتر و مهارت و دقت بیشتر مشهود است. هنرمندان بیشتر به اصول کلی پرداخته و از جزئیات غیر ضروری اجتناب، که در شیوه هراتی و تبریزی بکار رفته بود، خودداری کردند. صافی خطوط، بیان زود احساسات و متراکم شدن موضوعات از مشخصات سبک نقاشی صفوی است. از آنجاییکه در اواخر دوران صفوی، از لحاظ جنبه فکری اختلافات جزئی در رنگ آمیزی بوجود آورد، میتوان به ظهور سبک اروپایی در این نقاشی ها پی برد. نقاشی های دوران قاجاریه (قرن شانزدهم میلادی) یک ترکیبی از سبک هنرهای اروپایی کلاسیک و سبک و تکنیک مینیاتورهای دوران صوفی است. در این دوران &quot;محمد غفاری) کمال الملک(&quot; سبک کلاسیک اروپایی را در ایران رواج داد. در این دوران سبکی از نقاشی بوجود آمد که بنام &quot;قهوه خانه&quot; شناخته شد. این نوع نقاشی یک پدیده جدید در تاریخ هنر ایرانی است. سبک &quot;قهوه خانه&quot; عامه پسند و مذهبی است. موضوعات این سبک بیشتر تصاویر پیامبران و امامان، شعائر مذهبی، جنگها و نام آوران ملی بودند&quot;قهوه خانه ها&quot; رفته رفته جای خود را در بین مردم معمولی باز نمودند. در این مکان داستان سرایان و نقالان داستانهای حماسی و مذهبی را برای مردم بازگو میکردند. هنرمندان همان داستانها را بر روی دیوارهای این &quot;قهوه خانه ها&quot; نقاشی کرده بودند. قبلا در گذشته که پادشاهان و اشراف زادگان نقاشان را پشتیبانی میکردند؛ اما اینبار هنرمندان به درخواست مردم عادی آن مناظر را بر روی دیوارها میکشیدند و به این کار علاقمند بودند. در بیشتر این قهوه خانه ها این مناظر که بیشتر به درخواست عمومی بود رنگ آمیزی شده بود. زیباترین مثالها در موزه اصلی تهران و همینطور در بعضی از موزه های خصوصی داخل و خارج از کشور نگهداری میشود. نقاشی های ایرانی، یک نوع ملاحت را که بی شباهت به هر چیز دیگر است به تماشاگر عرضه میکند. آنها یک ارتباط بیکران با داستانهای حماسی نگه داشته اند. نقاشی ایرانی، بعنوان یکی از بزرگترین سبکهای نقاشی در آسیا مطرح است. آسمان های روشن (آبی)، زیبایی شگفت انگیز شکوفه ها و در میان آنها انسانهایی که دوست میدارند و انسانهایی که تنفر دارند، خوشگذران و افسرده، به موضوعات مختلف نقاشان ایرانی شکل میدهند</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">هنگامی که شاه&zwnj;عباس اول در </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۹۹۶</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ه. به حکومت رسید، مینیاتور ایران که پیشینه تابناکی را از عهد تیموری و اوایل صفوی پشت&zwnj;سر داشت، در اوج ترقی سیر می&zwnj;کرد و نگارگران شیرین&zwnj;کار و توانا در تنوع بخشیدن به کارهای خود و ابداع شیوه&zwnj;های تازه و تکامل بخش، همچنان راه جویی و رهیابی می&zwnj;کردند. این تکاپو، از &laquo;مکتب هرات&raquo; که از مهمترین و اصیل&zwnj;ترین مکتب&zwnj;های مینیاتور ایران است، آغاز شد و در تبریز در زمان شاه&zwnj;اسماعیل صفوی (</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۹۰۵</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ـ</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۹۳۰</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ه) رنگ و سنگی دیگر یافت و شیوه جالبی به همت امثال سلطان&zwnj;محمد، قاسم علی چهر&zwnj;ه&zwnj;گشا، مظفرعلی و دیگران به وجود آمد که &laquo;مکتب تبریز&raquo; نامیده شد.سپس در دوران پادشاهی شاه تهماسب (</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۹۳۰</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ـ</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۹۸۴</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ه) با جابه&zwnj;جایی پایتخت از تبریز به قزوین و تصرفات زیبنده&zwnj;ای که در مکتب سابق به عمل آمد، &laquo;مکتب قزوین&raquo; به ظهور رسید که دارای بسیاری از سحرآفرینی&zwnj;های شیوه &laquo;هرات&raquo; و &laquo;تبریز&raquo; بود و افزون بر این، از ظرافت&zwnj;های دیگر مانند جوان&zwnj;نمایی و خوشگل&zwnj;سازی چهره&zwnj;های اشخاص و ریزه&zwnj;کاری در منظره&zwnj;های عمارات سلطنتی بهره&zwnj;ها داشت.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">پس از آنکه بنابر تصمیم شاه&zwnj;عباس، پایتخت ایران از قزوین به اصفهان منتقل شد، عده&zwnj;ای از نگارگران دربار صفوی نیز به اصفهان کوچیدند و در این شهر هنرخیز به پدید آوردن نگاره&zwnj;های ارزنده خود در مینیاتور همت گماشتند؛ سلیقه&zwnj;های تازه&zwnj;ای که با پاسداری از اصالت فن، این نقاشان توانمند مانند رضا عباسی، حبیب&zwnj;الله مشهدی، فضل&zwnj;الدین حسینی، صادقی بیک، معین مصوّر و محمد یوسف در آفریدن آثار بدیع از خود به ظهور رساندند و تغییراتی که در صحنه&zwnj;پردازی و رنگ&zwnj;آمیزی و چهره&zwnj;نگاری و توجه به پدید آوردن نقاشی&zwnj;های تک صورته و چند صورته به وجود آوردند، سبک تازه&zwnj;ای را به معرض نمایش آورد که با مکتب&zwnj;های پیش از&zwnj;آن تفاوت داشت و به نام &laquo;مکتب اصفهان&raquo; شهرت یافت.مکتب نوزاد یاد شده که تا میانه&zwnj;های دوران شاه&zwnj;صفی گام به گام مراحل پیشرفت را می&zwnj;پیمود و استادان چربدستی چون شفیع عباسی، محمد یوسف، صادقی بیک و معین مصّور آن را کمال و رونق بخشیدند، تا آنجا پیش رفت که اگر از پاره&zwnj;ای جهات و از آن جمله رنگ&zwnj;آمیزی به پایه مکتب هرات و تبریز نرسید، ولی در صورت سازیهای مجالس و چهره&zwnj;های تک نفره بر مکتب&zwnj;های پیش برتری یافت. از ویژگیهای این سبک در چهره&zwnj;نگاری، نزدیک شدن آن به شبیه&zwnj;سازی است که در مجلس آرایی توجه برانگیز و خوشایند است. در این مکتب، مینیاتور ایران از تاثیر چینی&zwnj;سازی تا حد قابل توجهی دامن فراچید و چهره&zwnj;ها و مناظر به واقعیت نزدیک&zwnj;تر شد و رنگها به ملایمت و ماتی گرایید.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">از جمله نامبرداران این زمان رضای عباسی است که در عین تازگی کار، اصالت سبک را رعایت می&zwnj;کرد و افزون بر مجلس&zwnj;پردازی، در تک صورت&zwnj;سازی، یگانه عصر بود و شیوه شیخ محمد سبزواری و نگارگر معاصر شاه تهماسب را به سر حد کمال رسانید.مکتب اصثفهان پس از طی دوره بالندگی و رشد، هرچه به عصر پایانی صفوی نزدیک&zwnj;تر می&zwnj;شد، به انحطاط می&zwnj;پیوست و این فروافتادگی نه تنها در این مکتب، بلکه در مکاتب دیگر نیز مشهود بود و به&zwnj;طور کلی مینیاتور ایران در نقطه اوج خود گرایشی به سقوط پیدا کرد.چون پس از طی بیش از دویست سال که از آغاز ترقی مینیاتور می&zwnj;گذشت و شاهکارهای ارزشمندی در نقاشی ایران به دست استادان بزرگی مانند جنید، بهزاد، میرک، شیخ&zwnj;زاده، محمدی، سلطان محمد، میرسیدعلی و میرزاعلی و دیگران به عرصه&zwnj; شهود آمده بود، به علت رکود و انحطاط عارض برای این هنر، ظهور یک تحول در سبک نقاشی انتظار می&zwnj;رفت و پدید آمدن شیوه تازه&zwnj;ای که در عین تفاوت با مینیاتور، مورد پسند هنرشناسان قرار گیرد زمینه پیدا کرده بود، از این رو تنی چند از استادان چیره&zwnj;دست، پس از آشنایی با روش پی&zwnj;گیری شده نقاشان هند در اقتباس پاره&zwnj;ای از ویژگیهای نقاشی فرنگستان و مأنوس شدن با این سبک از روی باسمه&zwnj;های فرنگی، بر آن شدند که در نقاشی ایران به گونه&zwnj;ای منطقی تغییراتی بدهند و اسلوب تازه&zwnj;ای پدید آورند.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">یکی از سردستگان نامور و موفّق این عده، محمد زمان بود که وقتی به حکم شاه عباس دوم برای فراگرفتن فنون نقاشی با چند تن دیگر به اروپا اعزام گردید، سخت مجذوب سبک فرنگی شد و شیوه نقاشان ایتالیا را آماج فراگیریهای خود ساخت و با استعداد فزون&zwnj;مایه&zwnj;ای که داشت، در این مکتب پیشرفتهایی حاصل کرد و در تقلید استادان طراز نخستین، نمونه&zwnj;هایی خوب به نظر رسانید؛ وی پس از دو یا سه سال توقف در رم، تصمیم به بازگشت به ایران گرفت و چون متهم به تغییر مذهب شده بود، اندک زمانی بعد از ورود به وطن، به سوی هندوستان رخت سفر بست و در آنجا از طرف شاه جهان با مقررّی و منصب داری به کشمیر فرستاده شد. این نقاش در هند، &laquo;سبک هند و ایران&raquo; را که از شاخه&zwnj;های مینیاتور ایرانی است، مورد تتبع قرار داد و از آمیختن سبک هندی با ایرانی و ایتالیایی مکتبی به نام &laquo;اندپرسان ایتالین&raquo; به وجود آورد. محمد زمان در این مکتب که التقاطی از چند شیوه بود، به تدریج جنبه ایرانی را بر دو جنبه دیگر چربانید و برتری داد و سبک معروف به &laquo;ایرانی&zwnj;سازی&raquo; را ابداع کرد. از ویژگیهای این سبک، اقتباس (دور و نزدیک نمایی) یاپرسپکتیو و رعایت قواعد مربوط به آن بود. افزون بر این، از ویژگیهای محلی ایران و چاشنی ظرافت و جاذبه مینیاتور ایرانی و لطافت پردازکاری و درخشندگی رنگها بهره کافی داشت و می&zwnj;توانس', 'off', 'en', '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/14.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_14.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_14.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_14.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_14.jpg\"}', 'نقاشی , هنردستی , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , اولین نقاش ,  نقاش ایرانی', 'news3', 4707, 0, '2020-03-28 08:10:23', '2024-07-15 20:27:37');
INSERT INTO `articles` (`id`, `title`, `slug`, `description`, `body`, `showonmenu`, `lang`, `images`, `tags`, `type`, `viewCount`, `commentCount`, `created_at`, `updated_at`) VALUES
(6, 'انیمیشن پس از انقلاب', 'انیمیشن پس از انقلاب', 'انیمیشن ایران پس از انقلاب با بازگشایی دوباره دانشگاه ها و شکل گیری آسیفا (انجمن بین‌المللی سازندگان و دوست‌داران فیلم‌های انیمیشن) حرکت خود را قدم به قدم آغاز کرد.', '<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">انیمیشن ایران پس از انقلاب با بازگشایی دوباره دانشگاه ها و شکل گیری</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">آسیفا</span></span></span></strong><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> (</span></span></span></strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">انجمن بین&zwnj;المللی سازندگان و دوست&zwnj;داران فیلم&zwnj;های انیمیشن) حرکت خود را قدم به قدم آغاز کرد. مراکزی چون</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">وزارت فرهنگ و ارشاد اسلامی، کانون پرورش فکری کودکان و نوجوانان، سیمای جمهوری اسلامی ایران(مراکز استان ها) مرکز صبا، انجمن سینمای جوانان ایران (به شکل نیمه حرفه ای)، مرکز گسترش سینمای مستند و تجربی (به صورت حرفه ای)، حوزه هنری سازمان تبلیغات اسلامی</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">و بخش خصوصی اقدام به جذب نیرو و تولید و ساخت فیلم های انیمیشن کردند. حدود </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۵</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> سال پیش به غیر از این مراکز، استودیو حور تنها مرکز تخصصی تولید انیمیشن بود؛ اما در حال حاضر بیش از چند صد استودیوی کوچک و بزرگ در سطح کشور به این فعالیت مشغول اند. انیمیشن ایران همزمان با تولیدات و توجه صاحبان کالا و مدیران بخش دولتی و سرمایه گذاران خصوصی، بیش از پیش مورد توجه قرار گرفت و توسعه بیشتری یافت</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">برپایه برآوردهای انجام شده هم اکنون بیش از چند هزار تن در این حرفه مشغول هستند و همزمان شمار علاقه مندان و هنرجویان آن رو به فزونی است. پیش بینی می شود که ظرفیت اشتغال در این حرفه به بیش از</span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۲۰</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> هزار تن قابل افزایش است و این واقعیت با میزان نیاز تلویزیون و بنگاه های تبلیغاتی داخلی بدون درنظر گرفتن بازارهای جهانی و آثار سینمایی است. ورود رایانه در دهه گذشته به عرصه تولیدات انیمیشن در کشور ما، انقلاب جدیدی در این زمینه به وجود آورد و هنر پرخرج و سخت انیمیشن را به حرفه ای خودگیر و همه گیر تبدیل کرد، تا جایی که کاربران علاقه مند در خانه خود و با امکاناتی در حد یک دوربین ساده و رایانه شخصی، امکان فعالیت در این زمینه را یافته اند. گسترش ارتباطات اینترنتی و</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> IT </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">موجب شد که نرم افزارهای قدرتمند تولید انیمیشن به کشور ما راه یابد و علاوه بر تکنیک سه بعدی که به وسیله همین نرم افزارها صورت می گیرد، سرعت و دقت بیشتری در تولیدات </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۲</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">بعدی و سنتی ما به وجود آورد</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">با انیمیشن، زمان لازم برای بیان اندیشه ها نزدیکتر به زمان مرسوم می رسد. این شکل از آموزش بصری را می توان برای استفاده عام رواج داد. گذر انیمیشن ایران از مرحله سنتی به صنعتی آخرین تحولی است که در این چند سال در حال وقوع است. ضعف مدیریت، برنامه ریزی، کار گروهی و... از جمله آسیب هایی هستند که هم اکنون در این حوزه با آن مواجه ایم. راهی که کشورهای موفق در انیمیشن چند دهه پیش پشت سر گذاشته اند هم اکنون پیش روی ماست و بی تردید چالش های فراوانی از جمله آموزش و پژوهش صحیح و کاربردی، توجه حرفه ای به پیش تولید و توزیع مناسب محصولات تولیدشده و مدیریت برنامه ریزی دولتی و خصوصی بلندمدت لازمه عبور از این چالش ها است</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">بی تردید با افت نسبی سینما در ایران و پیشروی جهان به طرف آثار انیمیشن، ناگزیر به همراهی و همگامی با هنر روز جهان خواهیم بود. آثار سینمایی ساخته شده در عرصه انیمیشن در چند سال گذشته از جمله</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">خورشید مصر</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">از نادر یغماییان و شهرام خوارزمی<strong>، جمشید و خورشید</strong></span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">اثر بهروز یغماییان،</span></span></span><strong>&nbsp;</strong><strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">داستان های شاهنامه</span></span></span></strong>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">از حسین مرادی زاده،</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">هفت رنگ</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">ساخته مازیار محمدی نژاد،</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">تهران </span></span></span></strong><strong><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۵۰۰</span></span></span></strong>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">از بهرام عظیمی،</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">قلب سیمرغ</span></span></span></strong>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">اثر وحید نصیریان و</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">رستم و سهراب</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">از کورش دالوند از جمله این دستاوردها به شمار می رود</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> .</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>', 'off', 'fa', '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/8.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_8.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_8.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_8.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_8.jpg\"}', 'انیمیشن , انقلاب،موشن گرافیک،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'news1', 807, 0, '2020-03-31 15:48:52', '2024-07-15 13:19:48'),
(7, 'تاریخچه بازیگری', 'تاریخچه بازیگری', 'در اروپا پيوند بين تئاتر و سينما نزديک­تر بود گرچه هنرپيشه­ها معمولاً از تئاتر به فيلم راه پيدا مي­کردند و نه بالعکس. «آستانيلسن» (A. Nielsen) بازيگري سينمايي را به حد تکاملي رساند که فقط ليليان گيش با او برابري مي­کرد.', '<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در اروپا پيوند بين تئاتر و سينما نزديک&shy;تر بود گرچه هنرپيشه&shy;ها معمولاً از تئاتر به فيلم راه پيدا مي&shy;کردند و نه بالعکس. &laquo;آستانيلسن&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">A. Nielsen</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) بازيگري سينمايي را به حد تکاملي رساند که فقط ليليان گيش با او برابري مي&shy;کرد. از مردان &laquo;اميل يانينگز&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">E. Jannings</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) علاقمندان فراوان و شهرت بسيار داشت. هاليوود ذخيره بازيگران خود را با جذب هنرپيشه&shy;هاي ديگري از اروپا غني ساخت. از آن جمله بودند &laquo;گرتا گاربو&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">G. Garbo</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) &laquo;پولا نگري&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">P. Negri</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">)، &laquo;چارلز لوتون&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Ch. Laughton</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) و لارس هانسن (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">L. Hansen</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">). پيشرفت&shy;هاي فني و بالا رفتن سطح فهم و توقع تماشاگران در تغيير شيوه بازيگري سينمائي اثر داشت، ظرافت و عمق بازي جنت گي&shy;نور (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">J. Gaynor</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) در فيلم &laquo;طلوع&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Sunrise) (1927</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) اثر ف. و. مورنا (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">F. W. Murnau</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) تا به امروز تازه است. بازي &laquo;گاربو&raquo; نيز جاذبه خود را از سينماي صامت تا ناطق همچنان حفظ کرده است. ظهور صدا د سينما بين هنرپيشگان ضايعاتي به بار آورد، بعضي از بازيگران بد لهج? اروپايي (يانينگز، پولا نگري) مجبور به بازشگت به کشورهايشان شدند. صداي نامناسب بعضي ديگر مخصوصاً با دستگاه&shy;هاي ناقص ضبط صوت آن ايام، ادامه کار را در سينماي ناطق برايشان مشکل ساخت. بارزترين نمونه، سقوط هنرپيشه بسيار مشهور &laquo;جان گيلبرت&raquo; بود. ناطق لزوم مبالغه در بازي و تکيه بر حرکات را از ميان بر مي&zwnj;داشت در نتيجه تطابق با اين سبک جديد بازيگري براي خيلي از بازيگران تئاتري يا بازيگران سينماي صامت دشوار بود، با وجود اين بعضي از بازيگراني که از تئاتر شروع کرده بودند به سبب قوت و اصالت شخصيت و جاذبه فردي خاص در سينما ماندند و متمايز شدند، از آن جمله&shy;اند بتي ديويس (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Bette Davis</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) هامفري بوگارت (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">H. Bogart</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">)، کاري گرانت (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">C. Grant</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">)، کاترين هپبرن (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">K. Hepburn</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">)، ادوارد جي رابينسون (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">E. G. Robinson</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">)، اسپنسر تريسي (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">S. Tracy</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) و هنري فاند (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">H. Fondal</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) که همگي از تبحر فني و جاذبه شخصي برخوردار بودند. اما سپردن نقش&shy;هاي قالبي به هنرپيشه&shy;ها، حوزه هنرنمايي آنها را محدود به تکرار نقش&shy;هاي کمابيش مشابهي مجبورشان مي&shy;کرد. با پيدايش ناطق، بازي کمدي در سينما نيز دگرگوني عمده&zwnj;اي را متحمل شد. اولين دوربين&shy;هاي فيلمبرداري ناطق ساکن بود اين با تحرک لازم? فيلم&shy;هاي کمدي جور در نمي&shy;آمد، به علاوه در اولين فيلم&shy;هاي ناطق، به صورت يک جور تفريط، حرف زياد وجود داشت، در نتيجه کمدي بزن و بشکن (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Stap Stick</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) که در دست امثال &laquo;چاپلين&raquo; و &laquo;کيتون&raquo; تکميل شده بود جاي خود را به کمدي لفظي و متلک پران داد. پيوند بين سينما و تئاتر و بهره&shy;گيري متقابل اين دو از هم در بازيگري يک نوع سبک ناتوراليستي نسبتاً متعادل به وجود آورد گرچه در دهه 30 و 40 بازيک جور تيپ&shy;هاي قالبي در سينما برقرار بودند و مثلاً مردها عموماً ريخت&shy;هاي تر و تميز و اطو کشيده&shy;اي داشتند. از اوايل دهه پنجاه اين نوع تيپ&shy;سازي هم کم کم کنار رفت، مخصوصاً از موقعي که &laquo;ايليا کازان&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">E. Kazan</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) با استفاده از هنرپيشه&shy;هاي آکتورزاستوديو (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Actors&#39; Studio</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) فيلم&shy;هاي خود را عرضه کرد. بازي &laquo;مارلون براندو&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">M. Brando</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) و &laquo;جيمز دين&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">J. Dean</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) از هنرپيشگان اين کارگاه که بر اساس تعليمات بازيگري معلم روس &laquo;استانيسلاسکي&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Stanislavsky</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) پايه&shy;گذاري شده بود در شکل و کيفيت بازيگري مردان سينماي آمريکا اثر زيادي بجا گذاشت و براي نقش خلاقه هنرپيشه اهميت بيشتري را به وجود آورد. خارج از آمريکا و انگليس بعد از ظهور صدا، مرز بين بازي تئاتري و سينمائي نسبتاً تفکيک ناشده باقي ماند، مخصوصاً در فرانسه که هنرپيشه بيشتر &laquo;هنرمند&raquo; قلمداد مي&shy;شد تا کالاي تجاري. &laquo;ژان رنوآر&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">J. Renoir</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) در فيلم&shy;هاي قبل از جنگ خود در خلق نقش به بازيگران آزادي بي&shy;سابقه&shy;اي داد. در سينماي ايتالياي بعد از جنگ استفاده از افراد غير حرفه&shy;اي براي خلق نقش&shy;هايي مشابه با آنچه در زندگي داشتند از اساس خصوصيات جنبش نئورئاليزم (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Neorealism</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) بود (بنيان&shy;گذار اين روش را &laquo;آيزنشتاين&raquo; </span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Eisenstein</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> فيلمساز روس مي&shy;دانند). اين روش تا به امروز در بين فيلمسازاني که سعي در ايجاد فيلم&shy;هاي نيمه مستند دارند باقي مانده است. &laquo;روبر برسون&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">R. Bresson</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) در فرانسه و &laquo;کن لوچ&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">K. Loach</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) در انگلستان دو تن از پيروان اين شيوه هستند. بعضي از بهترين نمونه&shy;هاي بازيگري در آثار فيلمسازاني ديده شده که خود سابقه&shy;ي هنرپيشگي داشته&shy;اند. کساني مثل &laquo;رنوار&raquo; و &laquo;دوويويه&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Duvivier</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) در فرانسه، &laquo;دسيکا&raquo; و &laquo;فليني&raquo; در ايتاليا، &laquo;گريفيت&raquo; و &laquo;اشتروهايم&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Stroheim</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) در آمريکا، &laquo;پودوفکين&raquo; و &laquo;بوندارچوک&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Bundarchuk</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) در روسيه، &laquo;ميزوگوچي&raquo; در ژاپن. در مدرسه سينمايي مسکو، دانشجويان دوره کارگرداني بايد يک دوره بازيگري را هم ببينند. در آمريکا &laquo;جان کاساويتس&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">J. Cassavetts</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) با فيلم&shy;هاي شخصي و نسبتاً تجربي&shy;اش چون &laquo;سايه&shy;ها&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Shadows) (1960</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) &laquo;چهره&shy;ها&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Faces) (1968</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) و &laquo;شوهران&raquo; (</span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Husbands) (1970</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) شيوه&shy;اي از بداهه&shy;سازي را در بازيگري سينمايي ارائه داد، شيوه&shy;اي که خارج از فيلم&shy;هاي معدود او و چند تن ديگر گسترش چنداني نداشته است.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">برداشت</span></span></span></strong>&nbsp;<strong><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Take</span></span></span></strong>&nbsp;<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">يک نوبت فيلمبرداري از يک &laquo;نما&raquo; با يک صحنه. بعضي فيلمسازها معمولاً يکي دو نوبت بيشتر از يک صحنه فيلم نمي&shy;گيرند (مثل &laquo;جان فورد&raquo;)، و بعضي فيلمبرداري از يک &laquo;نما&raquo;ي واحد را چندين بار تکرار مي&shy;کنند (مثل &laquo;ويليام وايلر&raquo; که او را به شوخي &laquo;وايلر 90 برداشتي&raquo; - 90 </span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Take Wyler</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> مي&shy;گفتند). يک &laquo;برداشت&raquo; ممکن است از &laquo;نما&raquo;يي کوتاه و چند ثانيه&shy;اي باشد و يا ممکن است به اندازه&shy;ي ظرفيت قرقره&shy;ي فيلم خام (ده دقيقه) طول بکشد برداشت ده دقيقه&shy;اي 10 </span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Minute Take</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> را اولين بار &laquo;هيچکاک&raquo; در فيلم &laquo;طناب&raquo; </span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">The Rope (1948</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">) به کار برد. &laquo;برداشت&raquo; در واقع همان &laquo;نما&raquo; </span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Shot</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> ـ است، منتها &laquo;برداشت&raquo; از يک &laquo;نما&raquo; ممکن است تکرار شود و يک (&laquo;نما&raquo;)ي واحد سه يا چهار نوبت يا بيشتر فيلمبرداري (برداشت) شود و يکي که به نظر کارگردان بهترين است در فيلم به کار رود.</span></span></span>&nbsp;<strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">برداشت کنار گذاشته شده </span></span></span></strong><strong><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Out-Take</span></span></span></strong><strong>&nbsp;</strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">برداشتي که در نسخه نهايي فيلم به کار نمي&shy;رود. از يک صحنه معمولاً بيش از يک بار فيلم مي&shy;گيرند و به هر نوبت فيلم گرفتن يک &laquo;برداشت&raquo; گفته مي&shy;شود (به واژه&shy;ي </span></span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Take</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> رجوع شود).</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">نگاهی گذرا به تاریخچه بازیگری در جهان</span></span></span></span><span style=\"font-size:16.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">...</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">بازيگری سينما ريشه در سنت بازيگری تاتر دارد . اما به تدريج به شيوه ای مستقل بدل شده است . مجريان نقش در فيلمهای اوليه لوميير و اديسن افراد عادی بودند که نقش های خودشان را ايفا می کردند . اما با تکامل سينمای داستانی ، نياز به بازيگران حرفه ای افزايش يافت</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">طی سالهای </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۹۰۰</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> بازيگران تاتری ، سينما را مديومی خام تلقی می کردند و به ديده تحقير می نگريستند . بسياری از مجريان نقش درام های نخستين تاريخ سينما ، آماتور يا کنار گذاشته شده های تاتر بودند . آنها شيوه ای از بازيگری را برای فيلم برگزيدند که در تاتر آن دوران رواج داشت ، يعنی بازی تصنعی و پرطمطراق که ویژگی اش ژست های غلو آمیز بود</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۹۰۸</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> کمپانی فیلم فرانسوی</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> (( Film d Art )) </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">گروه بازيگران کمدی ـ فرانسز</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> ((Comedie_ Francaise)) </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">را ترغيب کرد تا اجازه دهند از چند اجرای صحنه شان با تمام گروه نقش آفرينان ، بزرگانی چون سارا برنار ، گابريل رژان و ماکس دوآرلی ، فيلم برداری شود . استقبال از این فیلمها به ویژه قتل دوک دوگیز ، باعث شد تا انگ (( بی ارزش )) از سینما زدوده شود اما کمک چندانی به بهبود و توسعه سینما نکرد ، در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۹۰۹</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> گریفیث گروهی از بازیگران جوان را گرد هم آورد و مدام با آنها تمرین کرد تا اینکه توانست به یک شیوه جدید و متعادل بازیگری دست یابد که تاُثیری ماندگار بر تکامل هنر سینما داشت</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">شرایط نقش آفرینی بازیگران در صحنه تاتر و بر پرده تفاوت های اساسی دارد . بازیگر تاتر می تواند طی یک نقش آفرینی شخصیتی را بسازد و آن را حفظ کند ، اما بازیگر سینما با مزیت و ضعف فن سینما روبرو است ـ برداشت های کوتاه ، فیلم برداری ناپیوسته ، تغییر زاویه و تکرار بیشمار صحنه</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">هنر بازیگر سینما به ویژه کسی که (( تیپ )) شده است ، تفاوت بسیاری با هنر همتای تاتریش دارد چون به طور معمول بازیگر سینما هیچ پیش فرضی از هویت شخصیت ندارد بلکه بیشتر ، تغییرهای ظریفی از شخصیت</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> (personality ) </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">خودش را عرضه می کند که کمی زیر قالب هایی که خود فرض می کند مستتر شده است</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">تهیه کنندگان گروه گروه بازیگران تاتر را وارد حرفه بازیگری سینما کردند . اینان تا حدودی موفق هم بودند اما بسیاری نتوانستند موفقیت را در آغوش بکشند چون سینما خواهان شیوه بیانی عادی تر از تاتر بود</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">سهم بازیگری در کل کیفیت یک تولید ، از فیلم به فیلم تغییر می کند . کارگردان های مشخصی مانند کلارنس براون ، سیدنی فرانکلین و جرج کیوکر ، به کارگردان های بازیگر لقب گرفته اند چون تمایل داشتند خود را کاملا ْبه استعداد و شخصیت مجریان نقش ها متکی کنند ، کارگردان های دیگر نظیر جوزف فن ، اشترنبرگ و آلفرد هیچکاک ، ترجیح می دادند بازیگر را فقط عنصری از میزانسن</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> (( mise _en_scene )) </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">تلقی کنند</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">بدون اشاره به سیستم ستاره سازی هیچ مبحثی در زمینه بازیگری سینما کامل نخواهد بود ، این پدیده برای تولید فیلم در هالیوود بسیار اساسی است ،سقوط کنترل ثابت و سخت استودیوها نیز تا بدان حد بر قدرت تعدادی از ستارگان افزوده است که بسیاری از فیلمها فقط به صرف حضورشان تولید می شود</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ستاره ها انعکاس تصویر و تصور مردم اند ... تاریخ اجتماعی یک ملت می تواند از خلال بررسی و مطالعه ستاره های سینمایی آن نگاشته شود . (( ریمند درگنات</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> ))</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ستاره ها به عنوان پدیده اجتماعی ممکن است از قدرت عمل سیاست مدارها بی بهره باشند اما بی شک بر زندگی فرد فرد ملت تاُثیر بیشتری می گذارند</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">به هر حال آنچه مسلم است ، ستارگان سینما چنان که گفتیم پدیده های مؤثر و بسیار جدی اجتماعی اند که نادیده انگاشتنشان ، کوچکترین خللی در تاُثیر گذاری و اهمیت شان وارد نمی کند پس بهتر است بپذیریم و پس از پذیرش در تحلیل و چرایی وجود این پدیده بکوشیم و این میسر نمی شود مگر با خواندن هر چه بیشتر درباره زندگی شخصی ، هنری و اجتماعی آنها</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">عزیزانی که خواهان اطلاعات در مورد زندگینامه هر یک از هنرپیشگان زیر می باشند ، در خدمت پاسخگویی به سؤالات شما عزیزان هستم</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ایزابل آجانی ، فرد آستر ، وودی آلن ، جرمی آیرونز ، لیو اولمان ، بی بی آندرسون ،&zwnj; هریت آندرسون ، سیلوستر استالون ، مریل استریپ ،&zwnj;شارون استون ، باربارا استنویک ، گری الدمن ، جیمز استیوارت ،&zwnj;اینوکنتی اسموکتونوفسکی ،&zwnj; الیویه لورنس ،&zwnj; امه آنوک ، پیتر اتول ، رایان اونیل ، کلینت ایستوود ، لورن باکال ،&zwnj; آن بنکرافت ، وارن بیتی ، مارلون براندو ، چارلز برانسن ، ریچارد برتن ، اینگرید برگمن ، یول برینر ، ژولیت بینوش ، ژان پل بلموندو ، همفری بو گارت ، آل پاچینو ،&zwnj;جک پالانس ، گرگوری پک ، شان پن ، سیدنی پوآتیه ، جان ترولتا ، یوما ترمن ، ژان لویی ترنتینیان ، اسپنسر تریسی ، چارلز چاپلین ، مایکل داگلاس ، کرک داگلاس ، جانی دپ ، ژرار دوپاردیو ،&zwnj; آلن دلون ، رابرت دنیرو ، کاترین دونوو ، رابرت دووال ، جیمز دین ، وینونا رایدر ، رابرت ردفورد ، سوزان ساراندن ، فرانک سیناترا ، آرنولد شوارتسنگر ، جودی فاستر ، جین فاندا ، هنری فاندا ، مورگان فریمن ، کوین کاستنر ، جیمز گاگنی ، تونی کرتیس ، تام کروز ، جین کلی ، مانتگمری کلیفت ، نیکلاس کیج ، نیکول کیدمن ، وال کیلمر ، گری کوپر ، آنتونی کویین ، ژان گابن ، اندی گارسیا ، ریچارد گر ، کری گرانت ، مل گیپسن ، کلارک گیبل ، الک گینس ، آلن لاد ، جک لمون ، برت لنکستر ، استن لورل و الیور هاردی ، سوفیا لورن ، جری لوییس ، ویکتور ماتیور ، دین مارتین ، لی ماروین ، جان مالکوویچ ، مارچلو ماسترویانی ، استیو مکویین ، جک نیکلسون ، پل نیومن ، دنزل واشینگتن ، کریستوفر واکن ، ژان کلود وان دام ، جان وین ، رابین ویلیامز ، بروس ویلیس ، آنتونی هاپکینز ، وودی هارلسن ، داستین هافمن ، جین هاکمن ، هالی هانتر ، ادری هپبرن ، کاترین هپبرن ، چارلتن هستن ، تام هنکس ، ریچارد هریس و دنیس هوپر</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> .</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>', 'off', 'fa', '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/15.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_15.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_15.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_15.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_15.jpg\"}', 'سینما , بازیگری , کارگردانی , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , تئاتر  ,  بازیگر تلویزیون', 'news', 3082, 0, '2020-03-31 16:15:40', '2024-07-15 09:11:22');
INSERT INTO `articles` (`id`, `title`, `slug`, `description`, `body`, `showonmenu`, `lang`, `images`, `tags`, `type`, `viewCount`, `commentCount`, `created_at`, `updated_at`) VALUES
(8, 'تاریخچه دوبله تلویزیون ایران', 'تاریخچه دوبله تلویزیون ایران', 'ما نمی‌توانیم در این باره اظهار نظر کنیم، اما از دو مطلب می‌توانیم مطمئن باشیم: اول این‌که تا زمان نمایش اولین فیلم ناطق (1927 م‌) سینما نوعی وسیله سرگرمی و شاید صنعتی فرض می‌شد و', '<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">ما نمی&zwnj;توانیم در این باره اظهار نظر کنیم، اما از دو مطلب می&zwnj;توانیم مطمئن باشیم: اول این&zwnj;که تا زمان نمایش اولین فیلم ناطق (1927 م&zwnj;) سینما نوعی وسیله سرگرمی و شاید صنعتی فرض می&zwnj;شد و دوم این&zwnj;که مخترعان سینما می&zwnj;دانستند که اختراع آنها ناقص است، هر چند اولین فیلمبرداری&zwnj;های برادران لومیر به آغاز عصر سینمای &laquo;صامت&raquo; منجر شد، اما نکته همین جاست: سینمای صامت سینمایی بود که از صدا ـ در شکل کنونی آن ـ بی&zwnj;بهره بود و تلاش&zwnj;های مخترعانی مثل ادیسون برای ساخت دستگاه ضبط صوت، نشان می&zwnj;دهد که آنها در پی رفع این نقیصه بودند. دستگاهی نیاز بود که بتوان صدای فیلم را روی آن ضبط کرد؛ البته همان موقع هم آدم&zwnj;های عاقلی بودند که می&zwnj;فهمیدند بین صدای سر صحنه و صدایی که در استودیو روی فیلم آمده، از لحاظ کیفیت تفاوت&zwnj;های روشنی هست. چنین بود که چاپلین تنها سکانس ناطق در فیلم &laquo;عصر جدید&raquo; را در استودیو صداگذاری کرد.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">مسأله این بود &zwnj;که یافتن راه حل مناسب برای صداگذاری قدری طول کشید. بعد از نمایش اولین فیلم ناطق، دوران جالبی آغاز شد. &laquo;بونوئل&raquo; تعریف می&zwnj;کند که چطور در هالیوود از هر فیلم، نسخه&zwnj;های متعددی به زبان انگلیسی، فرانسوی و اسپانیایی تولید می&zwnj;شد. برای این کار، فیلمنامه در اختیار بازیگرانی قرار می&zwnj;گرفت که انگلیسی، فرانسوی و اسپانیایی زبان بودند و هر گروه، فیلم را به زبان خود تولید می&zwnj;کرد (بونوئل / با آخرین نفس&zwnj;هایم) زیاد طول نکشید که تکنیک صداگذاری (دوبله) ابداع شد. از این طریق براحتی می&zwnj;شد گفتار مورد نظر را با کیفیت بهتر از صدای سر صحنه روی فیلم قرار داد.</span></span></span></span></p>\r\n\r\n<h2 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">صدا عقب ماند</span></span></strong></span></span></h2>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">در حاشیه باید اشاره کنم که ماجرای صدای فیلم به همین راحتی پیش نرفت. تا سال&zwnj;ها مشکل همزمانی صدا و فیلم، معضلی حل نشدنی به نظر می&zwnj;رسید. بعد از نمایش فیلم &laquo;دختر لر&raquo; در ایران، سینماگران مدت&zwnj;ها با این معضل دست و پنجه نرم می&zwnj;کردند.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">پرویز خطیبی در این باره دو خاطره بامزه دارد؛ یکی از آنها مربوط به فیلم &laquo;توفان زندگی&raquo; (علی دریابیگی / 1327 ش) است که مرحوم بنان، خالقی و حسین تهرانی نیز در آن شرکت داشتند. ظاهرا بعد از نمایش فیلم، استاد حسین خالقی سالن سینما را ترک نمی&zwnj;کرده و در برابر اصرار کنترلچی&zwnj;ها گفته که می&zwnj;خواهد صدای فیلم را هم بشنود و بعد برود! این گفته طنزآمیز ناظر به همان معضلی است که درباره&zwnj;اش می&zwnj;گفتم؛ دشواری پخش همزمان صدای فیلم. خاطره دوم مربوط به دوبله فیلمی از &laquo;لورل و هاردی&raquo; است.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">طبق روایت پرویز خطیبی، او و ابوالقاسم رضایی (فیلمساز مشهدی / 1375 ـ 1295 ش) فیلمی از لورل و هاردی را دوبله می&zwnj;کنند اما پخش آن با دردسر جدی روبه&zwnj;رو می&zwnj;شود چون نمی&zwnj;توانند صدا و تصویر را هماهنگ کنند. در نتیجه، خود رضایی کنار اپراتور می&zwnj;ایستد و هر بار که بازیگران حرف می&zwnj;زنند، دستگاه پخش صدا را روشن می&zwnj;کند و این کار شاق را تا آخر فیلم ادامه می&zwnj;دهد! (پرویز خطیبی / خاطراتی از هنرمندان) مرحوم فهیمه راستکار (1391 ـ 1311) از پیشکسوتان دوبله روایت مشابهی دارد: &laquo;در آن زمان، یک پرده فیلم باید در یک نوبت دوبله می&zwnj;شد. هیچ تکنیکی برای تکه&zwnj;تکه کردن فیلم وجود نداشت و ما باید بموقع جملات را ادا می&zwnj;کردیم که با موسیقی فیلم تداخل پیدا نکند.&raquo; (ایسنا / 9 آذر 91)</span></span></span></span></p>\r\n\r\n<h2 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">مدیریت دوبلاژ</span></span></strong></span></span></h2>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">حاشیه به متن رفت! بنا بود از تاریخچه دوبله در تلویزیون ایران بنویسم اما ایجاد، پرداخت و پرورش و ابداع دوبله در سینمای ایران صورت گرفته و نمی&zwnj;توان از این بخش براحتی گذشت، زیرا صنعت دوبله در کشور ما قدمتی شصت و چند ساله دارد. جالب اینجاست که در دهه 30 به دلیل محدودیت&zwnj;های فنی، دوبله فارسی تعدادی از فیلم&zwnj;های خارجی در کشورهایی مثل ایتالیا، ترکیه و آلمان صورت می&zwnj;گرفت اما بزودی با همت سرمایه&zwnj;گذاران و علاقه&zwnj;مندان به سینما ـ از جمله مرحوم کوشان ـ امکان دوبله در ایران فراهم شد. از همان ابتدا صداگذاری فیلم، جنبه&zwnj;ای جدی و هنری داشت. بهترین دوبلورهای آن زمان، فارغ&zwnj;التحصیلان تئاتر بودند.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">تئاتر، به دوبلور چند مهارت می&zwnj;بخشید. اول فن بیان. دوبلور باید می&zwnj;توانست شمرده، واضح، سلیس و روشن سخن بگوید. این کاری بود که هنرجویان تئاتر با آن آشنا می&zwnj;شدند. دوم لحن بیان که عبارت بود از فراز و فرود بموقع در سخن و نمایش احساسات مختلف. &laquo;پرویز بهرام&raquo; دوبلور باسابقه سینما و تلویزیون در پاسخ به این پرسش که آیا برای دوبله از او تست صدا گرفته&zwnj;اند یا خیر، چنین می&zwnj;گوید:</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">&laquo;نه، نه! من هیچ تستی ندادم. چون در کار تئاتر بودیم، صداها مشخص بود. یادم نمی&zwnj;رود در اولین فیلمی که در استودیوی ایران فیلم، به جای شخصیت صحبت کردم، هیچ امتحانی از من گرفته نشد. در واقع دوبلورهای اولیه به دلیل پیشینه تئاتر و به&zwnj;کارگیری فن بیان، صداهای شناخته شده&zwnj;ای داشتند.&raquo; (جام&zwnj;جم آنلاین / 14 اسفند 85)</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">چنین بود که حرفه&zwnj;ای به نام مدیر دوبلاژ پدید آمد. در کشور ما این شغل جایگاه خاصی پیدا کرد. دوبلورهای جوان تحت آزمایش&zwnj;ها و آموزش&zwnj;های سفت و سختی قرار می&zwnj;گرفتند. مرحوم راستکار چنین خاطره&zwnj;ای دارد: دو صفحه دیالوگ خود را در دو زمان کاری ـ یعنی هشت ساعت! ـ باید می&zwnj;گفتم. ابتدا چهار ساعت گفتم ولی حاصل کار خوب نشد.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">این در حالی بود که ایرج دوستدار و مهین معاون&zwnj;زاده در دو طرف من نشسته بودند و هر کدام فقط یک جمله دیالوگ داشتند و من خیلی خجالت کشیدم که دو دوبلور حرفه&zwnj;ای را در کنارم نشانده&zwnj;اند ولی من نمی&zwnj;توانستم دیالوگ&zwnj;ها را بگویم!&raquo; (همان منبع) در نظر داشته باشند که در آن زمان، استودیوی ضبط از ابتدایی&zwnj;ترین وسایل رفاهی مثل کولر یا بخاری محروم بود.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">دوبلورها مجبور بودند در سرمای زیر صفر یا گرمای بالای 38 درجه ساعت&zwnj;ها کار کنند! بالاخره نتیجه این شد که خانم راستکار باید به خانه می&zwnj;رفت و همه دیالوگ&zwnj;ها را حفظ می&zwnj;کرد و از بر می&zwnj;گفت! با این همه، خصلت&zwnj;های انسانی، سختکوشی و پیگیری مدیران دوبلاژ در پیش&zwnj;بردن آموزش&zwnj;ها و راه انداختن دوبلورهای جوان بسیار موثر بود. چنان که خانم راستکار می&zwnj;گوید: &laquo;رضایی خیلی سختگیر و لطیف&zwnj;پور (از پیشکسوتان دوبله در ایران و متولد 1311 ش) هم از کسانی بود که ابتدا گفتن یک جمله را به عهده گوینده می&zwnj;گذشت و بتدریج کار گوینده را افزایش می&zwnj;داد و به این ترتیب، گوینده هنگام دوبله نمی&zwnj;ترسید&raquo;. ( همان منبع)</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">این توضیح را در پرانتز فرض کنید، مجال این نوشته، اجازه نمی&zwnj;دهد و شاید بهتر باشد که فرصت مستقلی برای ادای حق مدیران دوبلاژ ایران اختصاص یابد. همین که یادی کنیم از مرحوم علی کسمایی (1391 ـ 1294 ش) که از او به عنوان پدر دوبله ایران یاد شده است. او در طول بیش از 30 سال فعالیت خود در عرصه دوبله و مدیریت دوبلاژ نه تنها شاگردان بسیار قابلی تربیت کرد، بلکه در کار خود به نوعی صاحب سبک به شمار می&zwnj;رفت. مرحوم کسمایی علاوه بر دوبله، فیلمنامه هم می&zwnj;نوشت و کارگردانی می&zwnj;کرد و جالب اینجاست که در اصل، دانش آموخته رشته حقوق بود و پروانه وکالت داشت.</span></span></span></span></p>\r\n\r\n<h2 dir=\"RTL\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">ظرافت&zwnj;های دوبله</span></span></strong></span></span></h2>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">از سال 1347 شمسی که تلویزیون ملی ایران راه افتاد، واحد دوبلاژ سیما نیز افتتاح شد. تاریخچه این واحد بسیار قابل توجه است. گروهی از بنام&zwnj;ترین دوبلورهای فارسی در طول 50 سال در این واحد فعال بوده&zwnj;اند و آثاری به یادماندنی خلق کرده&zwnj;اند. بله! خلق کرده&zwnj;اند! این فعل را از این جهت به کار می&zwnj;برم که کار صداگذاری در ایران از جهاتی با تمام مشاغل مشابه در جهان متفاوت است.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">مراحل دوبله به این صورت است که پس از انتخاب فیلم و ترجمه دقیق دیالوگ&zwnj;ها و تطبیق چندباره با متن، فیلم به مدیر دوبلاژ سپرده می&zwnj;شود و مدیر دوبلاژ روی متن کار می&zwnj;کند. مثلا در گویش انگلیسی عبارات و اصطلاحات زیادی است که برای مخاطب ایرانی نامفهوم است. مدیر دوبلاژ به جای این جملات با رعایت امانت معادل فارسی می&zwnj;نشاند.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">در مواردی از ضرب&zwnj;المثل&zwnj;های فارسی استفاده می&zwnj;کند و خلاصه با دیالوگ&zwnj;ها کاری می&zwnj;کند که مؤلف کلیله و دمنه با متن &laquo;پنجه تنتره&raquo; کرد! به عبارت بهتر آن را بازتولید می&zwnj;کند. نگارنده با تعداد زیادی از مدیران دوبلاژ آشناست و بارها از احاطه آنها به دقت&zwnj;های زبان فارسی شگفت&zwnj;زده شده است. در تاریخچه دوبله آثاری داریم که در رعایت امانت مثال زدنی هستند.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">فیلم &laquo;اشک&zwnj;ها و لبخندها&raquo; به کارگردانی رابرت وایز در سال 1345 با مدیریت مرحوم کسمایی دوبله شد. در جریان دوبله این فیلم تمام ترانه&zwnj;های فیلم (بجز آواز اول و آخر فیلم) به زبان فارسی بازنویسی و اجرا شد! این اتفاق درباره فیلم&zwnj;های &laquo;مری پاپینز&raquo; (رابرت استیونسون / 1964) و &laquo; بانوی زیبای من &raquo; (جورج کیوکر / 1964) نیز افتاده است.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">اکنون هنر دوبله به حیات خود ادامه می&zwnj;دهد هر چند ضبط مغناطیسی و دیجیتال، دشواری کار دوبلورها را از بین برده و جالب اینجاست که پیشکسوتان دوبله و قدیمی&zwnj;های این حرفه معتقدند کیفیت دوبله نیز کمی کاهش پیدا کرده است. اگر این گفته را بپذیریم علت آن جز غلبه کمیت بر کیفیت نیست، زیرا دوبلورهای ما باید همزمان به شبکه&zwnj;های متعدد تلویزیونی سرویس بدهند. به این مبحث باید در فرصتی کامل&zwnj;تر پرداخته شود.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"LTR\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#3a3a3d\">*&zwnj; </span></span></span><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#3a3a3d\">اولین دیالوگی که مرحوم راستکار در یک فیلم لهستانی ادا کرد</span></span><span dir=\"LTR\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#3a3a3d\">.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\">&nbsp;</p>\r\n\r\n<h2 dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:18px\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\"><span style=\"font-family:&quot;B Koodak&quot;\">گذری بر تاریخ دوبله در جهان</span></span></span></span></h2>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">دوبله به معنای آن است که دیالوگ&zwnj;های اصلی یک فیلم دوباره و در قالب</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">دیالوگ&zwnj;هایی به زبانی که دوبلورهای کشور واردکننده فیلم دوبله کرده&zwnj;اند، ضبط شوند آنهم در حالی که موسیقی اصلی فیلم و تاثیرات آوایی آن کماکان به جای خود باقی می&zwnj;ماند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">از لحاظ تاریخی دوبله نخستین تکنیکی نبود که برای انتقال زبان به کار گرفته شد بلکه در ابتدا نسخه اصلی فیلم خارجی با زبان اصلی ترجیح داده می&zwnj;شد و فیلم&zwnj;ها به همان روالی که بازیگران خارجی در آن صحبت می&zwnj;کردند پخش می&zwnj;شدند. بعدها فرآیند دوبله و زیرنویس کردن فیلم&zwnj;های خارجی باب شد و با فرآیند دوبله درک فیلم توسط مخاطبان کشور واردکننده آن آسان&zwnj;تر می&zwnj;شد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">گفته می&zwnj;شود دوبله در اوایل دهه ۱۹۳۰ و با هدف غلبه&zwnj;کردن بر مشکلی پدید آمد که بسیاری از بازارهای اروپایی با آن مواجه بودند و آن معضلات موجود بر سر راه تجاری&zwnj;سازی فیلم&zwnj;های صداداری بود که حاوی دیالوگ&zwnj; بودند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در پایان سال ۱۹۳۲ یعنی زمانی که فیلم&zwnj;های ناطق در حال شکستن بازارهای جهانی بودند، اروپا با معضلاتی چون تجربه کردن رژیم&zwnj;های خودکامه و فاشیست دست و پنجه نرم می&zwnj;کرد.</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">دولت&zwnj;های خودکانه آدولف هیتلر و بنیتو موسولینی از دیالوگ&zwnj;های موجود در فیلم&zwnj;ها وحشت داشتند چون چنین دیالوگ&zwnj;هایی ایده&zwnj;ها و ایدئولوژی&zwnj;هایی را مطرح می&zwnj;کردند که مخالف رهبران این حکومت&zwnj;ها بودند. این دیالوگ&zwnj;ها می&zwnj;توانستند در اختیار توده کم سواد مردم قرار گیرند و چنین موضوعی خطرناک بود.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\">&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">این امر مشکلاتی را برای صنعت فیلم آمریکا ایجاد کرد چون بسیاری از کشورهای اروپایی قوانینی علیه هر نوع مولفه خارجی وضع کرده بودند</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">بنابراین با وجود رژیم&zwnj;هایی مانند هیتلر در آلمان فاشیستی، دوبله تنها راه حل ممکن برای دور زدن این قوانین بود.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">با وجود این که دوبله فیلتری برای سانسور محسوب می&zwnj;شد ولی در مسیر خود بر مخاطبان تاثیر گذاشت و به سرعت به ابزاری سودمند برای گسترش فرهنگ&zwnj;های مختلف تبدیل شد به طوری که در کشوری مانند ایتالیا، دوبله تاثیر قابل توجهی بر گسترش زبان&zwnj; ایتالیایی استاندارد داشت.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">هنر دوبله ابزاری کارآمد در انتقال زبان و فرهنگ بود و غول&zwnj;های فیلمسازی در آمریکا به سرعت به پتانسیل بالای این ابزار انتقال فرهنگی پی بردند و شروع به بهره&zwnj;برداری از آن جهت صادرکردن فیلم&zwnj;هایشان به خارج از این کشور کردند.</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">آن&zwnj;ها همچنین از دوبله برای ممانعت کردن از ورود فیلم&zwnj;های وارداتی به ایالات متحده استفاده کردند. بنابراین موفقیت فوق العاده فیلم&zwnj;های آمریکایی در طول سال&zwnj;ها صرفا به دلیل سرمایه&zwnj;گذاری&zwnj;های پربازده در کیفیت و ویژگی&zwnj;های زیبایی&zwnj;شناختی آن&zwnj;ها نبود بلکه تا حد زیادی به دلیل بهره بردن سازندگانشان از استراتژی&zwnj;های دقیق بازاریابی و دوبله کردن تمامی فیلم&zwnj;های آمریکایی بود که به سرعت به بازارهای خارجی هم صادر می شدند. همچنین همان طور که ذکر شد ممانعت&zwnj;کردن هوشمندانه آمریکایی&zwnj;ها از دوبله&zwnj;شدن فیلم&zwnj;های وارداتی نقش مهمی را در این موفقیت ایفا می&zwnj;کرد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در دوران اولیه صدا و پیش از باب شدن زیرنویس و هنر دوبله، یک فیلم به چند زبان پخش می&zwnj;شد تا مخاطبان بین&zwnj;المللی را به سمت خود جلب کند. به طور مثال، پس از پخش&zwnj;شدن نسخه انگلیسی &laquo;دراکولا&raquo;</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در سال ۱۹۳۱، این فیلم با نسخه اسپانیایی به همراه کارگردانی متفاوت و هنرپیشه&zwnj;های اسپانیایی هم پخش شد اما این کار هزینه&zwnj;بر بود. در این میان، شرکت آمریکایی &laquo;پارامونت پیکچرز&raquo;</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در سال ۱۹۳۰ استودیویی عظیم را در حومه بخش</span></span><span dir=\"LTR\"><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">Joinville&nbsp;</span></span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در شهر پاریس تاسیس کرد تا فیلم&zwnj;های چندزبانه را در حجم انبوه تولید کند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">سایر استودیوهای عظیم آمریکایی نیز از این کار تبعیت</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">و شروع به تولید فیلم&zwnj;هایی حتی به پانزده زبان جداگانه کردند. با این حال در سال ۱۹۳۱ تکنیک دوبله تا حد زیادی کامل شده بود و جایگزین تولید چندزبانه فیلم شد. به دنبال آن، بخش</span></span>&nbsp;<span dir=\"LTR\"><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">Joinville</span></span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">هم به یک مرکز دوبله برای سراسر اروپا تبدیل شد. در ابتدای ظهور دوبله در اروپا، مخاطبان احساس می&zwnj;کردند هنرپیشه&zwnj;ها طبیعی نیستند و حرکات بدن آن&zwnj;ها متفاوت از صداهایشان است اما این مشکل فروکش کرد و دوبله به صنعتی ارزشمند در سراسر این قاره تبدیل شد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">از سال ۱۹۳۲ دنیا به کشورهایی تقسیم شد که طرفدار دوبله بودند یا این که از آن تنفر داشتند و زیرنویس فیلم&zwnj;ها را می&zwnj;پسندیدند و</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> این ترجیحات عمیقا ریشه در عادات ملی داشتند. در این میان ژاپن زیرنویس کردن فیلم را ترجیح می&zwnj;داد گرچه دوبله برای این کشور پرجمعیت از لحاظ اقتصادی به صرفه&zwnj;تر بود. از طرف دیگر در تعدادی از کشورها علاقه به دوبله تحت ملاحظات ملی&zwnj;گرایانه قرار داشت. زیرنویس کردن نشانه&zwnj;ای مداوم برای تماشاچی است که بین کشور تولیدکننده فیلم و کشور واردکننده آن گسست وجود دارد اما این نشانه در دوبله محو می&zwnj;شود.</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> دوبله پذیرش فرهنگی را آسان&zwnj;تر می&zwnj;کند و این امر همراه با اشاعه بین&zwnj;المللی فیلم&zwnj;ها صورت می&zwnj;گیرد زیرا فرهنگ نه تنها از طریق صدا بلکه از طریق تصاویر نیز منتقل می&zwnj;شود.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در ایران، پیش از سال ۱۹۳۱ (۱۳۱۰) منابع اصلی فیلم&zwnj;های بلند که در سینماهای کشورمان به نمایش گذاشته شدند، عموما فیلم&zwnj;های صامت بودند که از ایالات متحده، فرانسه، آلمان، روسیه و دیگر کشورها وارد می&zwnj;شدند. فیلم&zwnj;های صدادار فقط معدود سال&zwnj;هایی پس از اختراع آن&zwnj;ها در دنیای غرب یعنی اواخر سال ۱۹۳۰ وارد ایران شدند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در ابتدا سالن&zwnj;های سینما در ایران از یک مترجم استفاده می&zwnj;کردند که هنگام نمایش قصه را با صدای بلند برای تماشاگران تعریف می&zwnj;کرد</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">اما پس از آن رویه دیگری در پیش گرفته شد و پس از چند دقیقه&zwnj;ای که از نمایش فیلم می&zwnj;گذشت، نوشته&zwnj;ای روی پرده ظاهر می&zwnj;شد و خلاصه آن&zwnj; چه گذشته بود، در چند جمله به زبان فارسی گفته می&zwnj;شد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">پس از سال ۱۹۳۰، واردات تمامی فیلم&zwnj;ها به دلیل موقعیت اقتصادی عمومی جهانی (تا زمان جنگ جهانی دوم) کاهش یافت. با این حال، با کمبود فیلم&zwnj;های جدید، علاقه به سینما نزد ایرانیان کاهش نیافت و رفتن به سینما و تماشای فیلم در سراسر کشور به صورت باثباتی افزایش یافت. چنانچه موقعیت اقتصادی آن زمان اجازه وارد کردن فیلم&zwnj;های جدید را نمی&zwnj;داد، در عوض تعدادی از ایرانی&zwnj;ها را ترغیب کرد که بین سال&zwnj;های ۱۹۲۹ و ۱۹۳۷ علی رغم وجود امکانات ناکافی و محدودیت&zwnj;های اجتماعی، شروع به ساختن فیلم&zwnj;های بلند کنند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">با این حال، تعداد فیلم&zwnj;های تولیدشده ایرانی در این برهه زمانی به اندازه&zwnj;ای نبود که تغییر مهمی در واردات فیلم&zwnj;های خارجی ایجاد کند. در این جا مالکان سینماها مجبور بودند برای پر کردن برنامه&zwnj;هایشان به آن سوی مرزها چشم بدوزند.</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">بین سال&zwnj;های ۱۹۳۰ و ۱۹۴۰ فیلم&zwnj;های آمریکایی در ایران محبوبیتشان را حفظ کردند و فیلم های آلمانی وارداتی بیش از واردات فیلم&zwnj;های فرانسوی بودند چون به دست آوردن آن&zwnj;ها آسان&zwnj;تر بود.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">نخستین کسی که پیشگام دوبله در ایرانی شد، اسماعیل کوشان بود که در سال ۱۹۴۶ (۱۳۲۵) فیلم فرانسوی &laquo;نخستین وعده دیدار&raquo;</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">ساخته هانری دو کوئن را در استودیویی در ترکیه به زبان فارسی دوبله کرد و نام آن را برای پخش در ایران &laquo;دختر فراری&raquo;</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">گذاشت. در آن سال&zwnj;ها هنر دوبله در ترکیه متداول بود و این کشور استودیوهای دوبله تخصصی و مجهزی در اختیار داشت. همچنین به دلیل محدودیت&zwnj;های فنی دوبله فارسی تعدادی از فیلم&zwnj;های خارجی در کشورهای ایتالیا، ترکیه و آلمان انجام می&zwnj;شد. در ایران، دوبله&zwnj;کردن فیلم&zwnj;های خارجی تا حد زیادی کاتالیزوری برای ارتقای کیفیت هنری و تکنیکی تولید فیلم و در نتیجه انتظارات مخاطبان ایرانی بود.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در سال ۱۹۴۶ تعدادی از صاحبان فن و سرمایه&zwnj;گذاران نخستین استودیوی دوبله در تهران به نام &laquo;ایران نو فیلم&raquo;</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">را تاسیس کردند. در ابتدا این استودیو به مشکلات مدیریتی و فنی زیادی دست به گریبان بود اما در سال ۱۹۴۷ به عنوان یک استودیوی تولیدی و دوبله سازمان دهی شده مطرح شد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">موفقیت کلی فیلم&zwnj;</span></span><span dir=\"LTR\" lang=\"AR-SA\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">&lrm;</span></span></span><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">های دوبله&zwnj;شدن آغازین در ایران موجب شد استودیوهای دوبله جدیدی از علاقه عموم به فیلم&zwnj;های خارجی دوبله&zwnj;شده به زبان فارسی نهایت استفاده را ببرند. در نتیجه، استودیوی آریافیلم در سال ۱۹۴۹، استودیوی دیانافیلم در سال ۱۹۵۰، استودیوی عصر طلایی در سال ۱۹۵۱، استودیوی ایران فیلم در سال ۱۹۵۳، استودیوی داریوش فیلم در سال ۱۹۵۳ و استودیوی سانترال در سال ۱۹۵۴ تاسیس شدند. در این بین، استودیوی سانترال تحت مدیریت سیف الله کامرانی به یک مکان کلیدی برای دوبله فیلم تبدیل شد و این استودیو نیروی کاری ماهری را گرد هم آورد و فیلم&zwnj;هایی مانند &laquo;رویای شیرین&raquo;</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">ساخت رنه کلر فرانسوی را به فارسی دوبله کرد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">به جز یک استثنا تمامی این استودیوها در تهران بودند و این استثنا استودیوی داریوش فیلم بود که در ایتالیا مستقر بود و رئیس آن یک ایرانی ارمنی به نام آلکس آقابابیان بود که در دوبله فیلم&zwnj;های ایتالیایی به فارسی متخصص بود. در آن زمان دوبله در ایتالیا صنعت پیشرفته&zwnj;ای به شمار می&zwnj;آمد و آقابابیان در نخستین تلاشش در دوبله فیلم &laquo;سرگذشت فریدون بینوا&raquo;، نه تنها دیالوگ&zwnj;های ایتالیایی را به فارسی ترجمه کرد بلکه به اسامی شخصیت&zwnj;ها مانند شخصیت عنوان فیلم رنگ و بوی ایرانی داد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">پخش این فیلم در اکتبر سال ۱۹۵۲ در سینما دیانای تهران آغاز شد و اکرانی بسیار موفق داشت. استودیوی داریوش فیلم و دیگر استودیوهای ایتالیایی جبهه دومی برای دوبله فیلم خلق کردند و نه تنها فیلم&zwnj;های ایتالیایی بلکه فیلم&zwnj;های خارجی دیگری را نیز دوبله کردند و در این زمینه از استعدادهای ایرانی بهره می&zwnj;بردند. این جبهه ایتالیایی با جبهه دوبله فیلم در تهران رقابت می&zwnj;کرد و اواخر دهه ۱۹۵۰ و اوایل دهه ۱۹۶۰ از احترام خاصی نزد اهالی سینما و مخاطبان برخوردار بود. طی سال&zwnj;های بعد، افراد دیگری تلاش کردند فیلم&zwnj;های منطقه&zwnj;ای محبوب در ایران را دوبله کنند و فیلم&zwnj;های هندی در هند و فیلم&zwnj;های عربی در مصر دوبله شدند که این تلاش&zwnj;ها توفیق چندانی حاصل نکردند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">از سال ۱۹۶۸ (۱۳۴۷) که تلویزیون ملی ایران راه&zwnj;اندازی شد، واحد دوبله سیما نیز افتتاح شد. معمولا توزیع&zwnj;کنندگان فیلم&zwnj;ها</span></span>&nbsp;<span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> درباره دوبله آن&zwnj;ها با استودیوهای متخصص در امر دوبله قراردادی می&zwnj;بندند و یک مدیر دوبلاژ متن دوبله را به همراه یک مترجم آماده می&zwnj;کند و در یک استودیوی مجهز بر فرآیند کار دوبله فیلم نظارت می&zwnj;کند. در این جا چالش اصلی تهیه کردن متن دوبله و ضبط کردن دیالوگ&zwnj;ها به شیوه&zwnj;ای است که بازی بصری و محتوای دیالوگ&zwnj;های اصلی به زبان مقصد منتقل شود. فرآیند دوبله می&zwnj;تواند مدت طولانی زمان ببرد اما به طور متوسط، دوبله&zwnj;کردن یک فیلم بلند ۹۰ دقیقه&zwnj;ای، یک هفته زمان می&zwnj;&zwj; برد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">دوبله در ایران سابقه&zwnj;ای طولانی دارد و این صنعت با دوبله کردن فیلم&zwnj;ها و سریال&zwnj;های خارجی به زبان فارسی شروع شد اما در حال حاضر بسیاری از فیلم&zwnj;ها و سریال&zwnj;های ایرانی برای جذب مخاطبان بین&zwnj;المللی به زبان&zwnj;های خارجی دوبله می&zwnj;شوند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">منابع:</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">Movie History: A Survey: Second Edition</span></span></em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">&nbsp;By Douglas Gomery, Clara Pafort-Overduin</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">The History of Film&nbsp;</span></span></em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">edited by Nicholas Croce</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">The Oxford History of World Cinema</span></span></em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">&nbsp;By Geoffrey Nowell-Smith</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">A Social History of Iranian Cinema, Volume </span></span></em><em><span dir=\"RTL\" lang=\"FA\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۲</span></span></span></em><em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">: The Industrializing Years</span></span></em><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">&nbsp;By Hamid Naficy</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-family:&quot;Vazir&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">http://www.lib.washington.edu/neareast/cinemaofiran/intro.htm</span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>', 'off', 'fa', '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/11.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_11.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_11.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_11.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_11.jpg\"}', 'دوبله, تلویزیون،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'news', 1556, 0, '2020-03-31 16:19:51', '2024-07-15 09:11:22'),
(12, 'تاریخچه انیمیشن جهان', 'تاریخچه انیمیشن جهان', 'نخستین فردی که در خلق شخصیت های کارتونی موفقیت یافت، وینزور مک کی آمریکایی بود. وی، ابتدا شخصیت جذاب پسربچه ای به نام نیمو کوچولو', '<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">واژه انیمیشن از زبان لاتین و به معنای حرکت درآوردن تصویر است که پویانمایی واژه مصوب فرهنگستان زبان و ادب فارسی به جای</span></span></span><span style=\"font-size:15.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> Animation </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در انگلیسی عنوان می شود. در اواخر قرن بیستم، انیمیشن از قلمرو مرموز جادو و شگفتی بیرون آمد و به نوعی تفریح و سرگرمی بدل شد که تماشاگران آن را با طیب خاطر پذیرا شدند. فیلم های انیمیشن، حرکت جسمانی و اندامی را به گونه ای تصویری، بازسازی می کنند و برخلاف فیلم زنده از انعطاف بیشتری برای کاربرد خلاقانه حرکت برخوردار است به عبارت واضح تر، انیمیشن با کند و تند کردن حرکات یا کاربرد طنزآمیز یا بیانگرایانه آن، می تواند توانایی های جسمانی را جلوه ای اغراق آمیز ببخشد، هنر انیمیشن نیز از همین جا آغاز می شود. تلویزیون، عرصه ای جهانی را برای این نوع از فیلم کارتون فراهم آورد و چنین می نماید که تقاضا برای آن هیچ گاه فروکش نخواهد کرد، اما نمونه های متعددی نیز از امکانات بالقوه انیمیشن برای انتقال اندیشه های بصری طنزآلود و روایت های جدی و پیچیده به تماشاگران بزرگسال یافت می شود. برخی از فیلم های انیمیشن نه فقط از جاذبه آنی برخوردارند، بلکه فراموش نشدنی و حاوی تعابیر جالب اجتماعی هم هستند</span></span></span><span style=\"font-size:15.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">نخستین فردی که در خلق شخصیت های کارتونی موفقیت یافت، وینزور مک کی آمریکایی بود. وی، ابتدا شخصیت جذاب پسربچه ای به نام نیمو کوچولو و سپس گرتی دایناسور را ارایه داد. نیمو کوچولو در عالم خواب ابتدا در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۹۰۷</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> میلادی به صورت داستان مصور در روزنامه نیویورک هرالد چاپ شد. وینزورمک کی در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۹۱۱</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> میلادی تصمیم گرفت، نقاشی های نیموکوچولو و دوستانش را به صورت انیمیشن درآورد که موفقیت زیادی هم کسب کرد. در همین دوران، پیشرفت هایی مجزا در انیمیشن آمریکا و اروپا پدید آمد. در آمریکا درپی موفقیت های جیمز استوارت بلکتون و وینزور مک کی در هر گوشه و کنار استودیوهای انیمیشن و نیز دست اندرکاران این حرفه و شخصیت های کارتونی آنها سربرآورد. پس از گذشت </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۰۰</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> سال، امروزه صنعت انیمیشن در سراسر جهان گسترش یافته و شمار بسیاری از علاقه مندان از کودکان گرفته تا بزرگسالان را به سمت خود متمایل کرده است. شرکت های بزرگ با تولید آثار پرهزینه سینمایی و تلویزیونی از سینمای زنده پیشی گرفته اند و همواره به تسخیر گیشه ها و موفقیت های روزافزون در این عرصه می اندیشند. حضور پررنگ سینمایی انیمیشن و جلوه های ویژه در آثار زنده سینمایی نیز بر اهمیت آن می افزاید</span></span></span><span style=\"font-size:15.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><a href=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769595.jpg\" title=\"&quot;\r\n   وینزور مک کی طراح کارتون و پویانمای آمریکایی و از چهره های اولیه و تاثیرگذار در انیمیشن\r\n  &quot; \"><strong><span style=\"font-size:15.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#860f10\"><img alt=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769595.jpg\" id=\"Picture_x0020_7\" src=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769595.jpg\" style=\"height:120px; width:200px\" title=\"&quot;   وینزور مک کی طراح کارتون و پویانمای آمریکایی و از چهره های اولیه و تاثیرگذار در انیمیشن  &quot;\" />&nbsp;</span></span></span></strong></a></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">وینزور مک کی طراح کارتون و پویانمای آمریکایی و از چهره های اولیه و تاثیرگذار در انیمیشن</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">فیلم کارتونی</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">داستان اسباب&zwnj;بازی</span></span></span></strong>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">به عنوان نخستین فیلم تمام ساخته شده با جلوه&zwnj;های تصویری رایانه&zwnj;ای نخستین بار در آمریکا و سپس در سراسر جهان به اکران درآمد. این فیلم از نگاه انجمن فیلم آمریکا، یکی از </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۰</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> فیلم انیمیشن برتر جهان است و در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۹۹۶</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> میلادی نامزد دریافت هفت جایزه و برنده </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۹</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> جایزه در زمینه&zwnj;های مختلف از جمله بهترین موسیقی و فیلمنامه شد. داستان اسباب بازی در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۲۲</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> نوامبر </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۹۹۵</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> میلادی به نمایش در آمد اما در اکتبر </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۲۰۰۹</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> میلادی پس از </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۴</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> سال به همراه قسمت دوم این انیمیشن با عنوان داستان اسباب بازی </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۲</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> روی سیستم جدید سه بعدی رفت</span></span></span><span style=\"font-size:15.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/10.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_10.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_10.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_10.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/600_10.jpg\"}', 'انیمیشن،موشن گرافیک،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'news', 2003, 0, '2020-05-18 23:49:05', '2024-07-15 09:11:21');
INSERT INTO `articles` (`id`, `title`, `slug`, `description`, `body`, `showonmenu`, `lang`, `images`, `tags`, `type`, `viewCount`, `commentCount`, `created_at`, `updated_at`) VALUES
(13, 'تاریخچه کارگردانی', 'تاریخچه کارگردانی', 'در مورد انواع کارگردان می­توان به دو نوع تقسیم بندی اشاره کرد. یک تقسیم بندی دربرخورد کارگردان با متن شکل می­گیرد و دیگری در برخورد کارگردان باگروه اجرایی.\r\nدر چگونگی برخورد با متن می­توان به سه گروه کارگردان اشاره کرد:\r\nگروه اول کارگردانانی هستند که سعی می­کنند متن را همانجور که هست و نوشته شده موبه مو بفهمند و به اجرا درآورند.', '<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">دوران یونان باستان یعنی دوران سوفوکل، اشیل و </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">&hellip;</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> عصر حاکمیت نویسنده بر نمایش بود. بازیگران از ماسک استفاده می&shy;کردند و ماسکها نیز در تراژدیها و کمدیها ثابت بود. لذا میمیک وجود نداشت. همچنین به خاطر استفاده از کفشهای چوبی خاص در صحنه خیلی نمی&shy;توانستند جابجایی داشته باشند زیرا حرکت کردن با کفشها مشکل بود. اصولاً در این دوره سنت بر نمایش حاکم بود. در متون از اسطوره&shy;های ثابتی استفاده می&shy;کردند. زمان تمرین برای هر نمایش سه روز بود. در این دوره شخصی تحت عنوان کارگردان وجود نداشت و بانی نمایش خود نویسنده بود</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در روم نیز، وضعیت مشابه یونان بود. فقط شکل صحنه نعل اسبی شده و سن شکل نیم دایره به خود گرفته بود. تنها دکوری که در این نمایشها استفاده می&shy;شد &laquo;پریاکتوا&raquo; نام داشت که دکور گردان بود و ابداع آن را نیز به اشیل نسبت می&shy;دهند. این دکورها به تناسب هر فضا حول محوری می&shy;چرخید. در روم به تدریج نمایشها از مسائل اخلاقی تهی شدند و تا آنجا پیش رفتند که مسیحیت نمایش را تعطیل کرد</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">البته این هنر معترض هیچگاه از بین نرفت و تنها از شکلی به شکل دیگر درآمد. در این عصر گروهها بیرون از سالنها به فعالیت پرداختند و به شکل گروه&shy;های دوره گرد درآمدند. از طرفی به دلیل سانسور شدید نظام کلیسایی و از سویی ارتباط با آدمها و فرهنگهای مختلف، نمایشهای میم باب شد که به همان شکل سیار ادامه یافت. در این زمان کلیسا به تدریج تحت تأثیر عواملی، تأثیرش کاهش یافت کشیشها برای جلب دوباره مردم به بیرون از کلیسا رفتند تا مردم را به داخل دعوت کنند ولی باز هم بعد از مدتی اینکار نیز موثر واقع نشد. بانیان کلیسا به فکر راهی جدید افتادند. آنها نقاشان معتبر عصر خود را دعوت کردند تا صحنه&shy;هایی در کلیسا نقاشی کنند آنان تمام دیوارها و حتی سقف کلیسا را نقاشی کردند. که اینکار جاذبۀ فراوانی ایجاد کرد ولی برای مدتی کوتاه. به ناچار کشیشها به کنار پرده&shy;ها رفتند تا آنها را برای مردم تشریح کنند. بعد از مدت زمانی کشیش دیگری نیز اضافه شد و بصورت دو نفره به تشریح پرده&shy;ها پرداختند. به مرور بین آندو کلام درگرفت و مکالمه شکل گرفت ودر نتیجه دیالوگ پدید آمد که با استقبال فراوانی از طرف مردم روبرو شد. بعد از مدتی موضوعات و مضامین اجتماعی و اخلاقی جای موضوعات پرده&shy;ها را گرفت و کم کم این نمایشها به بیرون از صحن کلیسا رفتند ولی با این تفاوت که به جای پرده&shy;ها دکورهایی باب شد، که پشت سرهم قرار می&shy;گرفتند و بازیگران در مقابل آنها مدام جایشان را عوض می&shy;کردند. تداوم این جریان و جریانات نمایشهای دوره گردی منجر به تلفیق نمایشهای مذهبی و دوره گردی شد. در این زمان پرده&shy;ها را بر روی گاریهایی قرار دادند نمایشهای صنفی را پدید آوردند. که در این نمایشها تماشاگران ثابت بودند و گاریها حرکت می&shy;کردند. اما بتدریج این نمایشها شکل غیر مذهبی به خود گرفت. در این دوره نیز سنت حاکم بود. دکورها ثابت بودند و فقط یک بانی وجود داشت که او را اوستای نمایش می&shy;خواندند مانند معین&shy;البکاء در تعزیه. دوران قرون وسطی، عصر حاکمیت بازیگر بود. بعد از قرون وسطی، عصر نئوکلاسیک فرا رسید. قواعد سه گانه ( وحدت زمان، مکان و موضوع ) در این عصر حاکم شد. دوباره نویسندگان به قدرت رسیدند و نهایتاً به تئاتر دوران شکسپیر ختم شد. در این دوره هنوز تمرینها سه روز بود. و بازیگران به تناسب خود نقشی مشخص را ایفا می&shy;کردند و حرکات و بازیگری بسیار اغراق آمیز بود. که این شیوۀ اغراق آمیز تا قرن </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۸</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> ادامه پیدا کرد. از عصر شکسپیر بتدریج دوباره بازیگر حاکمیت پیدا کرد و گروههای بازیگری توسط مدیران بازیگران تشکیل شد. حفظ کردن متن باب نبود و سوفلور موجودیت مستقل داشت. به خاطر سوفلوری و شنیدن دیالوگها، میزانسن نمی&shy;توانست نقشی داشته باشد. دیگر اینکه در این دوره از نور طبیعی استفاده می&shy;شد که بعدها شمع و مشعلهای نفتی و گازی نیز وارد صحنه گردید. به دلیل این شکل نور پردازی قاعده میزانسن کمانی وجود داشت یعنی شخصیت محوری در هر موضعی می&shy;ایستاد بقیۀ اشخاص به طور نیم دایره اطراف او قرار می&shy;گرفتند. به طوری که هم در نور قرار گیرند و دیده شوند. در ضمن بازیگران هر یک دارای تخصصی بودند و نیاز به راهنمایی و هدایت نداشتند. این شکل از اجراها بسیار تصنعی بودند و هیچگونه باور پذیری در آنها دیده نمی&shy;شد و لذا برای مردم بسیار خسته کننده شده بود</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در همین دوران یکی از مدیران گروه بازیگران به نام دوک ساکس ماینینگن به همراه خانمش الن فرانس، که بازیگری بسیار خوش ذوق بود کمپانی بازیگری به وجود آورد. او به مدت طولانی به کمک همسرش و کمدین معروف لودویک کرونگ نمایشهایی به صحنه بردند</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">این دوره مصادف با صدور نظریه ناتورالیسم امیل زولا بود. ماینینگن تحت تأثیر این نطریه قواعد نمایش قبل از خود را به هم ریخت. برهمین اساس او را پدر کارگردانی دنیا می&shy;گویند. ماینینگن در سال </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">۱۸۷۰</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> در جهت شکل گیری فرضیاتش دکورهای سه بعدی را جایگزین دکورهای دوبعدی کرد. کف و سقف را به تناسب با هر نمایش تغییر می&shy;داد. به مطالعۀ تاریخی و جغرافیایی پرداخت و در طراحی لباس و صحنه نمایشها به بعد تاریخی و جغرافیایی آنها توجه بسیار کرد. از بازیگران متناسب با هر نقش استفاده می&shy;کرد و هر بازیگر باید نقشهای متفاوت را درهر کار ایفا می&shy;کرد. او قاعدۀ سه روز تمرین را به یک هفته افزایش داد. و باز تا زمانی که اجرا به حد قابل قبول خودش نمی&shy;رسید به گروه اجازۀ اجرا نمی&shy;داد. قاعدۀ میزانسن را تغییر داد. حتی بازیگران جاهایی از نمایش پشت به تماشاگر دیالوگ می&shy;گفتند. استانیسلاوسکی نیز تحت تأثیر او قرار گرفت و ایدۀ سیستم را از او گرفت. این دوره، دورۀ حاکمیت کارگردان است</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.<br />\r\n&laquo; </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در این دوران کارگردانان ناتورالیزم مقابل او رشد یافتند که نمونۀ ویژۀ ایشان اتوبرام</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">( O- Brahm ) </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">بود که البته خلاقیت کارگردانی نداشتند بلکه به عنوان کارگردانان منتقد باقی ماندند. آنچه ماینینگن با شور و هیجان تاریخی ایجاد کرده بود. آنان علیه&shy;اش به مبارزه برخاستند و به کنار نهادند. بر اساس قاعده و قانون قدیمی تزو آنتی تز </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">&ndash;</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> اکسپرسیونیزم به دنبال ناتورالیسم سررسید سبکی که بیان هنر ناب بود و به هر حال طبیعت را در قالب عناصر خویش به عنوان ابزار این بیان به کار بست. اما میان ناتورالیسم و اکسپرسیونیزم دورانی از جستجوی مردد قرار داشت و در همین دوران بود که یکی از بزرگترین کارگردانان به نام ماکس راینهارت ظهور کرد. بدنبال او لئوپولدیسنر کارگردانان دیگری آمدند که البته این دوران با نام برشت بهتر و کاملتر شناخته می&shy;شود</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> &raquo;</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">و تا به امروز سلطۀ کارگردانی بر تئاتر را شاهد بوده&shy;ایم و همچنین ظهور کارگردانان قدرتمند و خلاقی را که در عرصۀ تئاتر به فعالیت پرداختند و با خلاقیت و تفکر خود روزبروز جایگاه کارگردان را محکمتر و استوارتر نموده&shy;اند</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span></span></span></span></p>\r\n\r\n<h2 dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"color:#e67e22\"><strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">کارگردان و کارگردانی</span></span></strong></span></span></span></span></h2>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">کارگردان در حقیقت پل ارتباطی میان نویسنده و تماشاگر است. او نمایش را با بازیگر، طراحان، و از آنجا با تماشاگر مرتبط می&shy;سازد. کارگردان مسئول اصلی فرآیند تولید کار تئاتری و هماهنگ کننده عناصرتئاتر، و فکر اصلی از آن او است. کارگردان به تعبییری همچون یک باغبان است. او یک راهنماست و نه یک عروسک گردان</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">کارگردان ترکیب کننده است و از مجموع عناصر نمایشی تابلویی، جاندار می&shy;سازد. او کارش پویا کردن ایستاها و ماندگار کردن تجربیات گریزان است</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">و امروزه &laquo; کارگردان این عصر علمی به معنای دیگری دانش پژوه می&shy;باشد. او هر اثر را با نیروی علمی اندیشه در راستای سبک و شیوه خویش می&shy;آزماید و برهمین اساس نیز اقدام می&shy;کند. وی به همین جهت اهمیت یافته است. زیرا کارگردان دوره&shy;های پیشین می&shy;توانست مثل هر هنرمندی اصولی را رعایت کند ولی امروز دیگر چنین نیست. و او می&shy;بایست به خود و دانش و استعداد&shy;هایش تکیه کند. او البته می&shy;تواند از عناصر دوره&shy;های گذشته نیز بهره گیرد و از عناصرماینینگری، دیدگاه&shy;های ناتورالیستی، قدرت بیانی اکسپر سیونیستی، اما همة اینها را با مهارتهای خاص ترکیب ساخته یا دگرگون نموده و به صحنه آورد</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">امروزه دیگر کارگردانان کمتر وفادار به یک سبک می&shy;مانند و سعی&shy;شان بر این است که از ترکیب سبکهای مختلف و آمیزش آنها به یک شکل منطقی و درست اجرایی دست یابند. یعنی از سبکهای مختلف هنری عناصر و المانهایی را وام می&shy;گیرند . آنها را با هم ترکیب می&shy;کنند البته با استفاده از دانش و قدرت و خلاقیت خود و به اجراهایی قابل قبول و درخشان دست می&shy;یابند افرادی مانند پیتربروک، آریان منوشکین و </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">&hellip;</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> از این دست کارگردانان هستند. البته کارگردانهای بسیار بسیار آماتور و بی&shy;تجربه و بی&shy;سواد نیز به این شیوه کارگردانی می&shy;کنند. کارشان مخلوطی است از شیوه&shy;ها و سبکهای متفاوت اما بصورت نا آگاهانه و نادرست</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.<br />\r\n&ldquo;</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">کارگردان تحلیل و تعبیری کلی و عمومی از متن به عمل می&shy;آورد، سپس نمایشنامه را به لحظه&shy;ها و برهه&shy;های نمایشی تقطیع کرده و آنها را یکایک به طرف آن تحلیل و تعبیر کلی و عمومی تنظیم و تنسیق می&shy;نماید. کارگردان به تحلیلهای کلی و مقطعی خود واقعیتی بیرونی و عینی اعطاء می&shy;کند. به نوعی او حد فاصل میان نمایشنامه نویس و تماشاگر بوده و معبر و مفسر و مدافع و مکمل نمایشنامه نویس به شمار می&shy;رود</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.&rdquo;</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"color:#e67e22\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">و اما کارگردانی</span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">:</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">پییر امه توشار</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">pierr-Aime touchard </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">می&shy;گوید کارگردانی یعنی سامان دهندگی به تمام عناصری که به کمک اجرای اثر نوشته&shy;شده می&shy;آید. و آندره ونستن</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Andre veinstein </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">می&shy;گوید به طور کلی کارگردانی عبارتست از درک نمایش بر مبنای اثر نوشته&shy;شده و سازماندهی آن پس از انتخاب هنرپیشگان و هدایت آنها در آموختن نقشهایشان، انتخاب و دستور دوخت لباسها، یا واداشتن به ساخت موسیقی برای صحنه</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">البته تبدیل متن انتخاب شده به نمایش مستلزم روند پیچیده&shy;ای است که ژاک کوپو آن را به گذاری &laquo; از یک زندگی روحانی و پنهان، زندگی نوشته شده در متن، به یک زندگی جسمانی و پیدا به زندگی روی صحنه &raquo; تعبیر کرده است. که این گذار به عهدة کارگردان است که نقشی کاملاً شبیه به نقش رهبر ارکستر دارد که به اتفاق نوازندگان به نتهای موسیقی جان می&shy;بخشند، نتهایی که بدون او، حروف مرده&shy;ای بیش نیستند</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">کارگردان باید مانند رهبر ارکستر همه چیز را در نظر داشته باشد تا بتواند نتها را زنده کند و برای اینکار لازم است که تجزیه و تحلیل کند، حرکات بازیگران را تنظیم کند و به درست&shy;ترین و بهترین شکل آنها را روی صحنه بچیند. مشخص کند که هر یک از آنها در چه زمانی و به چه سمتی و مهمتر از همه اینکه با چه انگیزه&shy;ای حرکت کند. از بازیگران مقابل فاصله گیرد و یا به آنها نزدیک شود و باز چرا؟ که در اینجا به نظام نشانه&shy;های حفظ فاصله نیاز است تا به ترکیبی درست و منطقی بتواند دست یابد. تک تک صحنه&shy;ها و لحظه&shy;ها و تصویر&shy;ها باید توسط او نقطه&shy;گذاری و تاٌکیدگذاری شود تا بتواند فکر تماشاگر را نظم دهد و او را با خود همراه کند. در مورد ورود و خروجها نهایت دقت لازم است چرا که نقاط مهم یک نمایش می&shy;باشند و در تغییر فضا و ریتم نمایش بسیار موثر هستند. تمام این عناصر و عوامل و بسیاری فعالیتهای دیگرست که متن را تبدیل به اثری زنده می&shy;کند. تصمیم&shy;گیری دربارةچگونگی این اثر به عهدة کارگردان می&shy;باشد. اوست که باید آهنگ بیان و ضرباهنگ گفتار را تنظیم کند و مشخص سازد که در چه لحظاتی گفتگو باید فشرده باشد، به سرعت بیان شود و یا بر عکس از سرعت آن کاسته شود. سکوتها باید در جای خود قرار گیرد و ضرباهنگ هر قطعه به دقت تعیین گردد و از همه مهمتر آنکه ریتم مناسبی برای کل اثر انتخاب شود. نمایشنامه&shy;ای که در مسیر حرکت خود قرار نگیرد و راه را به خطا رود هیچگاه در اجرا موفق نخواهد شد</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در نهایت می&shy;توان گفت که کارگردانی تبدیل اثر نوشته شده به اجرا می&shy;باشد. یعنی تبدیل ذهنیت است به عینیت. تبدیل شکل نوشتاری است به شکل بصری. و در نهایت تبدیل روانشناسی است به رفتار</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">بسیاری گمان می&shy;کنند که اگر متن را به بازیگران بدهند و آنها را روی صحنه بچیند و بازیگران متن را برای تماشاگران بخوانند اثر اجرا شده است در صورتی که چنین نیست</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">کارگردانی اجرایی کردن متن است.تبدیل متن به حرکت و رفتارمندی است. یعنی متن باید کاملاً به عناصر دیداری و شنیداری تبدیل شود چرا که تماشاگر در حین دیدن نمایش فقط می&shy;تواند از این دوحس استفاده کند. ابزار تماشاگر برای برقراری ارتباط بینایی و شنوایی است پس کارگردانان هیچگاه نباید این نکته را در کارگردانی اثری فراموش کنند چرا که فراموش کردن این نکته فراموش کردن مخاطب است</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"color:#e67e22\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">انواع کارگردان</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در مورد انواع کارگردان می&shy;توان به دو نوع تقسیم بندی اشاره کرد. یک تقسیم بندی دربرخورد کارگردان با متن شکل می&shy;گیرد و دیگری در برخورد کارگردان باگروه اجرایی</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در چگونگی برخورد با متن می&shy;توان به سه گروه کارگردان اشاره کرد</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">:</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">گروه اول کارگردانانی هستند که سعی می&shy;کنند متن را همانجور که هست و نوشته شده موبه مو بفهمند و به اجرا درآورند. که اینان کمتر از خلاقیت خود و بیشتر از تکنیک خود استفاده می-کنند. و از این گروه عده&shy;ای نسبتاً موفق می-شوند و عده&shy;ای نیز مقلد</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">گروه دوم معتقدند که کارگردان و کارگردانی مکمل نویسنده و نمایشنامه است به نوعی کارکردان راکامل کنندة متن می&shy;دانند و بر این عقیده&shy;اند که همانقدر که نویسنده در تغییر متن حق دارد کارگردان نیز دارای این حق است چرا که او نویسندة دوم متن به شمار می&shy;رود. این گروه به دو شیوه عمل می&shy;کنند یا به ساختار و موجودیت متن وفادار می&shy;مانند و از عناصر و ابزار خود استفاده می&shy;کنند تا به اجرایی دلخواه دست یابند یا معتقدند متن هم دارای حسن و هم دارای نقص می&shy;باشد که باید بر طرف شود. لذا این گروه علاوه بر استفاده از ابزار کارگردانی، اجزای متن را نیز تغییر میدهند اما باز جوهره و موجودیت متن را حفظ می&shy;کند</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">گروه سوم معتقدند هرچه روی صحنه اتفاق می&shy;افتد از آن کارگردان است.</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> این دسته بر این عقیده&shy;اند که متن فقط مادة خامی است و استقلالی برای آن نمی&shy;شناسند. این گروه حتی به جوهر متن نیز وفادار نمی&shy;مانند و گاهی دیده می&shy;شود که متنی را ضد خودش اجرا می&shy;کنند. ( باید گفت این گروه بهتر آنست که خود بنویسد و به متون دیگران</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> ضربه نزنند</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"> ).</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">تقسیم بندی دیگر در برخورد کارگردانان با گروه اجرایی شکل می&shy;گیرد</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">:</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">دسته اول کارگردانان دیکتاتور هستند. در نظر اینان همه چیز و همه کس ابزاری در دست کارگردان است. و تنها کارگردان است که فکر می&shy;کند و ایده می&shy;دهد. اینان اجازة ابراز وجود به کسی را نمی&shy;دهند و نمی&shy;گذارند دیگران نیز از تخیل و خلاقیت خود به نفع کار بهره گیرند. و بسیار تکرو و افراطی عمل می&shy;نمایند</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">گروه دوم معتقدند که نهایتاً مسؤل اجرا، کارگردان است. اینان طرح و عملکرد خود را دارا می&shy;باشند. یعنی دارای تفکر خود می&shy;باشند. اما نظرات دیگر عوامل اجرایی را نیز می&shy;شنوند و در صورت مفید بودن و هم راستا بودن با هدف نهایی کار، آنان را نیز به خدمت می&shy;گیرند اما نظر نهایی با خود کارگردان است</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">گروه سوم مستقل و آگاهند. اما موجودیت متن را به شکل گروهی به اجرا تبدیل می&shy;کنند. اینان بیشتر به شکل بداهه عمل می&shy;کنند و از بداهه سازی به شکل نهایی اجرا می&shy;رسند. این طیف کارگردانان بیشتر هماهنگ کننده هستند و مجموعة ایده&shy;های دیگران را نظام می&shy;بخشند. البته این شیوة کارگردانی نیاز به مدت زمان طولانی برای تمرین یک اثر دارد. چرا که همه چیز در حال تمرین ساخته و پرداخته می&shy;شود</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span></span></span></span></p>\r\n\r\n<h3 dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"color:#e67e22\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">خصوصیات کارگردان</span></span></span></span></span></span></h3>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">مشکل می&shy;توان گفت که کارگردان باید دارای چه خصوصیاتی باشد چرا که او باید خصوصیات صدها نفر و حتی بیشتر را یکجا در خود داشته باشد. اما بازمی&shy;توان به خصوصیاتی چون، راهنما، تغذیه کننده، مشوق، پرقدرت، رهبری کننده، جسور، متواضع، مهربان، حیله&shy;گر، روانشناس، جامعه شناس، فهیم، نوگرا و</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:black\">&hellip;</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> اشاره کرد. اما همة اینها باز بخشی از خصوصیات یک کارگردان است</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">همچنین کارگردان باید روحیه&shy;ای جسور و ماجراجو داشته باشد و مشتاق باشد در راههای تازه طی طریق کند چرا که در این صورت است که می&shy;تواند همگام با زمانه&shy;اش پیش رود و حتی جلوتر از آن و همیشه حرفهایی تازه و نو برای جامعه&shy;اش درچنته داشته باشد</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.<br />\r\n&laquo; </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">کارگردان باید دارای کنجکاوی پایان ناپذیر یک ذهن روشن، عشق به انسان، حساسیت نسبت به محیط زندگی با همة جنبه&shy;های اجتماعی، انسانی و سیاسی آن و یک علاقه و انرژی زوال ناپذیر داشته باشد. در تحلیل نهایی باید ویراستار انسانها و رفتارهای آنها باشد ویراستاری که تصحیح کننده و نشان دهندة تصویر زندگی مردم، شادیها و رنجهای آنهاست. کنجکاوی پایان ناپذیر یک ذهن روشن برای یافتن ایده&shy;های نو، جدید و مناسب با توجه به ضرورتهای جامعه&shy;ای که در آن زندگی می&shy;کند نیاز کارگردان است. باید به انسانها بتواند عشق بورزد. تا بتواند با گروه اجرایی خود خصوصاً بازیگران رفتاری مناسب و شایسته داشته باشد. چرا که بازیگران موجوداتی بسیار حساس و شکننده می&shy;باشند. و دیگر اینکه گروه اجرایی و عوامل بردة کارگردان نیستند و کارگردان باید به این مسئله فکر کند که اگر آنها نباشند کارگردان و اجرایی نیز در میان نخواهد بود و لذا نباید به خود حق هرگونه برخوردی را باگروه بدهد. چرا که همگی انسان هستند و لایق احترام و توجه</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">دیگر اینکه کارگردان باید شکیبا باشد و نه مسامحه کار، مهربان و دلسوز باشد و نه ضعیف و احساساتی چرا که بر اریکه قدرت نشسته است و بیشتر به تعقل و تفکر نیاز دارد نه احساس، همچنین باید تمایل به قدرت و اعتبار و مسئولیت در او باشد. و دارای انضباط شخصی و بی باکی و هوش بالا و قابلیت استدلال و انعکاس</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> حقایق باشد.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/7.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_7.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_7.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_7.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_7.jpg\"}', 'کارگردانی, تاریخ, انواع کارگردانی،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'post', 482, 0, '2020-05-19 00:16:19', '2024-07-11 13:23:24'),
(14, 'خلاصه ای از تاریخچه کارگردانی سینما', 'خلاصه ای از تاریخچه کارگردانی سینما', 'بازیگران نقش اول به خاطر ضرورتی که در نقشهای خود احساس می کردند علاقه فراوانی به انجام اجراهای پیش بینی نشده ای از اثر بودند.بنابراین نیاز به حضور شخص دیگری بود که بیرون ازحیطه نمایش باشد وبه بازیافت نمایش کاراکترها جنبه های ارتباطی وکلا مفهوم نمایشنامه کمک کند.', '<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">در گذشته ازدوران سوفکل تا زمان شاو گاهی تمام مراحل تمرین نمایش با نظارت نویسنده نمایشنامه انجام می گرفت.این کار در نخستین دوره به صحنه آمدن توسط یک شاعر و یک صحنه آرا تحقق می یافت.شاعر کسی مانند جانسون و صحنه آرا مثل اینگو جونز بود. شکسپیر و بازیگران اصلی</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> نمایشنامه هایش وتمام آنهایی که در اجرای سهیم بودندهر کدام نقش معینی در اوج بخشیدن به نمایش و نیز راهنمایی بازیگران داشتند. در قرن نوزدهم سازمان دهی واقعی مراحل تمرین و اجرای نمایش به عهده سوفلورها یا مدیران صحنه بودو آنها نسخه از نمایشنامه را که در بر گیرنده جزئیات حرکتها وظایف هنر پیشگان اداره صحنه و چگونگی افکتها بودپیش خود نگه می داشتند. تمام کارگزاراننمایش که وظیفه ای به آنها محول شده بود با اشتیاق خاصی انجام میدادند نمایش نامه نویس تلاش می کرد که داستان را به صورت نمایشی در آورد.کارگردان صحنه به تدارکات صحنه لباس</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> واجرای حرکات موزون می پرداخت</span></span></span></span></span></span><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"><img alt=\"\" src=\"/upload/images/2020/5.jpg\" style=\"float:left; height:890px; width:555px\" /></span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">سوفلور وقتی با زیگران تمرین کافی نداشتند نمایش را انسجام می بخشید. بدین ترتیب بازی هنر پیشگان با درنظز گرفتن قوانینی معین و نه در اثر تمایل خود انگیخته آنان پی ریزی میشد. هنربازیگری که باآن</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> هر نمایشی روح میگیرد و یا از اعتبار می افتد طی قرون متمادیاغلب توسط هنر پیشگان بزرگ که استعدادعملی خود را ثابت کرده اند</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> و از معیارهای تئاتر زمان خودشان و ار میزان ونوع توقعات تماشاگران آگاه بوده اند به نسلهای بعدی منتقل شده است و اگر تئاتر آن زمان بی نقص به نظر میرسید علت آن درک این حقیقت بوده است که چه کسی&nbsp; بهتر از خود هنرپیشه می توانست مشخص نماید که چگونه یک نمایش احساس و اندیشه تماشاگرانش را بر انگیزاند؟</span></span></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">ظهور ناتورالیسم در اواخر قرن نوزدهم به حضور کارگردان هستی بخشید. خودانگیختگی نمایش بادیالوگهای واقعی و بازی روی صحنه و نیز رهایی ازشیوه های قراردادی بیان واقعیت و احساس مراحل تکوین تئاتر را ازحد نمایش صرف به درآورد و جنبه تحقیق راهم به آن افزود</span></span></span></span></span></span><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">بازیگران نقش اول به خاطر ضرورتی که در نقشهای خود احساس می کردند علاقه فراوانی به انجام اجراهای پیش بینی نشده ای از اثر بودند.بنابراین نیاز به حضور شخص دیگری بود که بیرون ازحیطه نمایش باشد وبه بازیافت نمایش کاراکترها جنبه های ارتباطی وکلا مفهوم نمایشنامه کمک کند.ضمنا ناتورالیسم برواقعیت مهمتری درباره نمایشنامه نویسی و اجرای آن اشاره داشت وخشنودی تماشاچیان را از صرف به خاطر هنر نمایی چند هنرپیشه به ستایش منطقی تری ازنمایشنامه و بازیگران ارتقا داد. بنابراین نخستین وظیفه یک کارگردان امروزی تغییر دادن هنر نمایی های فردی به یک کار گروهی هنری بود</span></span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span><br />\r\n<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">تئاتر در گذشته از ساختاری قراردادی برخورداربود. این بنای هنری ومعماران آن ارزش واعتبار فراوانی داشتندو در سطح گسترده ای</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> برای مردم قابل درک بود. در تئاتر&laquo;اورپید&raquo;بخشی از کاخ سلطنتی نسان داده میشود راه خروج از یک سوبه شهر و از سوی دیگر به حومه آن ختم می شود و گروه کر که در جای مدور خود به سرودخوانی سخن سرایی حرکات موزون و به اجرای حرکات میمیک می پرداخت به عنوان عامل پیوند دهنده میان</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> نمایشنامه و تماشاچی به حساب می آمد. اعضای این گروه میتوانستند به عنوان تفسیرگرانی مستقل و نیز درمقام کاراکترهایی گرفتار دایم در نوسان باشند. تئاتر دوره الیزابت از صحنه آرایی اجتناب می ورزد وفقط بر وقایع خود صحنه تاکید داشت. این کار حیطه قابل توجهی را برای بازی</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> بازیگران فراهم می آورد صحنه خارجی صفحه و سکویی دربالای صحنه داخلی.صحنه نمایش باتجمع گروهی از بازیگران خوش لباس شکل می گرفت. برای مثال این امکن وجود داشت که دیالوگهای مهم از طریق بالای صحنه به تعداد</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> بسیاری از بازیگران گفته شود . نمایش جز به هنگام ارتباط فیزیکی بازیگران کاملا غیر واقعی</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> به نظر می رسد</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> و افکت نمایش از پشت سقف صحنه ویا از طریق</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> دریچه ای درکف انجام می شد. بازیگری که نقش</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> یکی از خدایان و یاشیطان را بازی میکرد تنها می توانست به</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> تاثیرات ظاهری خودش و یا به قدرت وتوانایی نقش متکی باشد. تماشا گران برای درک واحساس بقیه نمایش از تخیلات خود بهره بجستند امکانات صحنه نمایش در چنین معیارهایی محدود شده بود نور و صحنه آرایی مناسب وجود نداشت. همه چیز خلاصه میشد در ژستهای عالی تمرین بیان ودر تراژدی های یونان به استفاده از یک سزی از حوادث پیشبینی</span></span></span>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\"> شده که کارگران نمایش را به موجودی زاید و بدل می ساخت .</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:12.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:black\">هنگامی که انتخاب افکت صحنه واقعیت و انعتاف پذیزی اعمال نمایشی و خود انگیختگی بیان نمایشی ضرورت وجود یک ساماندهنده و داور را برای نمایش پیش آوردکارگردان نمایش به معنای امروزی از تصوری پیشین هستی موجودیت یافته و از آن روز تا کنون مسولیت و نقش او اهمیت بیشتری پیدا کرده است. بی تردید نمایش باید پیش از آنکه تفسیر شود به اجرا در آید و سپس توسط کارگردان ارزیابی گردد</span></span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">.&nbsp;</span></span></span></span></span></span></p>', NULL, NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/6.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_6.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_6.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_6.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_6.jpg\"}', 'سینما,تئاتر,کارگردانی،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'post', 3, 0, '2020-05-20 22:25:06', '2023-08-05 02:21:54');
INSERT INTO `articles` (`id`, `title`, `slug`, `description`, `body`, `showonmenu`, `lang`, `images`, `tags`, `type`, `viewCount`, `commentCount`, `created_at`, `updated_at`) VALUES
(15, 'انیمیشن در ایران', 'انیمیشن در ایران', 'سازمان سینمایی ایران تا حدود ۱۳۱۰ خورشیدی فاقد امکانات تولید فیلم انیمیشن بود. علاقه و کنجکاوی اسفندیار احمدیه به نقاشی و سفالگری نخستین حرکت را در این حوزه به وجود آورد.', '<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">انیمیشن در ایران</span></span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">سازمان سینمایی ایران تا حدود </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۳۱۰</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> خورشیدی فاقد امکانات تولید فیلم انیمیشن بود. علاقه و کنجکاوی اسفندیار احمدیه به نقاشی و سفالگری نخستین حرکت را در این حوزه به وجود آورد. احمدیه، نخستین نقاشی کارتون را به صورت اسلاید ساخت و سپس آن را به صورت نقاشی متحرک درآورد. این فیلم ملانصرالدین نام داشت و در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۳۳۶</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> خورشیدی با فیلمبرداری پطروس پالیان با دوربین دستی </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۶</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">میلیمتری ساخته شد و </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۳</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> ثانیه بود</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><a href=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769599.jpg\" title=\"&quot;\r\n   اسفندیار احمدیه سازنده نخستین انیمیشن ایرانی و پدر انیمیشن ایران\r\n  &quot; \"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#860f10\"><img alt=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769599.jpg\" id=\"Picture_x0020_6\" src=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769599.jpg\" style=\"height:264.75pt; width:278.25pt\" title=\"&quot;\r\n   اسفندیار احمدیه سازنده نخستین انیمیشن ایرانی و پدر انیمیشن ایران\r\n  &quot;\" /> </span></span></span></strong></a></span></span></span></p>\r\n\r\n<h2 dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\">&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">اسفندیار احمدیه سازنده نخستین انیمیشن ایرانی و پدر انیمیشن ایران</span></span></span></span></span></span></h2>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۳۳۷</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> خورشیدی </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۶</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> تن از شاگردان ممتاز کارآموز رشته سینما در وزارت فرهنگ و هنر، برای تکمیل تحصیلات به آمریکا رفتند</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.&nbsp;</span></span></span><strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">پرویز اصانلو</span></span></span></strong>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در رشته انیمیشن تحصیل کرد و مدتی زیر نظر</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">لستر نورس</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">کارآموزی کرد. او مدتی هم در</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">استودیوی دیزنی</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">و</span></span></span><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">&nbsp;U. P. A&nbsp;</span></span></span></strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">به کار پرداخت. او در آمریکا و پیش از مراجعت به ایران، دستگاه</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">اکسبری</span></span></span></strong><strong>&nbsp;</strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">QXBERRY </span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">را به سفارش وزارت فرهنگ و هنر خریداری کرد و به ایران آورد</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۳۳۹</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> خورشیدی با دعوت از</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">جعفر</span></span></span></strong>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">تجارتچی</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">کاریکاتوریست قدیمی و همکاری پرویز اصانلو و اسفندیار احمدیه واحد نقاشی متحرک وزارت فرهنگ و هنر آغاز به کار کرد. فیلم</span></span></span><strong>&nbsp;</strong><strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">قمر مصنوعی</span></span></span></strong>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">حاصل همین دوره است که به کارگردانی اصانلو ساخته شد. طراحی و نقاشی آن را اسفندیار احمدیه بر عهده داشت. از آنجا که امکانات لابراتواری وجود نداشت این فیلم برای ظهور و چاپ به انگلیس فرستاده شد. مدت نمایش آن پنج دقیقه بود</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۳۴۳</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> خورشیدی</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">نصرت کریمی</span></span></span></strong>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">که تحصیلات سینمایی اش را در کشور چک و اسلواکی به پایان رسانده بود به ایران بازگشت. وی تا </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۳۴۷</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> خورشیدی هفت فیلم عروسکی و نقاشی متحرک برای وزارت فرهنگ و هنر ساخت که از میان آنها، فیلم</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">زندگی</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">شایان ذکر است، طرح های این فیلم به سبک نقاشی مینیاتور است. زندگی با تلاش</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">سرکیس زاکاریانس، نصرت شیرازی، فریدون فرشباف، تیموری</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">و</span></span></span><strong>&nbsp;</strong><strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">اسفندیار احمدیه</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">و با صرف </span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۳</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> سال وقت ساخته شد</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><a href=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769621.jpg\" title=\"&quot;\r\n   نصرت کریمی؛ از برجسته ترین چهره های انیمیشن پیش از انقلاب\r\n  &quot; \"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#860f10\"><img alt=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769621.jpg\" id=\"Picture_x0020_5\" src=\"https://img9.irna.ir/d/r2/2019/11/19/3/156769621.jpg\" style=\"height:387.75pt; width:324.75pt\" title=\"&quot;\r\n   نصرت کریمی؛ از برجسته ترین چهره های انیمیشن پیش از انقلاب\r\n  &quot;\" />&nbsp;</span></span></span></strong></a></span></span></span></p>\r\n\r\n<h2 dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">نصرت کریمی؛ از برجسته ترین چهره های انیمیشن پیش از انقلاب</span></span></span></span></span></span></h2>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">کانون پرورش فکری کودکان و نوجوانان در</span></span></span><span dir=\"RTL\" lang=\"FA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۱۳۴۷</span></span></span><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> خورشیدی شکل گرفت. فعالیت اصلی کانون تهیه کتاب برای کودکان بود. مرکز سینمایی کانون حاصل ارتباط این مرکز با نویسندگان و به ویژه نقاشان کتاب های کودکان بود. از نخستین تصویرگران کتاب کودک که به ساختن فیلم انیمیشن دست زدند، می توان</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">آراپیک باغداساریان</span></span></span></strong><strong>&nbsp;</strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">و</span></span></span>&nbsp;<strong><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">فرشید مثقالی</span></span></span></strong>&nbsp;<span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">را نام برد. در سال های بعد گرافیست ها و نقاشان و کاریکاتوریست های کانون چون مرتضی ممیز، علی اکبر صادقی، پرویز نادری، نورالدین زرین کلک، احمد عربانی و... نیز به تولید فیلم انیمیشن پرداختند؛ تلاش این هنرمندان در مدتی کوتاه، تولیداتی خوب و جوایز متعدد بین المللی را به همراه آورد</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\">&nbsp;</p>', NULL, NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/9.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_9.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_9.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_9.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_9.jpg\"}', 'انیمیشن ایران,نصرت کریمی,اسفندیار احمدیه', 'news1', 0, 0, '2020-05-20 22:55:20', '2020-06-15 02:41:01'),
(16, 'گریم و تفاوت آن با میک آپ', 'گریم و تفاوت آن با میک آپ', 'در گذشته، رشته گریم به ۲ نوع کلی تـقسیم مـی‌شد اما امروز بسیار گسترده‌تر از گذشته شده است. گریم صحنه، شامل: تـئاتر، اپرا و باله، گریم‌ و دوربین‌ شامل تلویزیون، سینما و عکاسی رنگی و سیاه و سفید است.', '<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در گذشته، رشته گریم به </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">۲</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\"> نوع کلی تـقسیم مـی&zwnj;شد اما امروز بسیار گسترده&zwnj;تر از گذشته شده است. گریم صحنه، شامل: تـئاتر، اپرا و باله، گریم&zwnj; و دوربین&zwnj; شامل تلویزیون، سینما و عکاسی رنگی و سیاه و سفید است. نوعی گریم که از سر تا پای&zwnj; بـازیگر بـا رنـگ&zwnj;های مایع کار می&zwnj;شود و کاربرد آن برای اپرا، باله و تئاترهای مدرن است که&zwnj;</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> BADY&zwnj; ART </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">نـامیده مـی&zwnj;شود. این نوع گریم را می&zwnj;شود بر بازیگر پوشیده با لباس&zwnj; مناسب، کار کرد</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">گریم</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> FASHION </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">یا مـد: کـه اغـلب بر چهره مانکن&zwnj;ها زن و مرد کار می&zwnj;شود. این نوع گریم در سازمان&zwnj;های امنیتی کشورها به عنوان ظـریف&zwnj;ترین گـریم استفاده می&zwnj;شود. برای مثال: برای&zwnj; فروشندگان&zwnj; مواد مخدر مأمور چهره خود را تبدیل به یک آدم مـعتاد مـی&zwnj;کند</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">گـریم جهت چهره سیاستمداران&zwnj;: در گذشته در مقابل دوربین به چهره سیاستمداران فقط ف&zwnj;ُن زده می&zwnj;شد اما امـروزه&zwnj; در غـرب، مشاهده می&zwnj;شود که سیاستمداران برای نشان دادن چهره جوان&zwnj;تر و شاد گریم&zwnj;های غلیظ بر چـهره&zwnj; دارنـد</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">گـریم&zwnj; در راهپیمایی&zwnj;ها و در استادیوم ورزشی بر چهره جوانان دیده می&zwnj;شود. گریم بر چهره&zwnj; کودکان مهدکودک و دبـستانی در خـیابان&zwnj;ها به چشم می&zwnj;خورد که نقش حیوانات و گل&zwnj;ها را بر چهره دارند</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">گریم&zwnj; برای فـیلم&zwnj;های سـوررئال بـا شخصیت&zwnj;های عجیب و غریب افکت گریم که بر قسمتی&zwnj; از بدن ساخته&zwnj;شده بازیگر یا مانکن ساخته شـده&zwnj; انـجام&zwnj; می&zwnj;شود، مانند منفجر کردن سر، قطع کردن دست&zwnj; و پا و دو نیم کردن بدن و غـیره</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">...</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:20.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">تفاوت گـریم با</span></span></span></strong><strong><span dir=\"LTR\" style=\"font-size:18.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> MAKE UP</span></span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">واژه&zwnj;</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> MAKE UP </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">انگلیسی به معنای&zwnj; آرایش&zwnj; برای چهره&zwnj; است. آرایش&zwnj; چهره، خـود رشته&zwnj;ای ظریف و هنری است&zwnj; که&zwnj; شامل آرایش روز، عصر، شب و عروس است که در آرایشگاه توسط آرایـشگر، انجام&zwnj; می&zwnj;شود</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">در بعضی از فیلم&zwnj;های خارجی&zwnj; به جای واژه گریم، واژه&zwnj;</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> MAKE UP &nbsp;ARTISTIC &nbsp;</span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">به معنای&zwnj; آرایش&zwnj; هنری، استفاده می&zwnj;شود. هـمچنین در زبـان فرانسه واژه</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> MAQIALLAGE </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">به معنای آرایش است&zwnj; که&zwnj; گاهی</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\"> MAQIALLAGE ARTISQUE </span></span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">به معنای&zwnj; آرایش&zwnj; هنری&zwnj; جایگزین مـی&zwnj;شود</span></span></span><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;iran-sans-web&quot;,&quot;serif&quot;\"><span style=\"color:#212529\">.</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\">&nbsp;</p>', NULL, NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/16.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_16.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_16.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_16.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_16.jpg\"}', 'گریم,مد,فشن،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'news1', 0, 0, '2020-05-20 23:55:15', '2020-06-15 20:10:55');
INSERT INTO `articles` (`id`, `title`, `slug`, `description`, `body`, `showonmenu`, `lang`, `images`, `tags`, `type`, `viewCount`, `commentCount`, `created_at`, `updated_at`) VALUES
(17, 'پیدایش گریم در ایران و جهان', 'پیدایش گریم در ایران و جهان', 'ریشه و اصل پیدایش گریم را در دوران های ابتدائی، در مراسم آئینی می توان جست، در این مراسم، از گریم به شكل بسیار ابتدائی استفاده می شده است...', '<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">ریشه و اصل پیدایش گریم را در دوران های ابتدائی، در مراسم آئینی می توان جست، در این مراسم، از گریم به شكل بسیار ابتدائی استفاده می شده است.</span></span></strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">..</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">گريم واژه&zwnj;اي فرانسوي است و از سال 1778 ميلادي متداول شده است و به معناي ادا، شكلك، صورتك و يا نقش يك پير مسخره مي&zwnj;باشد. مصدر اين كلمه گريمه به معناي خطوط پيري بر چهره كسي انداختن است و مفهوم آن آراستن چهره براي تئاتر و سينما مي&zwnj;باشد. صفت فاعلي آن &laquo;گريمور&raquo; است. اصطلاح انگليسي آن </span></span><span dir=\"LTR\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\">Make up</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> مي&zwnj;باشد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">پيدايش گريم بر دوران&zwnj;هاي ابتدائي زندگي بشر باز مي&zwnj;گردد. در آن زمان براي شركت در مراسم آييني چهره&zwnj;ها را با رسوب&zwnj;هاي مختلف، رنگ&zwnj;هاي معدني و يا عصاره گياهان رنگ&zwnj;آميزي مي&zwnj;كردند و گاهي صورتك&zwnj;هايي از پوست درختان تراشيده و روي چهرة خود قرار مي&zwnj;دادند. مصريان باستان، آفريقايي&zwnj;ها و سرخپوستان براي مراسم خود در جشن&zwnj;ها و عزاها از اين روش بهره مي&zwnj;گرفتند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">ريشه و اصل پيدايش گريم به را در دوران هاي ابتدائي، در مراسم آئيني مي توان جست، در اين مراسم، از گريم به شكل بسيار ابتدائي استفاده مي شده است. چهره ها را با رسوب مختلف، رنگ هاي معدني و يا عصاره گياهان رنگ آميزي مي كردند و گاه صورتك هائي كه از پوست درختان تراشيده شده بود، بر چهره مي نهادند. در طول زمان همچنان گریم را نزد مصریان باستان می توان مشاهده كرد كه كه عمدتا اطراف چشمان را با رنگ های سبز و خطوط سیاه رنگ آمیزی می نمودند، آفریقائیان نیز علاوه بر بكار بردن رنگ های شاد وزنده برای چهره می نهادند،همچنین سرخ پوستان با بكار بردن رنگ های مختلف بر چهره و تزئین سر با پرهای رنگی و صورتك هائی با رنگهای بسیار شاد، گریم را در زندگی و آئین های خود بكار می بستند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">بشر از بدو خلقت در پی فراهم آوردن موجبات سرگرمی خود بوده است، به اشكال گوناگون چون بازی با سایه، اجرای رقص، موسیقی و یا نمایش خود را سرگرم كرده است. در نمایش، بازیگر سعی كرده است، برای داشتن چهره ای متفاوت از دیگران و نیز به منظور تاثیر گذاردن نقش بر تماشاگر با تغییر شكل دادن خود به كمك رنگ آمیزی چهره، مو و به كار برده صورتك توسل جوید.آرایش کردن از زمان های بسیار قدیم مخصوصا زمانی که مردم با دارو آشنا شدند، بین خانمها و آقایان متداول بوده و آنها با علاقه فراوانی برای زیباتر نشان دادن صورتشان می کوشیدند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">مطالعه تاریخ باستان به ما ثابت میکند که در دوران ماقبل تاریخ نیز مردان و زنان به بهتر نشان دادن ظاهر خود علاقه مند بودند. نوع آرایش و وسایل آرایشی که در یک فرهنگ و ملیت برای زیباتر و جذابتر نشان دادن به کار میرود ممکن است در فرهنگ دیگر کاملا مردود باشد، اما اکثر مردم از تغییر در مد لباس، مو و آرایش صورت لذت میبرندبسیاری از مردم بر این باورند كه گریم جزئی از آرایش بوده و زیر مجموعه آن می باشد . اما به راحتی می توان پی به این واقعیت برد كه نه تنها گریم زیر مجموعه ارایش نیست بلكه بر عكس آرایش به عنوان جزئی كوچك از اجزای گریم به حساب می آید.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">در طول زمان همچنان گريم را نزد مصريان باستان مي توان مشاهده كرد كه كه عمدتا اطراف چشمان را با رنگ هاي سبز و خطوط سياه رنگ آميزي مي نمودند، آفريقائيان نيز علاوه بر بكار بردن رنگ هاي شاد وزنده براي چهره مي نهادند،همچنين سرخ پوستان با بكار بردن رنگ هاي مختلف بر چهره و تزئين سر با پرهاي رنگي و صورتك هائي با رنگهاي بسيار شاد، گريم را در زندگي و آئين هاي خود بكار مي بستند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">بشر از بدو خلقت در پي فراهم آوردن موجبات سرگرمي خود بوده است، به اشكال گوناگون چون بازي با سايه، اجراي رقص، موسيقي و يا نمايش خود را سرگرم كرده است. در نمايش، بازيگر سعي كرده است، براي داشتن چهره اي متفاوت از ديگران و نيز به منظور تاثير گذاردن نقش بر تماشاگر با تغيير شكل دادن خود به كمك رنگ آميزي چهره، مو و به كار برده صورتك توسل جويد.<br />\r\nامروزه واژه </span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">﴿</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> گريم </span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">﴾</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> در زبان فرانسه از سال </span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">۱۷۷۸</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> ميلادي به مفهوم آنچه در نمايش مورد نظر است به كار گرفته شده، به معناي ادا شكلك، صورتك و يا نقش يك پير مسخره. مصدر اين كلمه گريمه به معناي خطوط پيري بر چهره انداختن، آراستن چهره براي تئاتر و سينما است، صفت فاعلي آنرا گريمور مي نماند.امروزه واژه</span></span>&nbsp;<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> گریم</span></span>&nbsp;<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> در زبان فرانسه به مفهوم آنچه در نمایش مورد نظر است به كار گرفته شده، به معنای ادا شكلك، صورتك و یا نقش یك پیر مسخره. مصدر این كلمه گریم به معنای خطوط پیری بر چهره انداختن، آراستن چهره برای تئاتر و سینما است، صفت فاعلی آنرا گریمور می نماند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:20.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">پیدایش</span></span>&nbsp;<span style=\"font-size:20.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">گريم در جهان</span></span><br />\r\n<br />\r\n<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">تاريخ شروع آرايش كلا به مصريان قديم ميرسد. آنها براي مراسم مذهبي از عطريات و مواد آرايشي استفاده ميكردند و حمامهاي مخصوص براي خوشبو كردن پوست بدن ميگرفتند، به نظافت و پاكيزگي اهميت ميدادند و بعد از حمام از مواد خوشبوكننده و روغني براي نرم نگه داشتن پوستشان استفاده ميكردند. شانه و آيينه به عنوان اولين لوازم آرايشي مورد علاقه همه خانمها و آقايان بود. كلئوپاترا، ملكه مصر، مواد آرايش و عطر را عاشقانه براي پوست و مو و بدن خود استفاده ميكرده است.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">در قرون وسطي بيشتر مواد رنگي روي گونه ها و لبها زده ميشد. چشمها فقط با سرمه آرايش ميشدند. مواد آرايشي و داروها از هم جدا نبودند ولي در اواخر قرن شانزدهم تقريبا از هم تفكيك شدند. اين دوره بين كلاسيك و رنسانس بود، زماني كه مذهب نقش مهمي در زندگي و عقايد و رفتار اجتماعي مردم داشت. حمام روزانه متداول نبود ولي عطر و موادخوشبوكننده زيادي مصرف ميشد.در عصر رنسانس نقاشيهاي كلاسيك و نوشته ها نشان ميدهند كه چقدر آراستگي ظاهر بين مردم متداول بوده است. يكي از غيرعاديترين مدها تراشيدن ابروها و خط مو در پيشاني بود و معتقد بودند كه ابروي تراشيده چهره بسيار باهوش و متفكري به زن ميدهد.<br />\r\n<br />\r\nدر زمان اليزابت اول (1558 تا 1603 ميلادي ) ماسكهاي صورت متداول شدند . در اين دوره ماسكهايي از تركيب پودر پوست تخم مرغ , زاج سفيد , بوراكس , بادام , تخم خشخاش , شير , شراب , كره , ميوه و سبزيجات ساخته ميشد . گونه ولبها آرايش ميشدند ولي آرايش چشمها بسيار كم رنگ بود.ماري آنتوانت (1755 تا 1793 ميلادي) ملكه فرانسه و اكثر خانمهاي مرفه فرانسوي حمام توت فرنگي و شير ميگرفتند. لبها و گونه ها شديدا با رنگهاي صورتي و نارنجي آرايش ميشدند. به ابروها فرم داده مشد ولي از سايه چشم زياد استفاده نميشد و پودرهاي خوشبو براي صورت استفاده ميكردند. اين دوره به دوره افراط و زياده روي در آرايش معروف است.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">در زمان ويكتوريا، ملكه انگليس، آرايش صورت جذابيت خود را از دست داد. خانمها بسيار ساده و بدون رنگ بودند و معروف است كه زنان گونهاي خود را نيشگون گرفتن و لباهايشان را با گاز گرفتن سرخ ميكردند. ماسكهايي از تركيب عسل، تخم مرغ، شير، ميوه و سبزيجات و روغن هاي مختلف ساخته ميشد. اين دوره يكي از سخترين و محدودكننده ترين دوران تاريخ آرايش به حساب مي آيد.در سالهاي 1920 انقلابي در كارخانه هاي لوازم آرايش به وجود آمد و سايه هاي رنگارنگ براي چشم ها، رژگونه و رژ لبهاي متنوعي ساخته شد. كرمها و لوسيونهاي گوناگون براي پوست و شامپوهاي نرم كننده و تقويتي مو به بازار آمد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">در سالهاي 1950 و 1960 مدهاي اروپايي بيشتر كشورها را تحت الشعاع قرار دادند، مواد پاك كننده پوست، كرمها لوسيون ها و نرم كننده هاو لوازم آرايش متنوعي وارد بازار شدند. ابروهاي پهن مد شد، خط چشم نيز پهن مشخص بود، گونه ها و لبها رنگ ملايم داشتند. در اواخر 1960 , آرايش ترميمي و تكميلي متداول شد وباز هم ابروها باريك شدند.از سالهاي 1970 به بعد كارخانه ها رنگهاي متفاوتي براي آرايش روز و شب مواد و لوازم آرايش براي زيبايي، سلامتي و پاكيزگي پوست و مو ارائه كردند و خانمها بيشتر و بيشتر جذب اين مواد شدند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">روميها هم مانند يونانيها و مصريها حمام و نظافت را باور داشتند و خانمها در موقع حمام از شير و شراب استفاده مينمودند. حمامهاي رومي معمولا عمومي بود با قسمتهاي جداگانه براي خانمها و آقايان. مردان رومي نيز از عطريات و روغن براي خوشبو كردن پوست و نرم نگخ داشتن آن بعد از حمام استفاده ميكردند. شعرهاي يوناني زيادي درباره نظافت، زيبايي مو و پوست و ناخن سروده شده است و نقاشيهاي بسياري بيانگر اهميت آن است.<br />\r\n<br />\r\n<br />\r\nيونانيها از سال 460 ميلادي با علاقه فراوان عطريات و مواد آرايشي ميساختند و براي مراسم مذهبي و استفاده شخصي و حتي گاهي به عنوان دارو استفاده ميكردند.خانمهاي يوناني از موادي به نام ورميليون براي آرايش صورت استفاده ميكردند كه به رنگ فرمز روشن بود و از تركيب نمكهاي جيوه در ساخت آن استفاده ميشدخانمهاي رومي مخلوطي از گچ و سرب را به صورت ميزدند تا آن را سفيدتر كنند، همچنين با رنگهاي گياهي گونه ها و لبهايشان را سرخ ميكردندو از مخلوط ذرت، شير و آرد ماسكهايي براي صورت تهيه ميكردند كه آن را با كره نيز مخلوط مينمودند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><br />\r\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">آسياييها , به خصوص چينيها , تاريخچه بسيار دوري در مورد سلامتي دارند و آراستگي را يك هنر به حساب مي آورند , مخصوصا بين افرادي كه امكان استفاده از لوازم آرايش مختلف و لباسهاي متنوع را داشتند . در ژاپن آرايش گيشاها خيره كننده بود و هنوز هم يك هنر با ارزش به شمار مي آيد . ايرانيان نيز همانگونه كه تاريخ و شواهد نشان ميدهد , پيرو مد و آراستگي مو و صورت و لباس بودند.افريقايي ها تحول بزرگي در آرايش مو و طراحي لباسهاي مخصوص به وجود آوردند. آنها داروهاي بسياري از گياهان و مواد اوليه كه در اطراف خود داشتند تهيه ميكردند. همچنين از رنگهاي زيادي براي آرايش صورت خود استفاده ميكردند كه بعضي از انواع مدل مو و لباس آنها هنوز هم رايج است.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:22.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">پیدایش گریم در ایران</span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">اگر ما پيشرفت و تغييرات در مد و زيبايي را از زمان اجدادمان تا حالا دنبال كنيم، شايد به تمام آن تغييرات پي ببريم آثار به جا مانده از اجداد به ما نشان ميدهد كه مواد رنگي براي رنگ كردن مو، ناخن و پوست به كار ميرفته و خالكوبي نيز در آن زمان وجود داشته و مرسوم بوده است. مواد رنگي و آرايش از ريشه درختان، برگ و كلا مواد گياهي و معدني تهيه ميگرديد. مصلا پودر به نام كوهل شبيه سرمه در زمان مصريان قديم براي شفافتر و بزرگتر نشان دادن چشمها مصرف ميشد. دوده چراغ را به ابرو ميزدند و از اكسيد آهن براي تهيه سرخاب جهت آرايش صورت استفاده ميكردند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">تئاتر از نظر تاريخي در دوره قاجاريه</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">﴿</span></span> <span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">۱۷۸۰-۱۹۱۹</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> م</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">﴾</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> بطور كلي تئاتر ايران با تحول جديدي روبرو گرديد. نخست تعزيه به اوج خود رسيد، سپس تئاتر سنتي ديگري بنام </span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">﴿</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> تئاتر روحوضي </span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">﴾</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> رشد نمود.گريم براي سينما، تلويزيون و عكاسي جنبه فني تر بخود مي گيرد، چه چشم دوربين ها بسيار دقيق تر از چشم انسان است، اشتباه كوچكي در رنگ زمينه و يا وجود لكه كوچك و خطاي ظريفي كه چشم انسان قادر به تشخيص و رويت آنها نيست در مقابل لنزها ي دوربين بوضوح نشان داده مي شود. نبايد فراموش كرد كه روتوش </span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">﴿</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> اصلاح فيلم </span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">﴾</span></span><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> فيلم نگاتيف بخصوص در مورد فيلم هاي رنگي نه تنها كامل و بي عيب نخواهد بود بلكه اصولا در همه شرايط امكان پذير نيست.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">بهمين جهت زدن رنگ به چهره و عمل تصحيح چهره و استفاده از تكنيك سايه و روشن در مقابل دوربين كه عمل روتوش فيلم را انجام مي دهد براي سينما، تلويزيون و عكاسي ضروري است.با آثاري از خرابه هاي تئاتر در گوشه و كنار يقين است كه در گذشته نمايش در اين سرزمين موجود بوده و آثار مكتوب آن به عللي چون به آتش كشيدن، تاراج كردن از ميان رفته و نابود شده باشد.آرايش كردن از زمان هاي بسيار قديم مخصوصا زماني كه مردم با دارو آشنا شدند , بين خانمها و آقايان متداول بوده و آنها با علاقه فراواني براي زيباتر نشان دادن صورتشان مي كوشيدندمطالعه تاريخ باستان به ما ثابت ميكند كه در دوران ماقبل تاريخ نيز مردان و زنان به بهتر نشان دادن ظاهر خود علاقهمند بودند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">نوع آرايش و وسايل آرايشي كه در يك فرهنگ و مليت براي زيباتر و جذابتر نشان دادن به كار ميرود ممكن است در فرهنگ ديگر كاملا مردود باشد، اما اكثر مردم از تغيير در مد لباس، مو و آرايش صورت لذت ميبرند .متاسفانه براي ثبوت موجوديت تئاتر در گذشته ايران، مدارك و منابع تاريخي در دسترس نمي باشد. آنچه مسلم است از نظر جغرافيائي، ايران باستان ما بين سرزمين هائي چون مصر باستان، هند و يونان كه هر يك از تمدن، فرهنگ و هنرهاي خاصي از نمايش غني بوده اند قرار داشته است. بواسطه نزديكي و داشتن رابطه با اين سرزمين ها، ايران باستان نمي تواند در گذشته فاقد هنرهاي نمايشي بوده باشد.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">(با نگاهي به گذشته، مشاهده مي شود، اين سرزمين اغلب دستخوش تهاجم، حملات و جنگ ها ئي در طول تاريخ بوده است كه هر بار با استيلاي حكومتي جديد به فرهنگ و هنر هاي زيباي آن لطمه وارد آمده است.)</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">حال برای درك بهتر موضوع نخست به تفاوت های معنوی میان گریم ، آرایش و پیرایش میپردازیم</span></span></strong><strong>&nbsp;</strong><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> :</span></span></strong></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">آرایش :</span></span></strong>&nbsp;<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">از نظر لغوی به معنای آراستن ، زینت دادن و مرتب گردانیدن آمده كه گسترهء محدودی از فن را شامل می شود .</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">پیرایش :</span></span></strong>&nbsp;<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">این لغت هم به معنای برش دادن ، برداشتن زیادی ها و ازبین بردن نا همواری ها و رساندن فرد یا هر چیز پیراستنی به حد مطلوب از نظر نمای ظاهر است .</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><strong><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">گریم :</span></span></strong>&nbsp;<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">متأسفانه این لغت در برگردان فارسی ، چهره پردازی</span></span>&nbsp;<span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"> تعبیر شده است كه كلمه جامعی نیست و نشان از عدم اطلاع معادل سازان محترم دارد .</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">گریم به معنای هر گونه تغییر در چهره ، دست و پا و سایر اعضای بدن در جهت رساندن شخص به چهره مطلوبی كه مد نظر طراح است بوده و شامل انواع گریم های تئاتری ، تلویزیونی ، سینمایی و گریم شخصی و ... می شود .در گریم هم از آرایش و هم از پیرایش نشانه هایی دیده می شود ، چرا كه هر چقدر بازیگر به فرد یا شخصیت مورد نظر نزدیكتر باشد ، باز هم به دلیل این كه هیچ گاه دو نفر در سرتاسر دنیا نمی توان یافت كه كاملا شبیه هم باشند ، باید تغییراتی در رنگ مو ، اندازه مو ( كوتاه كردن و یا بلند كردن ) ، فرم مو ( صاف ، مجعد ، فر ،...) فرم اعضای چهره ، رنگ و اندازه و فرم چشم ها ، ابرو ها ، بینی و جام چهره فرد داده شود تا نهایتا شباهت بین دو نفر یا شباهت فرد با طرح ارائه شده به بیشترین حد ممكن برسد...</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:right\">&nbsp;</p>', NULL, NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/17.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_17.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_17.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_17.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_17.jpg\"}', 'گریم , ریشه گریم , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر', 'news2', 0, 0, '2020-05-20 23:59:07', '2020-11-01 03:06:00'),
(18, 'سرفصلهای آموزش گریم سینمایی', 'سرفصلهای آموزش گریم سینمایی', 'آموزش گریم سینمایی به دانشجویان امکان میدهد تا با اصول گریم و کاربرد رنگ ها در گریم، نحوه تعادل چهره، انواع گریم در سینما و تئاتر، مواد گریم و ویژه گی های انواع گریم آشنا شوند.', '<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">&nbsp;آموزش گریم سینمایی به دانشجویان امکان میدهد تا با اصول گریم و کاربرد رنگ ها در گریم، نحوه تعادل چهره، انواع گریم در سینما و تئاتر، مواد گریم و ویژه گی های انواع گریم آشنا شوند. علاوه بر این، علاقمندان به این حوزه میتوانند آموزش گریم سینمایی و انواع گریم را در دوره آموزشی گریمور سینما، تئاترو تلویزیون که توسط مرکز آموزش مجازی پارس ارائه و معرفی شده، دنبال کرده و برای انواع گریم ها تجربه عملی بدست آورند.</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">&nbsp;سر فصل ها آموزشی </span></span></span>&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">این دوره عباتند از :</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">&nbsp;فصل اول</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">&nbsp;سیر تحول و کاربرد گریم نحوه پیدایش گریم و سیر تحو ل آن نحوه گریم کردن درتئاتر و تفاوت های آن با گریم در سینما چگونگی اجرای گریم در تلویزیون طراح چهره پردازی</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">&nbsp;فصل دوم </span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">به کارگیری فنون های مختلف برای زیر سازی پوست و فونداسیون</span></span></span>&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\"> بررسی و تحلیل انواع چهره و حالات آن انواع پیشانی ابروها چشم ها تقسیمات چشم پوست مواد گریم ابزار چهره پردازی انواع پاک کننده ها در انواع پوست </span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">فصل سوم </span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">رنگها توانایی شناخت رنگ ها کاربرد رنگها در کنار هم جدول روانشناسی رنگها جغرافیائی رنگ شناخت رنگ در رابطه با سایه چشم متناسب با رنگ چشم </span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">فصل چهارم</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">&nbsp;ایجاد تعادل و متعادل سازی صورت انواع گریم سینمایی متعادل سازی و نامتعادل سازی چهر</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"color:#616161\">ٴ</span></span></span><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">ه بازیگر متعادل و نامتعادل سازی بر کل بدن چاق و لاغر کردن چهره بازیگر آرایش متفاوت مو قالب گیری از صورت بازیگر منفی و مثبت کرن چهره نژادهای مختلف تمهیدهای ویژه چهره پردازی</span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">&nbsp;فصل پنجم </span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">توانائی آماده کردن محیط و وسایل کار بهداشت جهت پيشگيري از شيوع بيماريهاي مسري خصوصیات گریمور از نظر ظاهری و اخلاقی آموزش</span></span></span>&nbsp;<span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">گریمور سینما و تئاتر و تلویزیون</span></span></span>&nbsp;</span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">&nbsp;وسایل گریم صورت </span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span style=\"font-size:18.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#616161\">گریم، نوعی آرایش است که به منظور متعادل و زیبا تر کردن چهره به کار می رود. انجام گریم ، مستلزم استفاده از ابزار خاصی است که عبارتند از : فون ( یک نوع کرم پایه که پوست صورت را صاف می کند) دارای سه نوع خشک (برای پوست های چرب) ، معمولی (برای پوست های آکنه دار )و مایع( دارای قدرت پوشانندگی کم) ،اسفنج مخصوص، براش یا قلم در اندازه های متفاوت، فرچه مناسب، رنگ مناسب، کرم پودر مناسب، موچین، فیکس کننده، چسب لاتکس و اکلیل هستند. </span></span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\">&nbsp;</p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/18.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_18.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_18.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_18.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_18.jpg\"}', 'گریم سینمایی،گریم فیلم،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'news3', 0, 0, '2020-05-21 00:03:25', '2020-06-15 20:09:52'),
(19, 'درباره موسسه هفت هنر سیمرغ', 'درباره موسسه هفت هنر سیمرغ', 'موسسه فرهنگی هنری چند منظوره \"هفت هنر سیمرغ\" در سال 1398 با شماره ثبت 49237 در اداره فرهنگ و ارشاد اسلامی تهران به منظور آموزش رشته های هنری اعم از : کارگردانی ،', '<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">موسسه فرهنگی هنری چند منظوره &quot;هفت هنر سیمرغ&quot; در سال 1398 با شماره ثبت 49237 در اداره فرهنگ و ارشاد اسلامی استان تهران به منظور آموزش و اشتغال رشته های هنری اعم از : کارگردانی ، بازیگری ، دوبله ، گویندگی ، انیمیشن سازی ، نویسندگی ، گریم سینمایی ، موسیقی و در زمینه تهیه و تولید فیلم ، تهیه کنندگی و حمایت از فیلمسازان و پروژه های تولیدی بخصوص حمایت از تولیدات دانشجویان و کار اولی ها و همچنین برگزاری جشنواره و سیمنار و نمایشگاه&nbsp; به ثبت رسیده و شروع به فعالیت نمود.همچنین طی این سال ها&nbsp;&nbsp;رادیو سیمرغ با همت هنرجویان این موسسه و حمایت ها و تهیه کنندگی سجاد نادر به ثبت رسید وماهانه بیش از 500&nbsp; دقیقه برنامه رادیویی طنز تولید و پخش میشود</span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-family:B Koodak\"><span style=\"font-size:21.3333px\"><em>اعضای هیئت موسس موسسه به شرح زیر می باشد.</em></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-family:B Koodak\"><span style=\"font-size:21.3333px\"><em>مدیر عامل موسسه : سجاد نادر</em></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-family:B Koodak\"><span style=\"font-size:21.3333px\"><em>هیئت موسسین : سجاد نادر&nbsp; ، شهره جوانبخت ، ندا کریمی&nbsp;</em></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-family:B Koodak\"><span style=\"font-size:21.3333px\"><em>اعضا هیئت مدیره : سجاد نادر ، میترا خواجه ئیان ، سمیه غفاری</em></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">خلاصه ای از زمینه فعالیت موسسه به شرح زیر است :</span></span> </span></span></p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">برگزاری همایش</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">گردهمایی</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">سمینار و همکاری در برپایی نمایشگاه ها و جشنواره های فرهنگی و هنری و مشارکت در برگزاری همایش ها و سمینارهای تخصصی از طریق خدمات فرهنگی هنری</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">تهیه و عرضه محصولات فرهنگی، هنری دارای مجوز از قبیل: کتاب، اقلام رایانه ای در زمینه زبان و ادبیات فارسی، معارف اسلامی و علوم قرآنی و نیز فیلمهای مستند و سینمایی(دارای مجوز ). </span></span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">گردآوری، تنظیم، تدوین و انتشار نمایه نامه و فهرستهای موضوعی و ارایه خدمات فهرست نویسی</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">نمایه سازی</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">سند آرایی مدارک و اسناد فرهنگی و تاریخی و نیز طراحی و تشکیل بانکهای اطلاعاتی</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">آسیب شناسی و معرفی آسیبهای اجتماعی به اقشار مختلف مردم و ارایه راه کارهای مقابله با آنها</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"font-family:&quot;B Koodak&quot;\">طراحی و برگزاری دوره آموزشی فرهنگی هنری مهارت های آزاد و مهارتی کار دانش طبق نظر اداره کل فرهنگ و ارشاد اسلامی استان تهران از تاریخ ثبت به مدت نامحدودمی باشد.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">مرکز اصلی موسسه : تهران ، میرزای شیرازی ، بعد از پمپ بنزین ، کوچه پانزدهم پلاک 44 طبقه اول&nbsp;</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">تلفن : &nbsp;53 و 02188341652</span></span></span></span></span></span></p>\r\n\r\n<p dir=\"rtl\" style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;\"><span dir=\"RTL\" lang=\"AR-SA\" style=\"font-size:16.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;B Koodak&quot;\"><span style=\"color:#212529\">واتساپ : 09354637055&nbsp;</span></span></span></span></span></span></p>', NULL, NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2021\\/WhatsApp Image 2021-06-21 at 10.17.47.jpeg\",\"300\":\"\\/upload\\/images\\/2021\\/300_WhatsApp Image 2021-06-21 at 10.17.47.jpeg\",\"600\":\"\\/upload\\/images\\/2021\\/600_WhatsApp Image 2021-06-21 at 10.17.47.jpeg\",\"900\":\"\\/upload\\/images\\/2021\\/900_WhatsApp Image 2021-06-21 at 10.17.47.jpeg\"},\"thumb\":\"\\/upload\\/images\\/2021\\/WhatsApp Image 2021-06-21 at 10.17.47.jpeg\"}', 'هفت هنر سیمرغ ,  رادیو هفت سیمرغ , سجاد نادر ,  , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ ,', 'about', 2107, 0, '2020-05-21 00:09:54', '2024-07-15 12:45:54'),
(20, 'دوره های گویندگی', 'دوره های گویندگی', 'رفع بهبود استرس جمع ، بدن در گویندگی ، شیوای کلامی ، اعتماد به نفس', '<p dir=\"rtl\">سرفصله های این دوره شامل :</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>گویندگی و فرق بین اجرای رادیو و تلویزیون</li>\r\n	<li>تقویت حنجره و تنفس صحیح</li>\r\n	<li>بیان و ترمیم بیان برای گویندگی</li>\r\n	<li>گرفتن لکنتهای زبانی</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">با این دوره هنرجویان مسلط به رفتارهای زیر می شوند:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>بهبود استرس جمع</li>\r\n	<li>زبان بدن در گویندگی</li>\r\n	<li>شیوایی کلام</li>\r\n	<li>اعتماد به نفس و تاثیر آن در بیان مناسب</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">مدرس این دوره خانم <strong>سمانه صادقی</strong> می باشد.</p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/11.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_11.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_11.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_11.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_11.jpg\"}', 'گویندگی  , دوبله , سخنوری , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , سخنوری ,  فن بیان ,  دوبله فیلم ,  دوبله انیمیشن  ,', 'منو', 1067, 0, '2020-05-22 04:14:27', '2024-07-15 15:36:21'),
(21, 'آموزش گریم سینمایی و ورکشاپ', 'آموزش گریم سینمایی', 'آموزش گریم سینمایی', '<p dir=\"rtl\">آموزش<strong> گریم سینمایی</strong> با پیشرفته و بروزترین متدهای اجرایی</p>\r\n\r\n<p dir=\"rtl\">سرفصلهای این دوره عبارتند از :</p>\r\n\r\n<p dir=\"rtl\"><strong>دوره مقدماتی </strong></p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>شناخت آناتومی چهره</li>\r\n	<li>روانشناسی چهره</li>\r\n	<li>گریم متعادل سازی و نا متعادل سازی</li>\r\n	<li>گریم فانتزی و فشن</li>\r\n	<li>گریم عروسکی</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\"><strong>دوره پیشرفته</strong></p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>افکت گریم زخم و سوختگی</li>\r\n	<li>قالب گیری و قطعه سازی</li>\r\n	<li>پتینه و کلاژ</li>\r\n	<li>ساخت و طراحی انواع ماسک</li>\r\n	<li>بافت و نصب مو بر ریش و سبیل</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">مدرس این دوره خانم <strong>مهرنوش صحابی گریمور سینما و تلویزیون م</strong>ی باشد.</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/24.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_24.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_24.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_24.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_24.jpg\"}', 'گریم , اموزش گریم , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ سجاد نادر , مهرنوش صحابی ,  آکادمی مولتی آرت', 'منو', 1060, 0, '2020-05-22 07:23:02', '2024-07-15 14:34:50'),
(22, 'آموزش انیمیشن', 'آموزش انیمیشن', 'آموزش انیمیشن برای', '<p dir=\"rtl\">با کمترین زمان ممکن بهترین و کاربردی ترین متدهای انیمیشن را از هفت هنر سیمرغ دوره ببینید.</p>\r\n\r\n<p dir=\"rtl\">انیمیشن و موشن گرافیک را در شش ماه یاد بگیرید و یک<strong> پروژه حرفه ای</strong> بسارید.</p>\r\n\r\n<p dir=\"rtl\">یک سوم مبلغ کلاسها بعد از استخدام شدن در موسسه از شما دریافت می گردد.</p>\r\n\r\n<p dir=\"rtl\">سرفصلهای این دوره ها عبارتند از :</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>دوره کامل موشن گرافیک</li>\r\n	<li>دوره کامل انیمیشن</li>\r\n	<li>دوره متحرک سازی</li>\r\n	<li>کارگردانی انیمیشن</li>\r\n	<li>کارگردانی موشن گرافیک</li>\r\n	<li>استوری بورد انیمیشن</li>\r\n	<li>ایده پردازی انیمیشن</li>\r\n	<li>ایده پردازی موشن گرافیک</li>\r\n	<li>مبانی دیجیتال پینت</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">مدرس این دوره خانم <strong>سمیه غفاری </strong>عضو انجمن انیمیشن ایران هستند.</p>\r\n\r\n<p dir=\"rtl\">نکته مهم : توجه داشته باشید که دوره <strong>موشن گرافیک و انیمیشن </strong>جداگانه هستند.</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/27.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_27.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_27.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_27.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/600_27.jpg\"}', 'انیمیشن , موشن گرافیک،موشن،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'منو', 1539, 0, '2020-05-22 07:30:49', '2024-07-15 11:48:06'),
(23, 'آموزش دوبله فیلم، انیمیشن و سریال', 'آموزش دوبله فیلم، انیمیشن و سریال', 'آموزش دوبله فیلم، انیمیشن و سریال', '<p dir=\"rtl\">دوره های حرفه ای آموزش دوبله فیلم و سریال یا انیمیشن به صورت کاربردی ، در صورت آموزش این دوره شما می توانید خلاقیت خود را با ما به اشتراک بگزارید و با موسسه همکاری نمایید.</p>\r\n\r\n<p dir=\"rtl\">هفت هنر سیمرغ قصد دارد از هنرجوهای خود به عنوان نیروی استخدامی دعوت به همکاری نماید.</p>\r\n\r\n<p dir=\"rtl\">یک سوم مبلغ کلاسهای دوبله بعد از استخدام شدن از حقوق شما کسر می گردد.</p>\r\n\r\n<p dir=\"rtl\">مدرس&nbsp;<strong>جناب استاد فلاحپور</strong>&nbsp;</p>\r\n\r\n<p dir=\"rtl\">سرفصلهای این دوره :</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>آموزش فن بیان در گویندگی</li>\r\n	<li>آموزش عملی دوبله در استودیو</li>\r\n	<li>آموزش حس و بازی</li>\r\n</ul>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/28.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_28.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_28.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_28.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_28.jpg\"}', 'دوبله , گویندگی ,  فن بیان , دوبله ,سریال , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , آکادمی مولتی آرت', 'منو', 1129, 0, '2020-05-22 07:38:09', '2024-07-15 13:35:52'),
(24, 'آموزش ویولن', 'آموزش ویولن', 'آموزش ویولن', '<p dir=\"rtl\">آموزش ویولن زیر نظر بهترین اساتید موسیقی ایرانی</p>\r\n\r\n<p dir=\"rtl\">استاد یاسین کشانی با 25 سال سابقه تدریس آماده ارائه بهترین سبک آموزش به هنرجویان عزیز می باشد.</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/3.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_3.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_3.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_3.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_3.jpg\"}', 'موسیقی,ویولن,آموزش ساز،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،آموزش ویالون', 'منو', 843, 0, '2020-05-22 07:46:52', '2024-07-15 14:23:06'),
(25, 'آموزش نقاشی کودک و نوجوان', 'آموزش نقاشی کودک و نوجوان', 'آموزش نقاشی کودک و نوجوان', '<p dir=\"rtl\">ترم تابستان را با موسسه ما سپری کنید</p>\r\n\r\n<p dir=\"rtl\">دوره نقاشی کودک و&nbsp; نوجوان از مبتدی تا پیشرفته برای فرزندان شما</p>\r\n\r\n<p dir=\"rtl\">پس از پایان دوره برای هنرجویان نمایشگاه نقاشی گذاشته می شود.</p>\r\n\r\n<p dir=\"rtl\">پک کامل آموزش نقاشی هدیه هفت هنر سیمرغ به هنرجویان عزیز می باشد.</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/ziba.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_ziba.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_ziba.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_ziba.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_ziba.jpg\"}', 'هنردستی,نقاشی,نقاشی کودک،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'منو', 1457, 0, '2020-05-22 23:15:42', '2024-07-15 23:43:11');
