(26, 'آموزش برنامه نویسی وب سایت', 'آموزش برنامه نویسی وب سایت', 'آموزش برنامه نویسی وب سایت', '<p dir=\"rtl\">دوره های طراحی وبسایت هم به صورت کلی و هم به صورت دوره ای و تخصصی با سرفصلهای زیر قابل برگزاری است:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>html5/css3/design photoshop templates</li>\r\n	<li>javascript/jquery</li>\r\n	<li>bootstrap</li>\r\n	<li>php/mvc/oop</li>\r\n	<li>laravel</li>\r\n	<li>wordpress</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">تمامی دوره ها پروژه محور می باشد و در انتهای دوره هر هنرجو یک وب سایت به عنوان نمونه کار خود دارد و می تواند از آن به درآمد برسد.</p>\r\n\r\n<p dir=\"rtl\">مدرس این دوره خانم مهندس شبنم بهمنی با 7 سال سابقه تدریس و طراحی و برنامه نویسی وب سایت هستند.</p>\r\n\r\n<p dir=\"rtl\">توجه : ظرفیت کلاسها محدود است.</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/Tehran-webdesign-.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_Tehran-webdesign-.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_Tehran-webdesign-.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_Tehran-webdesign-.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_Tehran-webdesign-.jpg\"}', 'وبسایت , برنامه نویسی,طراحی وبسایت,کدنویسی,مهندسی نرم افزار', 'منو', 842, 0, '2020-05-22 23:22:28', '2024-07-15 13:47:32'),
(27, 'آموزش ساخت زیورآلات و قطعات رزینی', 'آموزش ساخت زیورآلات و قطعات رزینی', 'آموزش ساخت زیورآلات و قطعات رزینی', '<p dir=\"rtl\">آموزش ساخت انواع زیورآلات و قطعات رزینی بدون نیاز به سرمایه و تخصص از صفر با شما همراهیم.</p>\r\n\r\n<p dir=\"rtl\">این دوره کارگاه تخصصی دوروزه می باشد. در صورت نداشتن مواد اولیه موسسه تهیه آن را برای شما مقدور می کند.</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/unnamed.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_unnamed.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_unnamed.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_unnamed.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_unnamed.jpg\"}', 'هنردستی,جواهرسازی,رزین,تزیینی،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'منو', 719, 0, '2020-05-22 23:42:38', '2024-07-15 17:52:55'),
(28, 'آموزش تنبک و سازهای کوبه ای', 'آموزش تنبک و سازهای کوبه ای', 'آموزش تنبک', '<p dir=\"rtl\">آموزش سازهای کوبه ایکلاسهای دف ،داربوکا،کاخن،تنبک<br />\r\nو ساز تنبور سطوح ( مقدماتی تا پیشرفته )<br />\r\nمدرس این دوره جناب <strong>دانیال فاضلی </strong>با رزومه های زیر :</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>مولف کتاب حکایت های دف</li>\r\n	<li>سرپرست گروه دف نوازان کژال</li>\r\n	<li>ارائه تمرینات گروهی و دوره های تئوری ریتم و نت نویسی</li>\r\n	<li>و برگزاری کنسرت های کوبه ای</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\"><strong>توجه : </strong>امکان برگزاری کلاس های آنلاین و مجازی برای سایر شهرهای ایران امکان پذیر است.<br />\r\nترم های دو ماهه شامل ۸ جلسه و هرجلسه ۳۰ دقیقه تمرین میباشد.</p>\r\n\r\n<p dir=\"rtl\">امکانات آموزش <strong>هفت هنر سیمرغ</strong>:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>کلاس های تمام آکوستیک صدا</li>\r\n	<li>استودیوی ضبط صدا و تصویر بصورت حرفه ای</li>\r\n	<li>پرده کروماکی و ست نورپردازی حرفه ای</li>\r\n	<li>امکان ضبط کلیپ و تیزر</li>\r\n</ul>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/tonbak1.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_tonbak1.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_tonbak1.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_tonbak1.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_tonbak1.jpg\"}', 'موسیقی,تنبک,آموزش ساز,داربوکا,کاخن،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،دانیال فاضلی', 'منو', 889, 0, '2020-05-24 00:29:24', '2024-07-15 15:10:03'),
(29, 'آموزش تنبک', 'آموزش تنبک', 'آموزش تنبک', '<p dir=\"rtl\">آموزش تنبک از مبتدی تا پیشرفته برای هنرجویان علاقمند آماده برگزاری است.<br />\r\nمدرس این دوره خانم ا<strong>ستاد نوروزی </strong>با 30 سال سابقه آموزش هسنتند.</p>\r\n\r\n<p dir=\"rtl\"><strong>توجه : </strong>امکان برگزاری کلاس های آنلاین و مجازی برای سایر شهرهای ایران امکان پذیر است.<br />\r\nترم های دو ماهه شامل ۸ جلسه و هرجلسه ۳۰ دقیقه تمرین میباشد.</p>\r\n\r\n<p dir=\"rtl\">امکانات آموزش <strong>هفت هنر سیمرغ</strong>:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>کلاس های تمام آکوستیک صدا</li>\r\n	<li>استودیوی ضبط صدا و تصویر بصورت حرفه ای</li>\r\n	<li>پرده کروماکی و ست نورپردازی حرفه ای</li>\r\n	<li>امکان ضبط کلیپ و تیزر</li>\r\n</ul>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/tonbak.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_tonbak.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_tonbak.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_tonbak.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_tonbak.jpg\"}', 'موسیقی,تنبک, ,آموزش ساز،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،آموزش تنبک', 'منو', 846, 0, '2020-05-24 00:33:34', '2024-07-15 16:15:00'),
(30, 'آموزش دف', 'آموزش دف', 'آموزش دف', '<p dir=\"rtl\">آموزش ساز دف از مبتدی تا پیشرفته برای تمامی علاقمندان توسط استاد دانیال فاضلی</p>\r\n\r\n<p dir=\"rtl\">با بیش از سالها تجربه و سرپرست گروه دف نواران کژال ، مولف کتاب حکایتهای دف</p>\r\n\r\n<p dir=\"rtl\">برگزاری کنسرت از آثار هنرجویان در پایان دوره&nbsp;</p>\r\n\r\n<p dir=\"rtl\">شرکت هنرجویان در کنسرتهای برگزاری موسسهزسر نظر استاد فاضلی</p>\r\n\r\n<p dir=\"rtl\"><strong>توجه : </strong>امکان برگزاری کلاس های آنلاین و مجازی برای سایر شهرهای ایران امکان پذیر است.<br />\r\nترم های یک ونیم ماهه ف دوماهه و سه ماهه&nbsp; و هرجلسه ۳۰ دقیقه تمرین میباشد.</p>\r\n\r\n<p dir=\"rtl\">امکانات آموزش <strong>هفت هنر سیمرغ</strong>:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>کلاس های تمام آکوستیک صدا</li>\r\n	<li>استودیوی ضبط صدا و تصویر بصورت حرفه ای</li>\r\n	<li>پرده کروماکی و ست نورپردازی حرفه ای</li>\r\n	<li>امکان ضبط کلیپ و تیزر</li>\r\n</ul>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/a5.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_a5.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_a5.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_a5.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_a5.jpg\"}', 'موسیقی , دف , آموزش ساز ,  اموزش دف  , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , دانیال فاضلی , آکادمی مولتی آرت', 'منو', 944, 0, '2020-05-24 00:37:59', '2024-07-15 09:11:10'),
(31, 'آموزش ساز تار', 'آموزش ساز تار', 'آموزش ساز تار', '<p dir=\"rtl\">آموزش ساز تار با بهترین متدهای آموزشی</p>\r\n\r\n<p dir=\"rtl\">استاد یاسین کشانی با 25 سال سابقه تدریس آماده ارائه بهترین سبک آموزش به هنرجویان عزیز می باشد.</p>\r\n\r\n<p dir=\"rtl\"><strong>توجه : </strong>امکان برگزاری کلاس های آنلاین و مجازی برای سایر شهرهای ایران امکان پذیر است.<br />\r\nترم های دو ماهه شامل ۸ جلسه و هرجلسه ۳۰ دقیقه تمرین میباشد.</p>\r\n\r\n<p dir=\"rtl\">امکانات آموزش <strong>هفت هنر سیمرغ</strong>:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>کلاس های تمام آکوستیک صدا</li>\r\n	<li>استودیوی ضبط صدا و تصویر بصورت حرفه ای</li>\r\n	<li>پرده کروماکی و ست نورپردازی حرفه ای</li>\r\n	<li>امکان ضبط کلیپ و تیزر</li>\r\n</ul>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/tar.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_tar.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_tar.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_tar.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_tar.jpg\"}', 'موسیقی,تار, ,آموزش ساز،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،', 'منو', 803, 0, '2020-05-24 00:44:16', '2024-07-15 09:11:10'),
(32, 'آموزش ساز سه تار', 'آموزش ساز سه تار', 'آموزش ساز سه تار', '<p dir=\"rtl\">آموزش سه تار زیر نظر بهترین اساتید موسیقی ایرانی</p>\r\n\r\n<p dir=\"rtl\">استاد یاسین کشانی با 25 سال سابقه تدریس آماده ارائه بهترین سبک آموزش به هنرجویان عزیز می باشد.</p>\r\n\r\n<p dir=\"rtl\"><strong>توجه : </strong>امکان برگزاری کلاس های آنلاین و مجازی برای سایر شهرهای ایران امکان پذیر است.<br />\r\nترم های دو ماهه شامل ۸ جلسه و هرجلسه ۳۰ دقیقه تمرین میباشد.</p>\r\n\r\n<p dir=\"rtl\">امکانات آموزش <strong>هفت هنر سیمرغ</strong>:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>کلاس های تمام آکوستیک صدا</li>\r\n	<li>استودیوی ضبط صدا و تصویر بصورت حرفه ای</li>\r\n	<li>پرده کروماکی و ست نورپردازی حرفه ای</li>\r\n	<li>امکان ضبط کلیپ و تیزر</li>\r\n</ul>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/3tar.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_3tar.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_3tar.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_3tar.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_3tar.jpg\"}', 'موسیقی,سه تار, آموزش ساز،موسسه فرهنگی هنری هفت هنرسیمرغ،موسسه هفت هنرسیمرغ،هفت هنرسیمرغ،سجاد نادر،ویولن ، ویولون،سه تار', 'منو', 777, 0, '2020-05-24 00:46:41', '2024-07-15 09:11:10'),
(35, 'آموزش فیلمنامه نویسی', 'آموزش-فیلمانامه-نویسی', 'آموزش فیلمنامه نویسی', '<p dir=\"rtl\">در این دوره <strong>آموزش فیلمنامه نویسی </strong>را به سبک <strong>رابرت مک لی </strong>آموزش می دهیم.</p>\r\n\r\n<p dir=\"rtl\">تکنیکهای نوشتن فیلمنامه و نمایش نامه آموزش داده خواهد شد.</p>\r\n\r\n<p dir=\"rtl\">مبانی فیلمنامه نویسی به سبک آمریکایی گفته خواهد شد.</p>\r\n\r\n<p dir=\"rtl\">تفاوت فیلمنامه های بلند و کوتاه به هنرجویان عزیز یاد خواهیم داد.</p>\r\n\r\n<p dir=\"rtl\"><strong>خبر خوب : ساخت یکی از فیلمنامه های انتخابی پس از دوره آموزشی با پشتوانه مالی موسسه نمونه کار قوی شما خواهد شد.</strong></p>\r\n\r\n<p dir=\"rtl\">مدرس این دوره خانم مهتاب غیاثی که مدرس فیلمنامه نویسی و کارگردانی به صورت تخصصی هستند، می باشند.</p>\r\n\r\n<ul dir=\"rtl\">\r\n</ul>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره 02188304196&nbsp;تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/robert_mckee.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_robert_mckee.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_robert_mckee.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_robert_mckee.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_robert_mckee.jpg\"}', 'نویسندگی , فیلمنامه , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , فیلمنامه , نویسندگی , آموزش نویسندگی , فیلمنامه نویسی ,  آکادمی مولتی آرت', 'منو', 1083, 0, '2020-05-30 00:48:30', '2024-07-16 00:34:22'),
(36, 'آموزش خوشنویسی', 'آموزش-خوشنویسی', 'آموزش خوشنویسی', '<h2 dir=\"rtl\" style=\"text-align:center\">خط ما اعتبار ماست</h2>\r\n\r\n<p dir=\"rtl\">آموزش خوشنویسی به صورت اصولی با سرفصلهای زیر آماده ارائه به علاقمندان می باشد:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>خوشنویسی</li>\r\n	<li>آموزش خط نستعلیق</li>\r\n	<li>اموزش خط شکسته</li>\r\n	<li>اموزش خط ثلث و نسخ</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">یاد بگیریم زیبا بنویسیم تا زیبا اثرگذار باشیم.</p>\r\n\r\n<p dir=\"rtl\">مدرس این دوره خانم فریده آندرواژ می باشند که عضو<strong> <a href=\"http://calligraphers.ir/\">انجمن خوشنویسان ایران</a></strong> هستند.</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>\r\n\r\n<p dir=\"rtl\">&nbsp;</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/khoshnveisi.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_khoshnveisi.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_khoshnveisi.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_khoshnveisi.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_khoshnveisi.jpg\"}', 'هنردستی , نوشتار , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , فریده اندرواژ , خوشنویسی , خط , نستعلیق , نقاشی خط', 'منو', 900, 0, '2020-06-01 04:03:33', '2024-07-15 22:13:29'),
(37, 'آموزش بازیگری جلوی دوربین', 'آموزش-بازیگری-به-صورت-عملی', 'آموزش بازیگری جلوی دوربین', '<p dir=\"rtl\">سرفصلهای این دوره شامل:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>شناخت پلان های تصویری<br />\r\n	فن بیان وبیومکانیک بدن<br />\r\n	تفاوت بازیگری رادیو ،تیاتر با تصویر<br />\r\n	در موسسه &quot;هفت هنر سیمرغ &quot; آموزش ببینید و در تولیدات موسسه جلو دوربین بازی کنید.</li>\r\n	<li>برگزاری ورکشاپ آموزشی برای تمام سنین</li>\r\n	<li>زیر نظر استاد امیر غفارمنش و مهتاب غیاثی</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\"><strong>هنرجویان عزیزی که در تست قبول شوند در فیلم سینمایی &quot;فصل سرما&quot; به تهیه کنندگی موسسه و کارگردانی اقای سجاد نادر تازه ترین تولید موسسه با عقد قرارداد بازی خواهند کرد.</strong></p>\r\n\r\n<p dir=\"rtl\">در این دوره به موارد زیر خواهیم پرداخت:</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>&nbsp;کلاسهای بازیگری جلو دوربین</li>\r\n	<li>تفاوت بازیگری تئاتر و تصویر</li>\r\n	<li>بازیگری جلو دوربین</li>\r\n	<li>استفاده از هنرجویان در فیلم سینمایی &quot; فصل سرما&quot;</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/ghafarmanesh.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_ghafarmanesh.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_ghafarmanesh.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_ghafarmanesh.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_ghafarmanesh.jpg\"}', 'بازیگری،تئاتر،موسسه فرهنگی هنری هفت هنر سیمرغ،موسسه هفت هنر سیمرغ،هفت هنر سیمرغ،امیرغفارمنش،سجاد نادر', 'منو', 1492, 0, '2020-06-01 04:11:04', '2024-07-15 14:43:40'),
(38, 'نمونه تولیدات موسسه', 'نمونه-تولیدات-موسسه', 'هفت هنر سیمرغ قصد دارد با ارائه بهترین امکانات با بهترین مزایا برای هنرجویان ، فضای پرتلاشی برای نقش آفرینی ایجاد کند.', '<p dir=\"rtl\">نمونه تولیدات <strong>موسسه هفت هنر سیمرغ</strong> :</p>\r\n\r\n<ol dir=\"rtl\">\r\n	<li>تله فیلم رجعت</li>\r\n	<li>تله فیلم پرستو می آید</li>\r\n	<li>تله فیلم خوابی برای محیا</li>\r\n	<li>تله فیلم دختری در پناهگاه</li>\r\n	<li>مستند سریال تا آسمان</li>\r\n	<li>مستند سینمایی خلوتگاه دل</li>\r\n</ol>\r\n\r\n<p dir=\"rtl\"><img alt=\"\" src=\"/upload/images/2020/DSC_1487.JPG\" style=\"height:335px; width:500px\" /></p>\r\n\r\n<p dir=\"rtl\"><img alt=\"enlightened\" src=\"http://artamoztv.com/ckeditor/plugins/smiley/images/lightbulb.png\" style=\"height:23px; width:23px\" title=\"enlightened\" /> در موسسه ما هدف فقط آموزش نیست و همه هنرجویان بنا به نقشهای مورد نیاز در همه تولیدات فیلم موسسه بازی خواهند کرد.<br />\r\nبا آموزش در موسسه ما همکار همیشگی ما خواهید بود.<br />\r\n&nbsp;</p>\r\n\r\n<p dir=\"rtl\"><strong>یک خبر خوب : </strong>شهریه اقساطی برای عزیزانی که قادر به پرداخت یک جا نیستند نیز در نظر گرفته شده است.</p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/DSC_1478.JPG\",\"300\":\"\\/upload\\/images\\/2020\\/300_DSC_1478.JPG\",\"600\":\"\\/upload\\/images\\/2020\\/600_DSC_1478.JPG\",\"900\":\"\\/upload\\/images\\/2020\\/900_DSC_1478.JPG\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_DSC_1478.JPG\"}', 'آموزش , بازیگری , تلویزیون , معرفی هفت هنرسیمرغ , نمونه کار , سیمرغ\r\nموسسه فرهنگی هنری هفت هنر سیمرغ , هفت هنر سیمرغ , سجاد نادر , آکادمی مولتی آرت', 'post', 69, 0, '2020-06-08 01:40:18', '2024-07-08 21:45:50'),
(39, 'آموزش ترمیم موی سینمایی گریم', 'آموزش-ترمیم-موی-سینمایی-گریم', 'آموزش ترمیم موی سینمایی گریم', '<p dir=\"rtl\">&nbsp;</p>\r\n\r\n<p dir=\"rtl\">ترمیم مو (انیجکشن) یکی از متدهای روز برای ترمیم مو بدون کاشت است.</p>\r\n\r\n<p dir=\"rtl\">سرفصهای این دوره عبارتند از :</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>آشنای با پلیمرهای پروتز</li>\r\n	<li>آشنایی با کلینر</li>\r\n	<li>طریق پاکسازی چسب از روی پروتز و بیس سر</li>\r\n	<li>آشنایی با نحوه قالب گیری جمجمه</li>\r\n	<li>طریقه بافت مو برروی اسکین بیس</li>\r\n	<li>آشنایی و نحوه بلیچینگ پروتز برای طبیعی سازی هدروم</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">هنرجویان عزیز پس از آموزش می توانند در <strong>موسسه ترمیم موی سون</strong> مشغول به کار شوند.</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>\r\n\r\n<p dir=\"rtl\">&nbsp;</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/unnamed22.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_unnamed22.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_unnamed22.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_unnamed22.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/600_unnamed22.jpg\"}', 'ترمیم مو,گریم , موسسه فرهنگی هنری هفت هنرسیمرغ , موسسه هفت هنرسیمرغ , هفت هنرسیمرغ , سجاد نادر , گریم , ترمیم مو', 'منو', 903, 0, '2020-06-12 01:22:26', '2024-07-15 14:11:17'),
(44, 'آموزش عکاسی', 'آموزش-عکاسی', 'آموزش عکاسی حرفه ای از مبتدی تا پیشرفته', '<p style=\"text-align:right\"><span style=\"font-size:22px\">عکاسی سینمایی عکاسی پرتره و عکاسی حرفه ای را با آموزشگاه ما تجربه کنید.</span></p>\r\n\r\n<p style=\"text-align:right\"><br />\r\nموسسه فرهنگی هنری &quot;هفت هنر سیمرغ&quot; با اساتید برجسته سینما دوره عکاسی برای ترم تابستان برگزار می نماید.<br />\r\nزیر نظر استاد حق نظرلو تصویر بردار و کارگردان صداسیما<br />\r\nدر موسسه ما آموزش ببینید و استخدام عکاس فیلمهای تولیدی ما شوید .همچنین معرفی به پروژه های فیلم بعنوان عکاس.</p>\r\n\r\n<p style=\"text-align:right\">آموزش &nbsp;آشنایی با دوربینهای دیجیتال و آنالوگ<br />\r\nاشنایی با ترکیب بندی و قاب بندی دوربین<br />\r\nاشنایی با تقاط طلایی و خطوط طلایی<br />\r\nاشنایی با انواع لنز و کاربرد آنها&nbsp;<br />\r\nاشنایی با وایت بالانس کردن و کاربا انواع فلاش<br />\r\nآموزش نور پردازی .نور سنجی و انواع فوکوس<br />\r\nآموزش عکاسی پرتره.طبیعت .عکاسی شب</p>\r\n\r\n<p style=\"text-align:right\">کلاسها در استودیوی عکاسی و از جلسه اول بصورت عملی و کار با دوربین برگزار میشود.</p>\r\n\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/TS-Night-Photography.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_TS-Night-Photography.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_TS-Night-Photography.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_TS-Night-Photography.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_TS-Night-Photography.jpg\"}', 'عکاسی , آموزش عکاسی , موسسه فرهنگی هنری هفت هنر سیمرغ , موسسه هفت هنر سیمرغ , هفت هنر سیمرغ سجاد نادر ,  فهیمه کنشلو ,  آکادمی مولتی آرت', 'منو', 917, 0, '2020-06-15 20:26:55', '2024-07-15 11:27:38'),
(45, 'دوره جدید موشن گرافیک و انیمیشن', 'دوره-جدید-موشن-گرافیک-و-انیمیشن', 'آموزش ببینید شاغل شوید', '<p>&nbsp;با آموزش در این دوره علاوه بر اینکه شاغل میشوید مهارت یک حرفه جدید با بازار کار زیاد را کسب می نمایید.</p>\r\n\r\n<p>تولید صفر تا صد موشن گرافیک و انیمیشن به همراه صداگذاری</p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/WhatsApp Image 2020-04-21 at 13.05.37.jpeg\",\"300\":\"\\/upload\\/images\\/2020\\/300_WhatsApp Image 2020-04-21 at 13.05.37.jpeg\",\"600\":\"\\/upload\\/images\\/2020\\/600_WhatsApp Image 2020-04-21 at 13.05.37.jpeg\",\"900\":\"\\/upload\\/images\\/2020\\/900_WhatsApp Image 2020-04-21 at 13.05.37.jpeg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_WhatsApp Image 2020-04-21 at 13.05.37.jpeg\"}', 'موسسه هفت هنر سیمرغ , هفت هنر سیمرغ , انیمیشن سازی , موشن گرافیک , سجاد ناد , ,موسسه فرهنگی هنری هفت هنر سیمرغ , سمیه غفاری بهار , آکادمی مولتی آرت', 'post', 12, 0, '2020-06-17 20:26:51', '2024-02-09 01:03:51'),
(46, 'گریم سینمایی', 'گریم-سینمایی', 'آموزش پیشرفته گریم سینمایی  برای فیلم', '<p>دوره پیشرفته گریم سینمایی</p>\r\n\r\n<p>گریم زخم و صورت</p>\r\n\r\n<p>قالب گیری و انواع آن</p>\r\n\r\n<p>ساخت و طراحی انواع ماسک</p>\r\n\r\n<p>بافت مو و ریش و سیبیل مصنوعی</p>\r\n\r\n<p>گریم متعادل سازی و نامتعادل سازی</p>\r\n\r\n<p>گریم فانتزی و فشن</p>\r\n\r\n<p>گریم عروسکی</p>\r\n\r\n<p>ترمیم و پروتز مو&nbsp;<img alt=\"\" src=\"/upload/images/2020/0bd00e74-8504-4909-bcc1-4ba55cf3d218.jpg\" style=\"height:500px; width:526px\" /></p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/be4455c9-dbe8-471b-882e-5639b68b61c3.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_be4455c9-dbe8-471b-882e-5639b68b61c3.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_be4455c9-dbe8-471b-882e-5639b68b61c3.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_be4455c9-dbe8-471b-882e-5639b68b61c3.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_be4455c9-dbe8-471b-882e-5639b68b61c3.jpg\"}', 'موسسه هفت هنر سیمرغ , هفت هنر سیمرغ , گریم , گریم سینمایی , گریم فیلم , ,سجاد نادر , موسسه فرهنگی هنری هفت هنر سیمرغ , آکادمی مولتی آرت', 'post', 287, 0, '2020-06-17 20:38:22', '2024-07-03 20:59:22'),
(47, 'آموزش  عکاسی و فیلمبرداری', 'آموزش-عکاسی-و-فیلمبرداری', 'عکاسی برای ترم تابستان', '<p>&nbsp;</p>\r\n\r\n<p>آموزش صفرتا صد عکاسی ، نورپردازی و تصویر برداری<br />\r\n&nbsp; برگزاری جشنواره عکس از عکسهای هنرجویان در&nbsp;&nbsp; پایان دوره با حمایت موسسه</p>\r\n\r\n<p>۱: آموزش عکاسی پرتره<br />\r\n۲ :آموزش عکاسی مدلینیگ<br />\r\n۳: آموزش عکاسی هنری و صنعتی<br />\r\n۴: آشنایی با لنزها و دوربینهای دیجیتال و آنالوگ<br />\r\n۵: آموزش نورپردازی حرفه ای برا فیلم و عکس<br />\r\n۶: آموزش کار با انواع فلاشرهای سرخود و استودیویی<br />\r\n۷: آشنایی با خطوط طلایی و نقاط طلایی عکس<br />\r\n&nbsp;زیر نظر استاد با تجربه عکاسی و تصویربرداری صداسیما<br />\r\nدعوت به کار از هنرجویان پس از پایان دوره در پشت صحنه فیلمهای تولیدی موسسه بعنوان عکاس<br />\r\nمعرفی به پروژه ها و همایش ها بعنوان عکاس و تصویربردار.</p>\r\n\r\n<p>به همراه آموزش رایگان فتوشاپ</p>\r\n\r\n<p>&nbsp;</p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/TS-Night-Photography.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_TS-Night-Photography.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_TS-Night-Photography.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_TS-Night-Photography.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_TS-Night-Photography.jpg\"}', 'موسسه فرهنگی هنری هفت هنر سیمرغ , هفت هنر سیمرغ , عکاسی , آموزش عکاسی , اموزشگاه سیمرغ , فیلمبرداری , تویربردار, \r\n , سینما , فیلم, , عکاس ,  آکادمی مولتی آرت', 'post', 87, 0, '2020-06-30 19:22:53', '2024-06-03 14:39:07'),
(48, 'ورکشاپ رایگان گریم', 'ورکشاپ-رایگان-گریم', 'ورکشاپ رایگان گریم سینمایی', '<p style=\"text-align:right\"><span style=\"font-size:18px\">ورکشاپ رایگان گریم برای علاقه مندان به گریم سینما</span></p>\r\n\r\n<p style=\"text-align:right\">در این ورکشاپ انواع &#39; گریم</p>\r\n\r\n<p style=\"text-align:right\">گریم زخم و صورت</p>\r\n\r\n<p style=\"text-align:right\">قالب گیری و انواع آن</p>\r\n\r\n<p style=\"text-align:right\">ساخت و طراحی انواع ماسک</p>\r\n\r\n<p style=\"text-align:right\">بافت مو و ریش و سیبیل مصنوعی</p>\r\n\r\n<p style=\"text-align:right\">گریم متعادل سازی و نامتعادل سازی</p>\r\n\r\n<p style=\"text-align:right\">گریم فانتزی و فشن</p>\r\n\r\n<p style=\"text-align:right\">گریم عروسکی</p>\r\n\r\n<p style=\"text-align:right\">اموزش داده میشود.</p>\r\n\r\n<p style=\"text-align:right\">در محیطی کاملا بهداشتی و رعایت فاصله&nbsp; اجتماعی</p>\r\n\r\n<p style=\"text-align:right\">تعداد خیلی محدود اولویت با ثبت نام کننده های اولیه است</p>\r\n\r\n<p style=\"text-align:right\">استفاده برای موم علاقه مندان مجاز است&nbsp;</p>\r\n\r\n<p style=\"text-align:right\">استفاده از ماسک و دستکش دراین ورکشاپ اجباریست&nbsp;و درصورت نداشتن ماسک و دستکش میتوانید از موسسه رایگان تحویل بگیرید.</p>\r\n\r\n<p style=\"text-align:right\">جهت شرکت دراین ورکشاپ حتما باید ثبت نام کنید قبلش و درصورت ثبت نام نکردن از پذیرش در کلاس معذوریم.</p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/WhatsApp Image 2020-06-01 at 14.05.07.jpeg\",\"300\":\"\\/upload\\/images\\/2020\\/300_WhatsApp Image 2020-06-01 at 14.05.07.jpeg\",\"600\":\"\\/upload\\/images\\/2020\\/600_WhatsApp Image 2020-06-01 at 14.05.07.jpeg\",\"900\":\"\\/upload\\/images\\/2020\\/900_WhatsApp Image 2020-06-01 at 14.05.07.jpeg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_WhatsApp Image 2020-06-01 at 14.05.07.jpeg\"}', 'موسسه هفت هنر سیمرغ,هفت هنر سیمرغ,گریم,گریم سینمایی,گریم فیلم,سجاد نادر,موسسه فرهنگی هنری هفت هنر سیمرغ,ورکشاپ،دوبله,عکاسی', 'post', 78, 0, '2020-07-03 22:43:47', '2024-07-05 19:32:47'),
(49, 'کلاسهای موشن گرافیک و انیمیشن', 'کلاسهای-موشن-گرافیک-و-انیمیشن', 'برگزاری کلاسهای موشن گرافیک درموسسه هفت هنر سیمرغ', '<p>برگزاری کلاسهای موشن گرافیک بصورت حرفه ای و تخصصی&nbsp; در موسسه هفت هنر سیمرغ&nbsp;</p>\r\n\r\n<p>بهترین مکان یادگیری موشن سازی حرفه ای توسط اساتید مجرب و موشن ساز این موسسه</p>\r\n\r\n<p>با ما شاغل شوید.</p>\r\n\r\n<p>صفرتاصد موشن سازی همراه با صداگذاری حرفه ای&nbsp;</p>', 'off', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/\\u06f2\\u06f0\\u06f2\\u06f0\\u06f0\\u06f7\\u06f1\\u06f6_\\u06f1\\u06f8\\u06f2\\u06f7\\u06f1\\u06f2.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_\\u06f2\\u06f0\\u06f2\\u06f0\\u06f0\\u06f7\\u06f1\\u06f6_\\u06f1\\u06f8\\u06f2\\u06f7\\u06f1\\u06f2.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_\\u06f2\\u06f0\\u06f2\\u06f0\\u06f0\\u06f7\\u06f1\\u06f6_\\u06f1\\u06f8\\u06f2\\u06f7\\u06f1\\u06f2.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_\\u06f2\\u06f0\\u06f2\\u06f0\\u06f0\\u06f7\\u06f1\\u06f6_\\u06f1\\u06f8\\u06f2\\u06f7\\u06f1\\u06f2.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_\\u06f2\\u06f0\\u06f2\\u06f0\\u06f0\\u06f7\\u06f1\\u06f6_\\u06f1\\u06f8\\u06f2\\u06f7\\u06f1\\u06f2.jpg\"}', 'انیمیشن  , موسسه فرهنگی هنری هفت هنر سیمرغ  ,  هفت هنر سیمرغ , سمیه  غفاری  , موشنگرافیک , سجاد نادر , آکادمی مولتی آرت', 'post', 78, 0, '2020-07-15 20:44:04', '2024-05-19 12:39:59'),
(50, 'دوره تخصصی تولید موشن گرافیک سه بعدی', 'دوره-تخصصی-تولید-موشن-گرافیک-سه-بعدی', 'موشن گرافیک سه بعدی با سینما 4d همراه با مباحث پیشرفته تولید موشن', '<p>&nbsp;</p>\r\n\r\n<p dir=\"rtl\">دوره تخصصی تولید موشن گرافیک سه بعدی همراه با سینما 4D</p>\r\n\r\n<p dir=\"rtl\">سرفصلهای آموزشی این دوره عبارتند از :</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>دوره متحرک سازی</li>\r\n	<li>کارگردانی موشن گرافیک</li>\r\n	<li>استوری بورد موشن گرافیک</li>\r\n	<li>ایده پردازی موشن گرافیک</li>\r\n	<li>مبانی دیجیتال پینت</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">نام برنامه های آموزشی در این دوره عبارتند از :</p>\r\n\r\n<ul dir=\"rtl\">\r\n	<li>after</li>\r\n	<li>tv paint</li>\r\n	<li>premire</li>\r\n	<li>photoshop</li>\r\n	<li>illustrator</li>\r\n	<li>nuke</li>\r\n</ul>\r\n\r\n<p dir=\"rtl\">15 جلسه کار حرفه ای با مدرسین جناب اقای <strong>رضا طاهری</strong> و <strong>سجاد نادر</strong> را خواهید گذراند.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره <a href=\"http://tel:02188312984\">02188312984</a> تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/motion.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_motion.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_motion.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_motion.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_motion.jpg\"}', 'انیمیشن , آموزشی موشنگرافیک , سجادنادر , موشن, , موشن گرافیک\r\nدو بعدی , سه بعدی , سینما فوردی', 'post', 1077, 0, '2020-08-03 01:56:12', '2024-07-15 22:48:47'),
(51, 'آموزش عکاسی با موبایل', 'آموزش-عکاسی-با-موبایل', 'آموزش عکاسی حرفه ای با موبایل در موسسه فرهنگی هنری \"هفت هنر سیمرغ\" زیر نظر استاد خانم راد\r\nبدون نیاز داشتن به دوربین عکاسی حرفه ای', '<p>آموزش عکاسی حرفه ای با موبایل در موسسه فرهنگی هنری &quot;هفت هنر سیمرغ&quot; زیر نظر استاد خانم راد<br />\r\nبدون نیاز داشتن به دوربین عکاسی حرفه ای با گوشی موبایل در همه جا بهترین عکسهای با کیفیتی ثبت کنید.<br />\r\nآموزش ببینید و از لحظه های زیبای زندگیتان همه جا عکس ثبت کنید.<br />\r\nبرای کالاهای تبلیغی خود نیاز به عکاس حرفه ای ندارید خودتون با گوشی خودتون بهترینهارا عکس بگیرید.<br />\r\nآموزش ترفندهای عکاسی با گوشی<br />\r\nآنوزش استفاده از خطوط شبکه برای متعادل سازی<br />\r\nآموزش تنظیم فوکوس دوربین گوشی<br />\r\nآموزش توجه به فضای منفی در عکاسی با موبایل<br />\r\nآموزش پیدا کردن چشم اندازهای مختلف<br />\r\nآموزش بازی با انعکاس در عکاسی با موبایل<br />\r\nآموزش توجه به خطوط پیشرو در عکاسی با موبایل<br />\r\nآموزش آشنایی با انواع لتز موبایل وواستفاده از آنها<br />\r\nآموزش نورپردازی و انواع فوکوس با موبایل<br />\r\nآموزش ادیت عکس و فیلم با گوشی موبایل<br />\r\nآموزش فیلمبرداری و ساخت کلیپ و ادیت و صداگذاری<br />\r\n(ورکشاپ ۴ و ۶ ساعته بهمراه پذیرایی)<br />\r\nکلاسها در استودیوی عکاسی و با امکانات عکاسی برگزار میشود&nbsp;<br />\r\nآدرس :هفت تیر .قائم مقام میدات شعاع اول خردمند پلاک ۱۳۵ طبقه اول</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/smartphone-camera.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_smartphone-camera.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_smartphone-camera.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_smartphone-camera.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_smartphone-camera.jpg\"}', 'موسسه هفت هنر سیمرغ  , اموزش عکاسی  ,, عکاسی با موبایل  ,  عکاسی , هفت هنر سیمرغ  ,  سجاد نادر , اموزشگاه عکاسی  ,  آکادمی مولتی آرت', 'news', 1004, 0, '2020-09-12 20:30:48', '2024-07-15 16:50:31'),
(52, 'آموزش عکاسی تخصصی', 'عکاسی-با-موبایل', 'عکاسی با موبایل و دوربین', '<p>آموزش عکاسی حرفه ای با موبایل و دوربین</p>\r\n\r\n<p>&nbsp;در موسسه فرهنگی هنری &quot;هفت هنر سیمرغ&quot; زیر نظر استاد خانم راد<br />\r\nبدون نیاز داشتن به دوربین عکاسی حرفه ای با گوشی موبایل در همه جا بهترین عکسهای با کیفیتی ثبت کنید.<br />\r\nآموزش ببینید و از لحظه های زیبای زندگیتان همه جا عکس ثبت کنید.<br />\r\nآموزش با دوربین عکاسی و موبایل<br />\r\nکلاسهای دوربین و موبایل جدا میباشند.</p>\r\n\r\n<p>عکاسی با موبایل برای کالاهای تبلیغی خودتان</p>\r\n\r\n<p>✅ آموزش ترفندهای عکاسی با گوشی<br />\r\n✅ آموزش استفاده از خطوط شبکه برای متعادل سازی<br />\r\n✅ آموزش تنظیم فوکوس دوربین و موبایل<br />\r\n✅ آموزش توجه به فضای منفی در عکاسی با موبایل<br />\r\n✅ آموزش پیدا کردن چشم اندازهای مختلف<br />\r\n✅ آموزش بازی با انعکاس در عکاسی با موبایل<br />\r\n✅ آموزش توجه به خطوط پیشرو در عکاسی با موبایل و دوربین<br />\r\n✅ آموزش آشنایی با انواع لنز موبایل و دوربین واستفاده از آنها<br />\r\n✅ آموزش نورپردازی و انواع فوکوس با موبایل و دوربین<br />\r\n✅ آموزش ادیت عکس و فیلم با گوشی موبایل<br />\r\n✅ آموزش فیلمبرداری و ساخت کلیپ و ادیت و صداگذاری<br />\r\n&nbsp; &nbsp; &nbsp; (ورکشاپ ۴ و ۶ ساعته با موبایل)<br />\r\nآدرس :هفت تیر .قائم مقام میدان شعاع اول خردمند پلاک ۱۳۵ طبقه اول</p>\r\n\r\n<p>جهت ثبت نام در این دوره از منوی ثبت نام اقدام نمایید.با شما تماس میگیریم.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/download.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_download.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_download.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_download.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_download.jpg\"}', 'موسسه هفت هنر سیمرغ ,, اموزش عکاسی  ,  عکاسی با موبایل  ,  عکاسی , هفت هنر سیمرغ ,, اموزشگاه عکاسی  ,  سجاد نادر , آکادمی مولتی آرت', 'post', 797, 0, '2020-09-12 20:36:50', '2024-07-15 16:28:37'),
(53, 'کلاس فوق تخصصی موشن گرافیک دو بعدی', 'کلاس-فوق-تخصصی-موشن-گرافیک', 'آموزش ببینید شاغل شوید', '<p>آموزش ببینید =استخدام شوید<br />\r\n&nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;موسسه فرهنگی هنری &quot;هفت هنر سیمرغ&quot; &nbsp;<br />\r\nآموزش تخصصی دوره موشنگرافیک دو بعدی سه بعدی و سینمافوردی</p>\r\n\r\n<p>✅ بهترین شغل ؛ حرفه و بازار کار فراوان همیشگی<br />\r\nدر کلاسهای فوق تخصصی موشنگرافیک موسسه فرهنگی هنری &quot;هفت هنر سیمرغ&quot; آموزش ببینید و بعد از پایان دوره استخدام &nbsp;موسسه شوید.</p>\r\n\r\n<p>دوره مبانی متحرکسازی &nbsp;(۱۰ جلسه )</p>\r\n\r\n<p>کارگردانی و استوری بورد (۶جلسه )</p>\r\n\r\n<p>ایده پردازی و تصویرسازی (۱۰ جلسه )</p>\r\n\r\n<p>متحرک سازی در افتر افکت ( ۸ جلسه )</p>\r\n\r\n<p>کامپوزیت و خروجی نهایی (۴ جلسه )</p>\r\n\r\n<p>دوره پروژه محور فوق تخصصی موشنگرافیک&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;✅ آموزش ببینید شاغل شوید✅<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;هزینه بصورت اقساطی</p>\r\n\r\n<p>آدرس &nbsp;هفت تیرقائم مقام میدان شعاع اول خردمند پلاک ۱۳۵ طبقه اول</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/\\u0633\\u06cc\\u0645\\u0631\\u063a \\u0645\\u0648\\u0634\\u0646.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_\\u0633\\u06cc\\u0645\\u0631\\u063a \\u0645\\u0648\\u0634\\u0646.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_\\u0633\\u06cc\\u0645\\u0631\\u063a \\u0645\\u0648\\u0634\\u0646.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_\\u0633\\u06cc\\u0645\\u0631\\u063a \\u0645\\u0648\\u0634\\u0646.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_\\u0633\\u06cc\\u0645\\u0631\\u063a \\u0645\\u0648\\u0634\\u0646.jpg\"}', 'هفت هنر سیمرغ  , موسسه هفت هنر سیمرغ , موشنگرافیک ,  سمیه غفاری ,  انیمیشن , کمپانی مولتی آرت , موشن گرافیک ,  سجاد نادر ,', 'post', 1107, 0, '2020-10-31 23:49:20', '2024-07-15 13:59:31'),
(54, 'فراخوان', 'فراخوان', 'فراخوان نمایشگاه خوشنویسی و طراحی', '<p>اولین فراخوان نمایشگاه و گالری خوشنویسی با موضوع آزاد و طراحی&nbsp;<br />\r\nمهلت ارسال آثار تا پایان ۲۵ آذر ماه ۱۳۹۹<br />\r\nجهت ثبت نام برای شرکت در نمایشگاه و گالری به آدرس اینترنتی www.artamoztv com مراجعه و یا آثار خود را به شماره زیر ارسال نمایید.</p>\r\n\r\n<p>شماره واتساپ&nbsp;</p>\r\n\r\n<p>09354637055<br />\r\nموسسه هفت هنر سیمرغ حامی هنر</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/\\u0633\\u06cc\\u0645\\u0631\\u063a \\u062e\\u0648\\u0634\\u0646\\u0648\\u06cc\\u0633\\u06cc.jpg\",\"300\":\"\\/upload\\/images\\/2020\\/300_\\u0633\\u06cc\\u0645\\u0631\\u063a \\u062e\\u0648\\u0634\\u0646\\u0648\\u06cc\\u0633\\u06cc.jpg\",\"600\":\"\\/upload\\/images\\/2020\\/600_\\u0633\\u06cc\\u0645\\u0631\\u063a \\u062e\\u0648\\u0634\\u0646\\u0648\\u06cc\\u0633\\u06cc.jpg\",\"900\":\"\\/upload\\/images\\/2020\\/900_\\u0633\\u06cc\\u0645\\u0631\\u063a \\u062e\\u0648\\u0634\\u0646\\u0648\\u06cc\\u0633\\u06cc.jpg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_\\u0633\\u06cc\\u0645\\u0631\\u063a \\u062e\\u0648\\u0634\\u0646\\u0648\\u06cc\\u0633\\u06cc.jpg\"}', 'هفت هنر سیمرغ , خوشنویسی.طراحی,فراخوان,نمایشگاه,سجاد نادر,مولتی آرت, سیمرغ,موسسه هفت هنر سیمرغ', 'post', 51, 0, '2020-12-07 00:19:04', '2024-07-15 00:23:15'),
(55, 'رادیو', 'رادیو', 'آموزش و جذب دوبلر و گوینده برای نمایشهای رادیویی', '<p>جذب گوینده و دوبلر جهت نمایشهای رادیویی</p>\r\n\r\n<p>موسسه فرهنگی هنری &quot;هفت هنر سیمرغ&quot; برگزار میکند.<br />\r\nآموزش و استخدام دوبلر جهت تولید نمایشهای رادیویی &nbsp;در &quot;رادیو سیمرغ&quot;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; آموزش شخصیت سازی و حس و بازی<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; آموزش فن بیان در بازیگری<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; تقویت حنجره و تنفس صحیح<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;ترمیم بیان برای گویندگی و راوی داستان<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;گرفتن لکنت های زبانی&nbsp;</p>\r\n\r\n<p>&nbsp;✅ تست کاملا رایگان<br />\r\n✅ نمره بالای ۷۰ بدون نیاز به آموزش<br />\r\n✅ با دریافت نمره ۷۰ از ۱۰۰ همکار همیشگی ما در رادیو سیمرغ باشید.<br />\r\nآموزش با کمترین هزینه ، ملاک استعدادیابیه نه هزینه آموزش</p>\r\n\r\n<p>در کنار ما ، مرحله به مرحله ، به رویای &nbsp;دوبلری و گویندگی برسید<br />\r\nلطفا در واتساپ با شماره 09354637055 ویا با شماره ثابت در ارتباط باشید.<br />\r\nآدرس : هفت تیر.قائم مقام میدان شعاع اول خردمند پلاک ۱۳۵ طبقه اول</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/WhatsApp Image 2020-12-07 at 11.30.28 (2).jpeg\",\"300\":\"\\/upload\\/images\\/2020\\/300_WhatsApp Image 2020-12-07 at 11.30.28 (2).jpeg\",\"600\":\"\\/upload\\/images\\/2020\\/600_WhatsApp Image 2020-12-07 at 11.30.28 (2).jpeg\",\"900\":\"\\/upload\\/images\\/2020\\/900_WhatsApp Image 2020-12-07 at 11.30.28 (2).jpeg\"},\"thumb\":\"\\/upload\\/images\\/2020\\/300_WhatsApp Image 2020-12-07 at 11.30.28 (2).jpeg\"}', 'هفت هنر سیمرغ,موسسه هفت هنر سیمرغ,فن بیان,نمایش رادیویی,گوینده,دوبلر, تست صدا,,تست دوبله', 'post', 1026, 0, '2020-12-07 01:24:17', '2024-07-15 20:12:49'),
(56, 'کلاس تخصصی موشن گرافیک', 'کلاس-تخصصی-موشن-گرافیک', 'برگزاری کلاس مقدماتی و پیشرفته به همراه وام آموزشی', '<p>دوره فوق تخصصی موشن گرافیک<br />\r\nدو ترم<br />\r\nمقدماتی و پیشرفته<br />\r\nهرترم ۱۰ جلسه<br />\r\nهفته ای یک جلسه<br />\r\nهر ترم یک ونیم الی دو ماه طول میکشه<br />\r\nهزینه هر ترم ۲.۹۰۰ بهمراه وام و مدرک<br />\r\nمعرفی به شرکتها و موسسات تولید محنوای زیرنظر خودمون پس از پایان دوره جهت استخدام حضوری&nbsp;<br />\r\nهمکاری بصورت دور کاری با خود موسسه (ماهانه پروژه میدیم چندتا)</p>\r\n\r\n<p>insta:30morgh7honar<br />\r\nwww.artamoztv.com<br />\r\n&nbsp;whats up : 09354637055</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2021\\/\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f6\\u06f5\\u06f3\\u06f4\\u06f9.jpg\",\"300\":\"\\/upload\\/images\\/2021\\/300_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f6\\u06f5\\u06f3\\u06f4\\u06f9.jpg\",\"600\":\"\\/upload\\/images\\/2021\\/600_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f6\\u06f5\\u06f3\\u06f4\\u06f9.jpg\",\"900\":\"\\/upload\\/images\\/2021\\/900_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f6\\u06f5\\u06f3\\u06f4\\u06f9.jpg\"},\"thumb\":\"\\/upload\\/images\\/2021\\/300_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f6\\u06f5\\u06f3\\u06f4\\u06f9.jpg\"}', 'موشن گرافیک , انیمیشن سازی , استخدام , هفت هنر سیمرغ  ,', 'post', 1017, 0, '2021-02-04 00:02:31', '2024-07-16 00:51:05'),
(57, 'برگزاری کلاس دوبله و گویندگی', 'برگزاری-کلاس-دوبله-و-گویندگی', 'به همراه وام اموزشی 3 الی 8 ماهه', '<p>دوره کامل دوبله و گویندگی&nbsp;۱۵ جلسه&nbsp;<br />\r\nهرهفته یک جلسه<br />\r\nدوبله و گویندگی<br />\r\nهزینه&nbsp;۲.۷۰۰ بهمراه وام و مدرک</p>\r\n\r\n<p>یه دوره کوتاه مدت داریم ماهانه ۵ جلسه هزینش ۵۰۰ هرچقد که خودتون نیاز به اموزش داشته باشین.</p>\r\n\r\n<p>insta:30morgh7honar<br />\r\nwww.artamoztv.com<br />\r\n&nbsp;whats up : 09354637055</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2021\\/\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f7\\u06f0\\u06f3\\u06f3\\u06f8.jpg\",\"300\":\"\\/upload\\/images\\/2021\\/300_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f7\\u06f0\\u06f3\\u06f3\\u06f8.jpg\",\"600\":\"\\/upload\\/images\\/2021\\/600_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f7\\u06f0\\u06f3\\u06f3\\u06f8.jpg\",\"900\":\"\\/upload\\/images\\/2021\\/900_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f7\\u06f0\\u06f3\\u06f3\\u06f8.jpg\"},\"thumb\":\"\\/upload\\/images\\/2021\\/300_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f2\\u06f0\\u06f3_\\u06f1\\u06f7\\u06f0\\u06f3\\u06f3\\u06f8.jpg\"}', 'دوبله , گویندگی , سخنوری ,  موسسه هفت هنر سیمرغ', 'post', 826, 0, '2021-02-04 00:21:45', '2024-07-15 21:00:38'),
(59, 'برگزاری کلاس تخصصی گریم سینمایی و تئاتر', 'برگزاری-کلاس-تخصصی-گریم-سینمایی-و-تئاتر', 'دو ترم مقدماتی و پیشرفته بهمراه وام آموزشی', '<p>آموزش گریم &nbsp;سینمایی مقدماتی و پیشرفته<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;در موسسه &quot;هفت هنر سیمرغ&quot;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;... &nbsp;شهریه اقساطی &nbsp;...</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;✅ آموزش پیشرفته گریم&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;✅ افکت گریم (زخم و سوختگی)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;✅ قالب گیری و انواع آن<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;✅ پتینه و کلاژ<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;✅ بافت و نصب مو بر ریش و سبیل</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; آموزش مقدماتی گریم&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;✅ شناخت آناتومی چهره<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;✅ روانشناسی چهره<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;✅ گریم متعادل سازی نا متعادل سازی<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;✅ گریم فانتزی و فشن</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره 02188304196&nbsp;تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2021\\/\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f1\\u06f2\\u06f3_\\u06f1\\u06f4\\u06f3\\u06f6\\u06f2\\u06f4.jpg\",\"300\":\"\\/upload\\/images\\/2021\\/300_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f1\\u06f2\\u06f3_\\u06f1\\u06f4\\u06f3\\u06f6\\u06f2\\u06f4.jpg\",\"600\":\"\\/upload\\/images\\/2021\\/600_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f1\\u06f2\\u06f3_\\u06f1\\u06f4\\u06f3\\u06f6\\u06f2\\u06f4.jpg\",\"900\":\"\\/upload\\/images\\/2021\\/900_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f1\\u06f2\\u06f3_\\u06f1\\u06f4\\u06f3\\u06f6\\u06f2\\u06f4.jpg\"},\"thumb\":\"\\/upload\\/images\\/2021\\/300_\\u06f2\\u06f0\\u06f2\\u06f1\\u06f0\\u06f1\\u06f2\\u06f3_\\u06f1\\u06f4\\u06f3\\u06f6\\u06f2\\u06f4.jpg\"}', 'گریم , گریم سینمایی , \'گریم تئاتر ,  هفت هنر سیمرغ', 'post', 921, 0, '2021-02-04 00:28:00', '2024-07-15 09:11:17'),
(60, 'کلاس افتر افکت و سینما فوردی', 'کلاس-تخصصی-افتر-افکت-و-سینما-فوردی', 'دوره فوق تخصصی افترافکت و سینما فوردی\r\nبه همراه آموزش پریمیر ، فتوشاپ و ایلیستراتور\r\n۳۰ جلسه تخصصی', '<p>اصول پرنت وکامپوزیشن<br />\r\nنحوه استفاده از موشن بلر<br />\r\nدوربین و سه بعدی در افتر افکت<br />\r\nگراف ادیتور و درک انیمیشن<br />\r\nاستفاده از اکسپرژن ها و ترنزیشن ها<br />\r\nساخت انواع لایه و ماسک<br />\r\nرندرنیگ و خروجی گرفتن<br />\r\nتکسچرینگ و تشریح آن<br />\r\nنورپردازی و صداگذاری<br />\r\nدوربین و متحرک سازی آن<br />\r\nمروری بر ترنزیشنهای سه بعدی<br />\r\nساخت انواع ماسک و لایه<br />\r\nاستفاده از اکسپرژن ها<br />\r\nنحوه ساخت متریال<br />\r\nانیمیت با موتکست<br />\r\nدفورمرها و تایپ فیس ها</p>\r\n\r\n<p>و ....</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره 02188304196&nbsp;تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2021\\/WhatsApp Image 2021-02-06 at 16.30.11.jpeg\",\"300\":\"\\/upload\\/images\\/2021\\/300_WhatsApp Image 2021-02-06 at 16.30.11.jpeg\",\"600\":\"\\/upload\\/images\\/2021\\/600_WhatsApp Image 2021-02-06 at 16.30.11.jpeg\",\"900\":\"\\/upload\\/images\\/2021\\/900_WhatsApp Image 2021-02-06 at 16.30.11.jpeg\"},\"thumb\":\"\\/upload\\/images\\/2021\\/300_WhatsApp Image 2021-02-06 at 16.30.11.jpeg\"}', 'افتر افکت ,  سینما فوردی ,  فتوشاپ ,  ایلیستراتور , موشن گرافیک , انیمیشن سازی , استخدام , هفت هنر سیمرغ  ,', 'post', 1075, 0, '2021-02-06 22:39:20', '2024-07-15 16:40:45'),
(61, 'آموزش خوشنویسی  و دیزاین', 'آموزش-خوشنویسی-و-دیزاین', 'با قلم  و خودکار نستعلیق ؛ شکسته و ثلث', '<p>دوره فشره خط و خوشنویسی ۱۰ جلسه تا عید با خودکار و قلم</p>\r\n\r\n<p>خط ما ، اعتبار ماست ؛ زیبا بنویسیم</p>\r\n\r\n<p><br />\r\nآموزش خط خوشنویسی با خودکار و قلم</p>\r\n\r\n<p>آموزش خط نستعلیق ، شکسته ، نسخ و ثلث</p>\r\n\r\n<p>آموزش دیزاین و دیوار نویسی&nbsp;<br />\r\nآموزش پارچه نویسی و پوستر نویسی</p>\r\n\r\n<p>&nbsp; &nbsp; با استاد خانم دیبا<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;عضو انجمن خوشنویسان ایران</p>\r\n\r\n<p>&nbsp; با ارائه مدرک</p>\r\n\r\n<p>آموزش ببینید = زیبا بنویسید</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2021\\/\\u062e\\u0637.jpeg\",\"300\":\"\\/upload\\/images\\/2021\\/300_\\u062e\\u0637.jpeg\",\"600\":\"\\/upload\\/images\\/2021\\/600_\\u062e\\u0637.jpeg\",\"900\":\"\\/upload\\/images\\/2021\\/900_\\u062e\\u0637.jpeg\"},\"thumb\":\"\\/upload\\/images\\/2021\\/300_\\u062e\\u0637.jpeg\"}', 'خوشنویسی ,  خط ,  نستعلیق , دیزاین , خط شکسته ,  موسسه هفت هنر سیمرغ ,  نیلوفر قیصری', 'post', 115, 0, '2021-02-15 00:18:25', '2024-06-08 02:04:25'),
(62, 'آموزش عکاسی', 'آموزش-عکاسی-1', 'با موبایل و دوربین', '<p>آموزش فشرده عکاسی حرفه ای با موبایل و دوربین تا عید</p>\r\n\r\n<p>&nbsp; آموزش ببینید از تعطیلات عید عکسهای حرفه ای بگیرید و در مسابقه عکاسی موسسه ما شرکت کنید.</p>\r\n\r\n<p>&nbsp;موسسه فرهنگی هنری &quot;هفت هنر سیمرغ&quot; زیر نظر استاد خانم کنشلو<br />\r\nبدون نیاز داشتن به دوربین عکاسی حرفه ای با گوشی موبایل در همه جا بهترین عکسهای با کیفیتی ثبت کنید.</p>\r\n\r\n<p><br />\r\n✅ آموزش ترفندهای عکاسی با گوشی و دوربین<br />\r\n✅ آموزش عکاسی مدلینیگ<br />\r\n✅ آموزش عکاسی پرتره<br />\r\n✅ آموزش توجه به فضای منفی در عکاسی&nbsp;<br />\r\n✅ آموزش عکاسی خیابانی<br />\r\n✅ آموزش عکاسی سینما<br />\r\n✅ آموزش عکاسی صنعتی و تبلیغاتی<br />\r\n✅ آموزش عکاسی فشن و خلاق<br />\r\n✅ آموزش نورپردازی و انواع فوکوس با موبایل و دوربین<br />\r\n✅ آموزش ادیت عکس و فیلم&nbsp;<br />\r\n✅ آموزش عکاسی طبیعت<br />\r\n✅ آموزش فتوشاپ تخصصی</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره 02188304196&nbsp;تماس بگیرید.</p>', 'on', NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2021\\/\\u0639\\u06a9\\u0627\\u0633\\u06cc.jpeg\",\"300\":\"\\/upload\\/images\\/2021\\/300_\\u0639\\u06a9\\u0627\\u0633\\u06cc.jpeg\",\"600\":\"\\/upload\\/images\\/2021\\/600_\\u0639\\u06a9\\u0627\\u0633\\u06cc.jpeg\",\"900\":\"\\/upload\\/images\\/2021\\/900_\\u0639\\u06a9\\u0627\\u0633\\u06cc.jpeg\"},\"thumb\":\"\\/upload\\/images\\/2021\\/300_\\u0639\\u06a9\\u0627\\u0633\\u06cc.jpeg\"}', 'عکاسی , عکاسی با موبایل ,  عکاسی با دوربین , فتوشاپ , عکاس  , هفت هنر سیمرغ', 'post', 828, 0, '2021-02-15 00:20:16', '2024-07-16 00:44:36'),
(63, 'آموزش گریم', 'آموزش-گریم', 'سینمایی ، تئاتر و تلویزیونی', '<p>آموزشگاه هفت هنر سیمرغ زیر نظر اداره ارشاد با ارائه مدرک معتبر گریم سینما و تئاتر ثبت نام مینماید.<br />\r\nدوره های جامع مقدماتی و پیشرفته انواع گریم سینما ، تئاتر ، فشن ، عروسکی ، شناخت آناتومی چهره ،گریم متعادل سازی و نامتعادل سازی ، گریم انواع پیری و جوانی ، بافت مو و سبیل ، گریم زخم و صورت ، سوختگی ، حجم سازی ، گریم انواع بخیه زدن ، قالب گیری ، پتینه و کلاژ ، شناخت آناتومی چهره&nbsp;<br />\r\nمقدماتی و پیشرفته</p>\r\n\r\n<p>قابل توجه کسانیکه میخواهند گریمور سینما و تئاتر شوند.<br />\r\nآموزش گریم عملی در سر فیلمبرداری تئاتر و فیلم به همراه استاد<br />\r\nدوره فشرده به تمام علامندان سراسر ایران<br />\r\nدر جای معتبر و تخصصی آموزش ببینید تا حرفه ای شوید .</p>\r\n\r\n<hr />\r\n<p dir=\"rtl\">با ارائه <strong>مدرک معتبر </strong>زیر نظر <strong>وزارت ارشاد</strong></p>\r\n\r\n<p dir=\"rtl\">برای ثبت نام می توانید از لینک<a href=\"http://artamoztv.com/contact\"> ثبت نام</a> اقدام نمایید .</p>\r\n\r\n<p dir=\"rtl\">یا برای کسب اطلاعات بیشتر با شماره 02188304196&nbsp;تماس بگیرید.</p>', NULL, NULL, '{\"images\":{\"original\":\"\\/upload\\/images\\/2021\\/\\u06af\\u0631\\u06cc\\u0645.jpeg\",\"300\":\"\\/upload\\/images\\/2021\\/300_\\u06af\\u0631\\u06cc\\u0645.jpeg\",\"600\":\"\\/upload\\/images\\/2021\\/600_\\u06af\\u0631\\u06cc\\u0645.jpeg\",\"900\":\"\\/upload\\/images\\/2021\\/900_\\u06af\\u0631\\u06cc\\u0645.jpeg\"},\"thumb\":\"\\/upload\\/images\\/2021\\/300_\\u06af\\u0631\\u06cc\\u0645.jpeg\"}', 'گریم , گریم سینمایی , \'گریم تئاتر ,  هفت هنر سیمرغ , گریمور سینما , گریم فشن , گریم وحشت', 'post', 244, 0, '2021-02-15 00:24:32', '2024-07-06 17:59:59');
